{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 aspect = vec2(iResolution.x / iResolution.y, 1.);
    uv *= aspect * zoom;

    vec2 mouse = iMouse.xy / iResolution.xy * aspect * zoom;

    float radius = .4 + cos(iTime * 2. - (uv.x + uv.y) / 2.) / 20.;

    vec2 center = floor(uv) + vec2(0.5, 0.5);
    //vec2 center = floor(mouse) + vec2(0.5, 0.5);

    vec3 cog = cog(uv, center, radius);
    vec3 background = vec3(uv.x / zoom, uv.y / zoom, .5);

	fragColor = vec4(cog * background, 1.0);
}

