float glint(vec2 p)
{
    vec2 uv = p;
    float speed = 0.68;
    float linewidth = 1.38;
    float grad = 3.0;
    vec2 linepos = uv;
    linepos.x = linepos.x - mod(iTime*speed,4.0)+2.0;
