    vec2 cst = vec2( cos(d+speed), sin(d+speed) );
    float zoom = 1.0+(0.5*sin(iTime*0.675));
    mat2 rot = zoom * mat2(cst.x,-cst.y,cst.y,cst.x);
    float s = glint(p*rot*0.44);
    return s;
}


void mainImage(out vec4 k, vec2 p)
{
    const vec4 col1 = vec4(0.27,.1,.1,1.0);
    const vec4 col2 = vec4(0.9,0.5,0.5,1.0);
    vec2 uv = p.xy / iResolution.xy;
    float gl = _glint(p)*0.5;

    float s = sin(iTime*0.1);
    float s2 = 0.5+sin(iTime*1.8);
    vec2 d = uv*(4.0+s*.3);

    d.x += sin(d.y + iTime*0.2)*1.2;
    d.y += sin(d.x + iTime*0.2)*1.2;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.90;

    d = uv;
    float v2=length(0.5-fract(d.xy));
    v1 *= 1.0-v2*v1;
    v1 = v1*v1*v1;
    v1 *= 1.9+s2*0.2;
    k = mix(col2,col1,v1)*(3.4+(s2*0.2));


    k *= 1.0-(v2);

    // invert the hole on mouse click
	if (iMouse.z > 0.5)
    {
        k = clamp(k,vec4(0.0),vec4(1.0));
        k = 1.0-k;
        k = mix(col1,col2,k.r+v1*0.2);
    }

    if (k.r<=0.5)
    {
        float m = 1.0-clamp(k.r,0.0,0.5);
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        float col = 0.3 + 0.2*f*1.0;
        k.x = k.y = k.z = col*m;
        k.z *= 2.0;
    }
    else
    {
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        vec4 _col = 0.2*f*vec4(0.175);
        k+=_col;
    }
    // glint
    k *=vec4(1.0+gl);


}

#define PI 3.1415972

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*(fragCoord.xy / min(iResolution.x,iResolution.y) - 0.5*iResolution.xy/ min(iResolution.x,iResolution.y));

    float theta = atan(uv.y, uv.x);
    float theta2 = theta;
    float rho = length(uv);

    float period = 2.*PI/5.;
    theta = mod(theta, period);
    theta = min(theta, period-theta);

    vec2 p = vec2(cos(theta), sin(theta)) * rho;

    float speed = 1.0;

    float alpha = PI*0.3;

    float x = 2.*sin(alpha)/(sin(period*.5)+2.*sin(alpha+period*.5));
    float rad = sin(period*.5)/(sin(period*.5)+2.*sin(alpha+period*.5));

    if(length(p-vec2(1.0-rad, 0.0))<rad || length(p)<1.-rad-rad*cos(alpha) && length(p-x*vec2(cos(.5*period), sin(.5*period)))>rad)
        speed = -1.0;

    float val = sin(100.*rho-iTime*50.*speed);
    val = max(val, sin(100.0 * theta2-iTime*50.*speed)*clamp(rho*10.0 - 2.0, 0.0, 1.0));
    fragColor = vec4(val);

    fragColor *= clamp(rho*10.0 - .5, 0.0, 1.0);

    if(rho < 0.01)
     fragColor = vec4(1.0, 0.0, 0.0, 1.0);
}

