// Superformula: https://en.wikipedia.org/wiki/Superformula
float Superformula( in vec2 p, in vec4 params ) {
    vec2 pt = abs(vec2(cos(p.y * params.x * 0.25), sin(p.y * params.x * 0.25)));
    pt = pow(pt, vec2(params.z, params.w));
    return pow(pt.x + pt.y, -1.0 / params.y) * 0.5 - p.x; // the * 0.5 is a tweaked trick I stole from here: https://www.shadertoy.com/view/llsyz8
}

float func( in vec2 p ) {
    #ifdef OneD
    return cos(p.x * 32.0 + iTime * 0.5) * sin(p.x * 13.0 + iTime * 1.5) * 0.15; // some 1D fxn

    #else

    //return p.x - 1.0 + 0.5 * sin(3.0 * p.y + 2.0 * p.x * p.x); // 3-blade fan

    vec4 shapes [2];

    shapes[0] = vec4(3, 3, 14, 2);
    shapes[1] = vec4(7, 3, 6, 6);

    float animate = sin(iTime) * 0.5 + 0.5;
    animate = (max(min(animate, 0.75), 0.25) - 0.25) * 2.0;

    return mix(Superformula(p, shapes[0]), Superformula(p, shapes[1]), animate);

    #endif
}

vec2 grad( in vec2 x )
{
    vec2 h = vec2(/*1.0 / iResolution.y*/0.1, 0.0);
    #ifdef OneD
    vec2 result;
    result.x = (func(x + h.xy) - func(x - h.xy)) / (2.0 * h.x);
    result.y = 1.0;
    return result;
    #else
    return vec2(func(x + h.xy) - func(x - h.xy),
                func(x + h.yx) - func(x - h.yx)) / (2.0 * h.x);
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 scrPt = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    #ifdef ROTATE
    // Rotate the scrPt for funsies
    float theta = -iTime * 0.25;
    mat2 rot;

    // cache calls to sin/cos
    float cosTheta = cos(theta);
    float sinTheta = sin(theta);

    rot[0][0] = cosTheta;
    rot[0][1] = -sinTheta;
    rot[1][0] = sinTheta;
    rot[1][1] = cosTheta;

    scrPt *= rot;
    #endif

    vec2 inputVal;
    #ifdef OneD
    inputVal = scrPt;
    #else
    scrPt *= 2.0;
    inputVal = vec2(length(scrPt * 1.0), atan(scrPt.y, scrPt.x)); // for 3-blade fan
    #endif

    float val = func(inputVal);

    // Compute numerical gradient
    vec2 gradient;
    gradient = grad(inputVal);

    float gradDenom = length(gradient);

    float valNum;
    #ifdef OneD
    valNum = abs(val - scrPt.y); // only subtract for 1D fxns
    #else
    valNum = abs(val);
    #endif

    float distEst = valNum / gradDenom; // improve distance estimation

    #ifdef OneD
    distEst = smoothstep(0.005, 0.0051, distEst);
    #else
    distEst = 1.0 - smoothstep(0.0, 0.009, distEst);
    #endif

    vec3 color;
    color = vec3(distEst);

    // Compute some outline color
    vec3 outlineCol;

    float alternate = sign(sin(inputVal.y * 20.0 + iTime * 8.0));
    outlineCol = mix(vec3(0.2, 0.3, 0.4), vec3(1.0, 0.3, 0.2), alternate);

    color = outlineCol * pow(color, vec3(0.175));

    vec3 bgColor;
    bgColor = vec3(0.08, 0.25, 0.21);
    color = mix(bgColor, color, ceil(distEst));

    fragColor = vec4(color, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy / iResolution.xy;

    float d = sin(iTime) * 0.2;

    float dist1 = pow(max(1.0 - distance(pos, vec2(0.5 + d, 0.5)) * 3.5, 0.0), 3.0);
    float dist2 = pow(max(1.0 - distance(pos, vec2(0.5 - d, 0.5)) * 3.5, 0.0), 3.0);

    float c = sin(1.0 - smoothstep(0.3, 0.5, dist1 + dist2) * iTime);

    fragColor = vec4(c, c, c, 1.0);
}

const float PI =  3.141592654;
const float TWOPI = 2.0 * PI;
const float PHI = 1.618033989;
const vec4 I = vec4(1,1,1,1);
const vec3 AXIS = vec3(1,1,1); // axis for rotation
const int NLINES = 27;
vec4 lines[NLINES*2];
int colors[NLINES];

// Lighting params
vec3 light;
float ambient;
