#define M_PI 3.14159

int numRipples = 12;

vec2 center ()
{
    // Center point that orbits the screen center.
	vec2 c = vec2(0.5, 0.5);
    c.x += 0.1 * cos(iTime);
    c.y += 0.1 * sin(iTime);
    return c;
}

bool inRange(float n, float lower, float upper)
{
	return n > lower && n < upper;    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Color w/ White Center
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
	fragColor = vec4(uv, 0.5 + 0.2*sin(iTime), 1.0);
    fragColor = fragColor / distance(uv, center());
    float d = distance(uv, center());

    // Ripples
    for (int u = 0; u < numRipples; u++)
    {    
        float phi = M_PI/float(u) * iTime;
        float width = float(u) / float(numRipples);
        vec2 ripple = vec2(0.1 * float(u) + 0.1*tan(phi), 0.1*float(u) + width + 0.1*tan(phi));
        if (inRange(d, ripple.x, ripple.y))
        {
            fragColor = 0.9*fragColor;   
        }
    }
}

#define cross2(v0, v1) v0.x * v1.y - v0.y * v1.x
#define IsIn(b) all(greaterThan(b, vec3(0.)))

float lineDistanceWork(in vec2 p0, in vec2 p0p, in vec2 p1p, in vec2 p0p1, in vec2 p)
{
    vec2 p0p1Norm = normalize(p0p1);

    float t = dot(p0p, p0p1Norm);    
    float nearestDistance = abs(cross2(p0p, p0p1Norm));

    return mix(
        nearestDistance*nearestDistance,
        min(dot(p0p,p0p), dot(p1p,p1p)),
        float(t <= 0.0 || t*t >= dot(p0p1,p0p1))
    );  
}    

float lineDistance(in vec2 p0, in vec2 p1, in vec2 p)
{
    vec2 p0p = p - p0;
    vec2 p1p = p - p1;
    vec2 p0p1 = p1 - p0;

    return sqrt(lineDistanceWork(p0, p0p, p1p, p0p1, p));  
}

float triangleDistance(in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p, out vec3 barycentric)
{
    vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
    vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

    float a = cross2(e0, e1);

    //Calculate distance to the triangle as a min distance to it's edges
    float dst = 
        sqrt(min(min
        (
            lineDistanceWork(p0, v0, v1, e0, p),
            lineDistanceWork(p1, v1, v2, e1, p)
        ),  lineDistanceWork(p2, v2, v0, e2, p)));

    //Using simplified 2d version of barycentric coords
    vec2 bar2 = vec2(cross2(v1, v2), cross2(v2, v0)) / a;
    barycentric = vec3(bar2, 1.0 - bar2.x - bar2.y);

    bool isInside = IsIn(barycentric);
    //When we inside a triangle the distance is negative
    return dst * mix(1.0, -1.0, float(isInside));    
}

vec2 rotate(vec2 inVec, float a)
{
    float cs = cos(a);
    float sn = sin(a);
    mat2 m =
        mat2(  cs, -sn,
               sn,  cs );
    return m * inVec;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 cntr = vec2(0.5, 0.5);
    cntr.x = cntr.x / iResolution.y * iResolution.x;

    vec2 a = cntr + rotate(vec2(-0.2, -0.125), iTime);
    vec2 b = cntr + rotate(vec2( 0.2, -0.125), iTime) * 0.7;
    vec2 c = cntr + rotate(vec2( 0.0, -0.125 + 0.2 * tan(3.1416 / 3.0)), iTime) * 1.3;

    vec3 baryc;
    float dst = triangleDistance(a, b, c, uv, baryc) * 5.0;    

    fragColor = vec4(
        (0.8 + 0.2*cos(120.0*dst)) * mix(mix(
            baryc, 
            vec3(0.97, 0.37, 0.0) * (1.0 - dst*.3), 
            float(dst > 0.0)
        ), vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(dst))), 1.0);
}

