void mainImage(out vec4 k, vec2 p)
{
    const vec4 col1 = vec4(0.0,.1,.1,1.0);
    const vec4 col2 = vec4(0.5,0.9,0.3,1.0);
    vec2 uv = p.xy / iResolution.xy;
	float gl = _glint(p)*0.5;

    float speed = iTime*0.2;
 	float _fd = 1.0+uv.x*uv.y*0.5;
    vec2 cst = vec2( cos(_fd+speed), sin(_fd+speed) );
    float zoom = 3.0+(0.5*sin(iTime*0.55));
    mat2 rot = zoom * mat2(cst.x,-cst.y,cst.y,cst.x);
    uv = rot*uv;


    float s = sin(iTime*0.1);
    float s2 = 0.5+sin(iTime*1.8);
    vec2 d = uv*(4.0+s*.3);						// mod 4.0 for irregularity...

    d.x += iTime*0.25+sin(d.x+d.y + iTime*0.3)*0.5;
    d.y += iTime*0.25+sin(d.x + iTime*0.3)*0.5;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.75;				// 0.9 =more gooey bits, 1.2 = less gooey bits

    d = 0.15+(uv*0.7);			// zoom
    float v2=length(0.5-fract(d.xy));
    v1 *= 1.0-v2*v1;
    v1 = v1*v1*v1;
    v1 *= 1.9+s2*0.2;
    k = mix(col2,col1,v1)*(3.4+(s2*0.2));
    k *= 1.0-(v2);


    if (k.g<=0.4)
    {
        float m = 1.0-clamp(k.g,0.0,0.5);
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        float col = 0.3 + 0.5*f*1.0;
        k.x = k.y = k.z = col*m;
    }
    else
    {
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        vec4 _col = 0.2*f*vec4(0.175);
        k+=_col;
    }
    // glint
    k *=vec4(1.0+gl);

}

float sph(vec3 pos,float y)
{
	pos.y -= y;
	pos.z += 3.0*sin((y/1.5+iTime*4.0-1.0)*0.5);
	return length(pos)-1.0;
}

float sphs(vec3 pos)
{
	pos.y -= iTime*(1.5*4.0);
	pos.z = abs(pos.z);
    float s = 0.8;
	return min(
		min( sph(pos,floor(pos.y*s    )/s),
		     sph(pos,floor(pos.y*s+1.0)/s) ),1.0);
}

float FieldFn(vec3 pos)
{
	float h = pos.z+0.5;
	pos.y -= iTime*6.0;
	return min(
		min( sph(pos,floor(pos.y    )),
		     sph(pos,floor(pos.y+1.0)) ),min(h,1.0));
}

vec4 fractal(vec2 cx)
{
	cx.x += 0.1;
	cx.y += 0.1;
	vec2 z = cx;
	float cc = 1.0;
	for(int i=0;i<22;i++)
	{
		vec2 z1;
		z1.x = z.x*z.x - z.y*z.y + cx.x;
		z1.y = 2.0*z.x*z.y + cx.y;
		z = z1;
        if(dot(z,z)>4.0)
        {
            cc = float(i)/21.0;
            break;
        }
		//cc += clamp((dot(z,z)-4.0)*10.0,0.0,1.0)/21.0;
	}
    if(cc>1.0-0.2) return vec4(0,0,0.1,0);
    cc *= 2.5;
    cc += 0.5;
    cc /= 1.0+length(cx);
    return vec4(cc,cc,cc*2.0,0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 vpos = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	vpos.x *= iResolution.x/iResolution.y;
	vpos *= .8;


	vec2 mpos = iMouse.xy / iResolution.xy*2. - 1.;
	vec3 front = normalize(vec3(-1.,-mpos.x,mpos.y-.5));
	vec3 pos = vec3(0,0,1.2)-front*6.0;


	front = normalize(vec3(-1,-0.7,0));
	vec3 up = vec3(0,0,1);
	vec3 right = cross(up,front);
	pos = vec3(1,-2,1.2)-front*4.0;

	vec3 vdir = normalize(front + vpos.x*right + vpos.y*up);
	vec3 tdir = normalize(vdir);
	vec3 tpos = pos;
	float g = 0.0;
	vec4 c = vec4(0.0);
	float d, m=0.0;
	vec3 lpos = vec3(10,10,0);

	for(int i=0;i<150;i++)
	{
		if(g>=1.0) break;
		d = sphs(tpos);
		if(d<0.002) break;
		tpos += d*tdir;
		g += d*0.02;
	}

	if(d<0.002)
	{
		vec3 normal, ld = normalize(vec3(4,-2,4));
		normal.x = sphs(tpos+vec3(0.01,0,0)) - d;
		normal.y = sphs(tpos+vec3(0,0.01,0)) - d;
		normal.z = sphs(tpos+vec3(0,0,0.01)) - d;
		normal = normalize(normal);
		c = vec4(clamp(dot(normal,ld),0.0,1.0));
		c *= vec4(0.2,1,0.3,0.0);
		c += c.y*pow(clamp(dot(ld,reflect(tdir,normal)),0.0,1.0),64.0);
	}
	float a = 0.41*2.0*3.141593;
	float ss = sin(a);
	float cc = cos(a);
	if(tdir.z<0.0 && (d>=0.002 || tpos.z<0.0))
	{
		float t = -pos.z/tdir.z;
		tpos = pos + tdir*t;
		tpos.xy *= 0.3;
		//tpos.x += 6.0;
		//tpos.y += 13.5;
		tpos.xy *= 0.5;
        tpos.x -= 0.3;
		tpos = vec3(tpos.x*cc-tpos.y*ss,tpos.y*cc+tpos.x*ss,tpos.z);
		tpos.x -= 0.7+0.65;
		tpos.y -= 0.27;
		c *= 0.3;
		c += fractal(tpos.xy);
		c += mix(vec4(1,0,0.5,0),vec4(0.0),pow(-tdir.z*2.0,0.1));
	}
	else if(d>=0.002)
	{
		c = mix(vec4(1,0,0,0),vec4(0,0,0.7,0),sqrt(tdir.z*1.5));
	}


	fragColor = floor(c*32.0)/32.0;
}

#define EPSILON 0.01
#define MIN_DISTANCE 0.0
#define MAX_DISTANCE 2000.0
#define MAX_MARCHING_STEP 256

struct Ray {
    vec3 o; // origin
    vec3 d; // direction, always normalized
};

struct Camera {
	vec3 pos;
    vec3 lookAt;
    float focal;
    vec2 sensorSize;
};

struct Light {
    vec3 pos;
    vec3 color;
};

vec3 xe = vec3(EPSILON, 0., 0.);
vec3 ye = vec3(0., EPSILON, 0.);
vec3 ze = vec3(0., 0., EPSILON);

// get corresponding ray from uv (0.0 -> 1.0)
Ray getRay( Camera cam, vec2 uv ) {
    vec3 camDir = normalize(cam.lookAt - cam.pos); // the camera direction
    vec3 center = cam.pos + cam.focal * camDir; // get the coordinate of the screen center
    vec2 coord = (uv - 0.5) * cam.sensorSize; // convert from 0.0 -> 1.0 to -sensorSize / 2. -> sensorSize / 2.

    // axis relative to camera
    vec3 zpos = -camDir; // k hat, outward the screen
    vec3 xpos = cross(vec3(0., 1., 0.), zpos); // i hat, cross from ypos global and zpos local
    vec3 ypos = cross(zpos, xpos); // j hat, cross from zpos local and xpos local

    // target in screen
    vec3 target = center + coord.x * xpos + coord.y * ypos; // get pixel target coordinate from the screen

    Ray ray;
    ray.o = cam.pos; // the origin of the ray is from the camera duh
    ray.d = normalize(target - cam.pos); // the direction is from the camera to the target

    return ray;
}

// project point to ray
vec3 projPoint2Ray( vec3 point, Ray ray ) {
    vec3 rel = point - ray.o; // get relative vector from the point to the ray origin
    float len = dot(rel, ray.d); // project that vector to vector direction of the ray, we get the length
    return len * ray.d + ray.o; // multiply the length with unit vector direction and add to ray origin
}

// get the distance from point to ray (or line)
float distPoint2Ray( vec3 point, Ray ray ) {
    vec3 proj = projPoint2Ray(point, ray); // project it first to the ray
    return distance(proj, point); // get the distance from the projected position to the original position
}

// just linear interpolation, map value v between fromA and fromB to between toA and toB
float lerp( float v, float fromA, float fromB, float toA, float toB ) {
    return (v - fromA) / (fromB - fromA) * (toB - toA) + toA;
}

// sphere function
float sdSphere( vec3 p, float radius ) {
    return length(p) - radius;
}

// box function
float sdBox( vec3 p, float width, float height, float depth ) {
    vec3 d = abs(p) - vec3(width, height, depth);
    return max(d.x, max(d.y, d.z));
}

// cylinder function
float sdCylinder( vec3 p, float radius, float height ) {
    return max(length(p.xz) - radius, abs(p.y) - height);
}

float sdUnion( float sd1, float sd2 ) {
    return min(sd1, sd2);
}

float sdIntersect( float sd1, float sd2 ) {
    return max(sd1, sd2);
}

float sdSubstract( float sd1, float sd2 ) {
    return max(sd1, -sd2);
}

// change this
float sdScene( vec3 p ) {
    float sphere = sdSphere( p, 220. );
    float box = sdBox(p, 200., 50., 200.);
    float cylinder = sdCylinder(p, 150., 250.);
    return sdSubstract(sdIntersect(box, sphere), cylinder);
}

// estimate normal with nudging
vec3 estimateNormal( vec3 p ) {
    return normalize(vec3(
    	sdScene(p+xe) - sdScene(p-xe),
        sdScene(p+ye) - sdScene(p-ye),
        sdScene(p+ze) - sdScene(p-ze)
    ));
}

// ray marching algorithm
float getSDF( Ray r ) {
    float depth = MIN_DISTANCE;
    for (int i=0; i<MAX_MARCHING_STEP; i++) {
        float dist = sdScene( r.o + depth * r.d );

        if (dist < EPSILON) { // inside a scene
            return depth;
        }

        depth += dist; // circle marching!

        if (depth >= MAX_DISTANCE) {
            return MAX_DISTANCE;
        }
    }

    return MAX_DISTANCE;
}

vec3 ambientLight( vec3 ambientColor ) {
    return ambientColor;
}

vec3 phongLight( vec3 diffuseColor, vec3 specularColor, float shininess, Ray ray, float dist, Light light ) {
    vec3 p = ray.o + dist * ray.d;

    vec3 N = estimateNormal(p); // surface normal
    vec3 L = normalize(light.pos - p); // surface to light direction
    vec3 V = -ray.d; // surface to camera direction
    vec3 R = reflect(-L, N); // mirror of L by the axis N

    float dotLN = dot(L, N); // project light direction to surface normal
    float dotRV = dot(R, V); // project light reflection direction to direction to camera

    vec3 color = vec3(0.);
    if (dotLN > 0.) { // we can see the diffuse
        color += diffuseColor * dotLN;
    }
    if (dotRV > 0.) { // we can see the specular
        color += specularColor * pow(dotRV, shininess);
    }
    color *= light.color; // influence of color of the light
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // convert from fragCoord (0.0 -> iResolution.xy) to uv (0.0 -> 1.0)
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 circleMovement = vec3(600. * cos(iTime * 2.), 600. * cos(iTime * 2.), 600. * sin(iTime * 2.)); // to get circle movement

    // set camera
    Camera cam;
    cam.sensorSize = iResolution.xy; // set sensor size to the screenSize
    cam.focal = 200.; // set the focal length (distance between sensor (screen) to the aperture
    cam.lookAt = vec3(0., 0., 0.); // where to look at (this camera use two point system)
    cam.pos = circleMovement; // the position of the camera

    // get ray
    Ray ray = getRay(cam, uv); // get corresponding ray, given the uv

    // create 8 points of cube
    vec3 points[8];
    for (int i=0; i<2; i++) {
        for (int j=0; j<2; j++) {
            for (int k=0; k<2; k++) {
                points[i*4+j*2+k] = vec3(float(i) * 2. - 1., float(j) * 2. - 1., float(k) * 2. - 1.);
            }
        }
    }

    // render
    float dist = getSDF(ray);
    if (dist > MAX_DISTANCE - EPSILON) { // don't hit anything, set to background
        fragColor = vec4(0.);
        return; // or discard?
    }

    vec3 ambientColor = vec3(0.1, 0.1, 0.2);
    vec3 diffuseColor = vec3(0.7, 0.2, 0.2);
    vec3 specularColor = vec3(1.0, 1.0, 1.0);
    float shininess = 50.0;

    Light light1;
    light1.pos = vec3(500. * sin(iTime), 500. * cos(iTime), 400.);
    light1.color = vec3(1., 0.5, 0.3);

    Light light2;
    light2.pos = vec3(1000. * cos(iTime), 400., 1000. * sin(iTime));
    light2.color = vec3(1., 0.8, 0.5);

    vec3 color = vec3(0.);
    color += ambientLight(ambientColor);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light1);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light2);
    fragColor = vec4(color, 1.);
}

