#define SHOW_NOISE 0
#define SRGB 0
// 0: Addition, 1: Screen, 2: Overlay, 3: Soft Light, 4: Lighten-Only
#define BLEND_MODE 0
#define SPEED 2.0
#define INTENSITY 0.075
// What gray level noise should tend to.
#define MEAN 0.0
// Controls the contrast/variance of noise.
#define VARIANCE 0.5

vec3 channel_mix(vec3 a, vec3 b, vec3 w) {
    return vec3(mix(a.r, b.r, w.r), mix(a.g, b.g, w.g), mix(a.b, b.b, w.b));
}

float gaussian(float z, float u, float o) {
    return (1.0 / (o * sqrt(2.0 * 3.1415))) * exp(-(((z - u) * (z - u)) / (2.0 * (o * o))));
}

vec3 madd(vec3 a, vec3 b, float w) {
    return a + a * b * w;
}

vec3 screen(vec3 a, vec3 b, float w) {
    return mix(a, vec3(1.0) - (vec3(1.0) - a) * (vec3(1.0) - b), w);
}

vec3 overlay(vec3 a, vec3 b, float w) {
    return mix(a, channel_mix(
        2.0 * a * b,
        vec3(1.0) - 2.0 * (vec3(1.0) - a) * (vec3(1.0) - b),
        step(vec3(0.5), a)
    ), w);
}

vec3 soft_light(vec3 a, vec3 b, float w) {
    return mix(a, pow(a, pow(vec3(2.0), 2.0 * (vec3(0.5) - b))), w);
}

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;
    color = texture(iChannel0, uv);
    #if SRGB
    color = pow(color, vec4(2.2));
    #endif

    float t = iTime * float(SPEED);
    float seed = dot(uv, vec2(12.9898, 78.233));
    float noise = fract(sin(seed) * 43758.5453 + t);
    noise = gaussian(noise, float(MEAN), float(VARIANCE) * float(VARIANCE));

    #if SHOW_NOISE
    color = vec4(noise);
    #else    
    // Ignore these mouse stuff if you're porting this
    // and just use an arbitrary intensity value.
    float w = float(INTENSITY);
    if (iMouse.z > 0.0) {
        w = iMouse.y * ps.y;
        w *= step(coord.x, iMouse.x);
    }

    vec3 grain = vec3(noise) * (1.0 - color.rgb);

    #if BLEND_MODE == 0
    color.rgb += grain * w;
    #elif BLEND_MODE == 1
    color.rgb = screen(color.rgb, grain, w);
    #elif BLEND_MODE == 2
    color.rgb = overlay(color.rgb, grain, w);
    #elif BLEND_MODE == 3
    color.rgb = soft_light(color.rgb, grain, w);
    #elif BLEND_MODE == 4
    color.rgb = max(color.rgb, grain * w);
    #endif

    #if SRGB
    color = pow(color, vec4(1.0 / 2.2));
    #endif
    #endif
}

vec3 red = vec3(1.0, 0.1, 0.1);
vec3 green = vec3(0.1, 1.0, 0.1);
vec3 blue = vec3(0.1, 0.1, 1.0);

float rand(float t)
{
    return fract(sin(t * 123.456) * 768.89);
}

vec3 rotateX(vec3 v, float angleRads)
{ 
	float c = cos(angleRads);
    float s = sin(angleRads);
  	return vec3(v.x, v.y * c - v.z * s, v.y * s + v.z * c);
}

vec3 rotateY(vec3 v, float angleRads)
{ 
	float c = cos(angleRads);
    float s = sin(angleRads);
  	return vec3(v.x * c + v.z * s, v.y, -v.x * s + v.z * c);
}

vec3 rotateZ(vec3 p, float angleRads) { 
	float c = cos(angleRads);
    float s = sin(angleRads);
  	return vec3(c*p.x+s*p.y, -s*p.x+c*p.y, p.z);
}

float distanceFromPointToLine(vec3 rayOrigin, vec3 rayDirection, vec3 point)
{
	return length(cross(point - rayOrigin, rayDirection)) / length(rayDirection);   
}

void drawPoint(vec3 rayOrigin, vec3 rayDirection, vec3 point, float size, float blur, out vec4 fragColor, int starNum, float t)
{
    float dist = distanceFromPointToLine(rayOrigin, rayDirection, point);

    dist = smoothstep(size, size * (1.0 - blur), dist); // 2nd param means blur is always that percentage of point size

    vec3 col = vec3(0.0);    
    if (starNum % 3 == 0) { col = red;   }
    if (starNum % 3 == 1) { col = green; }
    if (starNum % 3 == 2) { col = blue;  }

    fragColor += vec4(vec3(dist * col), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy; // Range is 0 to 1    
    uv -= vec2(0.5);                         // Adjust range to -0.5 to +0.5 so (0,0) is in centre of screen 
	uv.x *= iResolution.x / iResolution.y;   // Correct aspect ratio
    float t = iTime * 0.3;                   // Get the time and slow it down by multiplying by a small value

	vec3 rayOrigin = vec3(0.0, 0.0, 0.0); // i.e. Camera origin

    vec3 lookAt = vec3(0.0, 0.0, 1); // Looking into the screen (negative on the Z-axis points outward from the screen)

    // Generate ortho camera normal basis using global Y-axis for up when generating right (positive X-axis) vector
    float zoom = 1.0;
    vec3 forward = normalize(lookAt - rayOrigin);
    vec3 right = cross(vec3(0.0, 1.0, 0.0), forward);
    vec3 up = cross(forward, right);

    vec3 centreOfScreen = rayOrigin + forward * zoom;     
    vec3 intersection = centreOfScreen + uv.x * right + uv.y * up;        
    vec3 rayDirection = intersection - rayOrigin;

    vec3 point = vec3(0.4, 0.4, 15.0);

    float pointSize = 0.1;
    float pointBlur = 0.3;

    const float numParticles = 20.0;
    float loopStep = 1.0 / numParticles;
    int starNum = 0;

    float oneTenthRads = 6.28 / 10.0;

    for (int camTwistLoop = 0; camTwistLoop < 10; camTwistLoop++)
    {

        for (float loop = 0.0; loop < 1.0; loop += loopStep)
        {
            float timeStepped = fract(t + loop);

            drawPoint( rayOrigin, rayDirection, vec3(point.x, point.y, point.z - timeStepped * point.z), pointSize, pointBlur, fragColor, starNum++, t);
        }

        point = rotateZ(point, oneTenthRads * t * 2.0);        
    }
}

