/**
 * Created by Kamil Kolaczynski (revers) - 2017
 *
 * Licensed under Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
 * 
 * Just an exercise from texture and modeling. As always, most of general raymarching
 * ingredients (raymarch loop, geometric primitives, lighting/shadowing, noise)
 * is based on iq's shaders and articles. Thanks for sharing your knowledge, iq!
 *
 * Referenced shaders:
 * - "Raymarching - Primitives" by iq [ https://www.shadertoy.com/view/Xds3zN ]
 * - "Noise - value - 2D" by iq [ https://www.shadertoy.com/view/lsf3WH ]
 *
 * Referenced articles:
 * - "modeling with distance functions" by iq [ http://iquilezles.org/www/articles/distfunctions/distfunctions.htm ]
 * - "free penumbra shadows for raymarching distance fields" by iq [ http://iquilezles.org/www/articles/rmshadows/rmshadows.htm ]
 * 
 * The shader was created and exported from Synthclipse (http://synthclipse.sourceforge.net/)
 */

const vec3 LightDir = vec3(0.057800323, -0.5716256, -0.81847644);
const float MarchDumping = 0.9;
const float Far = 32.0;
const int MaxSteps = 156;
const float PalmCocoRadius = 0.206;
const float PalmLeafLength = 1.4476738;
const vec2 PalmLeafHash = vec2(0.494, 0.485);
const float PalmParaAmp = 2.005;
const float PalmParaShiftX = -0.32558393;
const float PalmParaShiftY = -0.34907204;
const float PalmSawAngle = 1.270597;
const float PalmSawFreq = 0.15;
const float PalmSawShift = 3.355552;
const mat3 PalmLeavesRot = mat3(0.43584922, 0.08419673, -0.8960726, -0.50385904, 0.8477993, -0.16541581, 0.7457622, 0.52359074, 0.41193599);
const float PalmTrunkSkew = 0.008;
const float PalmTrunkLength = 5.0;
const float TrunkPower = 1.7391294;
const float TrunkPowerFactor = 0.009565654;
const vec3 CoconutColor = vec3(0.3529412, 0.16862746, 0.05882353);

#define M_NONE     -1.0
#define M_SAND      1.0
#define M_TRUNK     2.0
#define M_LEAF      3.0
#define M_COCONUTS  4.0
#define M_WATER     5.0

#define PI      3.141592
#define TWO_PI  6.2831852

#define INF     1e7

#define POINTS 8

vec2 LeafAngles[POINTS];

vec2 min2(vec2 a, vec2 b) {
	return a.x < b.x ? a : b;
//	return mix(a, b, step(b.x, a.x));
}

vec3 min3(vec3 a, vec3 b) {
	return a.x < b.x ? a : b;
//	return mix(a, b, step(b.x, a.x));
}

/**
 * Hash function by David Hoskins.
 * https://www.shadertoy.com/view/4djSRW
 */
vec2 hash21(float p) {
	vec2 p2 = fract(p * vec2(5.3983, 5.4427));
	p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
	return fract(vec2(p2.x * p2.y * 95.4337, p2.x * p2.y * 97.597));
}

/**
 * hash by iq.
 */
float hash(float h) {
	return fract(sin(h) * 43758.5453123);
}

/**
 * hash by iq.
 */
float hash(vec2 n) {
	return fract(sin(dot(n, vec2(1.0, 113.0))) * 43758.5453123);
}

/**
 * Procedural Value Noise 2D, by iq.
 * https://www.shadertoy.com/view/lsf3WH
 */
float noise(vec2 p) {
	vec2 i = floor(p);
	vec2 f = fract(p);

	vec2 u = f * f * (3.0 - 2.0 * f);

	vec2 a = vec2(0.0, 0.0);
	vec2 b = vec2(1.0, 0.0);
	vec2 c = vec2(0.0, 1.0);
	vec2 d = vec2(1.0, 1.0);

	float n0 = hash(i + a);
	float n1 = hash(i + b);
	float n2 = hash(i + c);
	float n3 = hash(i + d);

	float ix0 = mix(n0, n1, u.x);
	float ix1 = mix(n2, n3, u.x);

	return mix(ix0, ix1, u.y);
}

vec3 rotateX(vec3 p, float a) {
	float sa = sin(a);
	float ca = cos(a);
	return vec3(p.x, ca * p.y + sa * p.z, ca * p.z - sa * p.y);
}

vec3 rotateY(vec3 p, float a) {
	float sa = sin(a);
	float ca = cos(a);
	return vec3(ca * p.x + sa * p.z, p.y, ca * p.z - sa * p.x);
}

vec3 rotateZ(vec3 p, float a) {
	float sa = sin(a);
	float ca = cos(a);
	return vec3(ca * p.x + sa * p.y, ca * p.y - sa * p.x, p.z);
}

vec2 rotate(vec2 p, float a) {
	float sa = sin(a);
	float ca = cos(a);
	return vec2(ca * p.x - sa * p.y, sa * p.x + ca * p.y);
}

/**
 * http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
 */
float sdEllipsoid(vec3 p, vec3 r) {
	return (length(p / r) - 1.0) * min(min(r.x, r.y), r.z);
}

float leafParabola(float x) {
	x = clamp(x * 0.35, 0.0, 1.0);
	x -= PalmParaShiftX;

	return -PalmParaAmp * x * (x - 1.0) + PalmParaShiftY;
}

float leafSawtooth(vec3 p) {
	p.z = -abs(p.z);
	vec2 nor = vec2(cos(PalmSawAngle), sin(PalmSawAngle));

	p.z += PalmSawShift;

	p.y = mod(p.y, PalmSawFreq) - PalmSawFreq * 0.5;

	float d = nor.x * p.z + nor.y * p.y - 1.0;
	d = max(d, -p.y);

	return d;
}

float sdLeaf(vec3 p) {
	vec3 q = p;
	p.z = abs(p.z);
	p.xz = rotate(p.xz, PI - PI * 0.35);

	p.z += leafParabola(p.y);
	p.y -= PalmLeafLength + 0.05;

	float d = sdEllipsoid(p, vec3(0.25, PalmLeafLength, 0.04));
	return max(d, -leafSawtooth(q));
}

void initLeafAngles() {
	for (int i = 0; i < POINTS; i++) {
		vec2 rnd = hash21(float(i) * PalmLeafHash.x + PalmLeafHash.y);

		float a = float(i) / float(POINTS) * TWO_PI + (rnd.x * 2.0 - 1.0) * 0.2;
		LeafAngles[i] = vec2(a, -(rnd.y * PI * 0.25 + 1.25));
	}
}

vec3 sdLeaves(vec3 p) {
	p *= PalmLeavesRot;
	float d = INF;
	float index = 0.0;

	const float len = 0.5;
	p.y -= len;

	for (int i = 0; i < POINTS; i++) {

		vec3 q = p + vec3(0.0, len, 0.0);

		vec2 ang = LeafAngles[i];
		q = rotateY(q, ang.x);
		q = rotateZ(q, ang.y);

		float d2 = sdLeaf(q);

		// same as:
		// if (d2 < d) { index = float(i); }
		index = mix(index, float(i), step(d2, d));
		d = min(d, d2);
	}
	return vec3(d, M_LEAF, index);
}

vec2 sdTrunk(vec3 p) {
	vec3 q = p;
	p.x -= 1.0;
	p.y += PalmTrunkLength - 0.15;

	float a = 0.05;
	float x = p.y;

	p.xy = rotate(p.xy, -x * x * PalmTrunkSkew);

	float f = clamp(p.y / PalmTrunkLength, 0.0, 1.0);

	// cylinder with distortions
	float d = length(p.xz) - 0.18 + pow(f, TrunkPower) * TrunkPowerFactor * 10.0;

	d = max(d, -p.y); // trim bottom
	d = max(d, (p.y - PalmTrunkLength)); // trim top

	return vec2(d, M_TRUNK);
}

vec2 sdCoconuts(vec3 p) {
	p *= PalmLeavesRot;
	p.y *= 0.9;
	p += vec3(0.1, 0.15, 0.0);
	float d = dot(p, p);

	p += vec3(-0.3, 0.0, 0.2);
	d = min(d, dot(p, p));

	p += vec3(0.0, 0.0, -0.4);
	d = min(d, dot(p, p));

	d = sqrt(d) - PalmCocoRadius;
	return vec2(d, M_COCONUTS);
}

vec2 sdPalmTree(vec3 p) {
	vec3 q = p;
	q.y -= PalmTrunkLength;
	vec2 res = sdLeaves(q).xy;

	res = min2(res, sdTrunk(q));
	res = min2(res, sdCoconuts(q));

	return res;
}

vec2 sdIsland(vec3 p) {
	p.x = p.x * 0.8 + p.z * 0.2;

	p.y -= 0.1 * sin(p.x * 2.0) + 0.6 * cos(p.z * 0.5 - 1.6);

	p += vec3(-0.7, 3.0, 0.7);

	float d = length(p) - 4.0;

	return vec2(d, M_SAND);
}

vec2 sdWater(vec3 p) {
	float d = p.y - 0.5 * noise(p.xz + iTime) * 0.3;
	return vec2(d, M_WATER);
}

vec2 smin(vec2 a, vec2 b, float k) {
	float h = clamp(0.5 + 0.5 * (b.x - a.x) / k, 0.0, 1.0);
	return vec2(mix(b.x, a.x, h) - k * h * (1.0 - h), mix(b.y, a.y, h));
}

vec3 map(vec3 p) {
	vec3 q = p;
	q.y -= PalmTrunkLength;
	vec2 res = sdTrunk(q);

	res = min2(res, sdCoconuts(q));

	res = smin(res, sdIsland(p), 0.3);
	res = min2(res, sdWater(p));

    return min3(sdLeaves(q), vec3(res, 0.0));
}

vec3 castRay(vec3 ro, vec3 rd) {
	float tmin = 0.0;
	float tmax = Far;

	float precis = 0.002;
	float t = tmin;
	float m = M_NONE;
	float s = 0.0;

	for (int i = 0; i < MaxSteps; i++) {
		vec3 res = map(ro + rd * t);

		if (res.x < precis || t > tmax) {
			break;
		}
		t += res.x * MarchDumping;
		m = res.y;
		s = res.z;
	}
	if (t > tmax) {
		m = M_NONE;
	}
	return vec3(t, m, s);
}

/**
 * http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
 */
float softshadow(vec3 ro, vec3 rd, float mint, float maxt, float k) {
	float res = 1.0;
	for (float t = mint; t < maxt;) {

		float h = map(ro + rd * t).x;

		if (h < 0.001) {
			return 0.0;
		}
		res = min(res, k * h / t);
		t += h;
	}
	return res;
}

vec3 calcNormal(vec3 pos) {
	vec2 eps = vec2(0.001, 0.0);

	vec3 nor = vec3(
			map(pos + eps.xyy).x - map(pos - eps.xyy).x,
			map(pos + eps.yxy).x - map(pos - eps.yxy).x,
			map(pos + eps.yyx).x - map(pos - eps.yyx).x);
	return normalize(nor);
}

float calcAO(vec3 pos, vec3 nor) {
	float occ = 0.0;
	float sca = 1.0;

	for (int i = 0; i < 5; i++) {
		float hr = 0.01 + 0.12 * float(i) / 4.0;
		vec3 aopos = nor * hr + pos;
		float dd = map(aopos).x;

		occ += -(dd - hr) * sca;
		sca *= 0.95;
	}
	return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

float luma(vec3 col) {
	// BT.709
	return dot(col, vec3(0.2126, 0.7152, 0.0722));
}

/**
 *  Three plannar texture mapping by iq.
 */
vec3 texcube(sampler2D sa, vec3 p, vec3 n) {
	vec3 x = texture(sa, p.yz).xyz;
	vec3 y = texture(sa, p.zx).xyz;
	vec3 z = texture(sa, p.xy).xyz;

	return x * abs(n.x) + y * abs(n.y) + z * abs(n.z);
}

vec3 bumpMap(sampler2D tex, vec3 pos, vec3 nor, float amount) {
	float e = 0.001;

	float ref = luma(texcube(tex, pos, nor));

	vec3 gra = -vec3(luma(texcube(tex, vec3(pos.x + e, pos.y, pos.z), nor)) - ref,
					 luma(texcube(tex, vec3(pos.x, pos.y + e, pos.z), nor)) - ref,
					 luma(texcube(tex, vec3(pos.x, pos.y, pos.z + e), nor)) - ref) / e;

	vec3 tgrad = gra - nor * dot(nor, gra);
	return normalize(nor - amount * tgrad);
}

vec3 trunkColor(vec3 pos);

vec3 trunkBumpMap(vec3 pos, vec3 nor, float amount) {
	float e = 0.001;

	float ref = luma(trunkColor(pos));

	vec3 gra = -vec3(luma(trunkColor(vec3(pos.x + e, pos.y, pos.z))) - ref,
					 luma(trunkColor(vec3(pos.x, pos.y + e, pos.z))) - ref,
					 luma(trunkColor(vec3(pos.x, pos.y, pos.z + e))) - ref) / e;

	vec3 tgrad = gra - nor * dot(nor, gra);
	return normalize(nor - amount * tgrad);
}

vec3 trunkColor(vec3 pos) {
	pos.y *= 1.2;
	vec3 brown = vec3(0.48, 0.29, 0.070) * 0.7;

	float ang = atan(pos.z, pos.x) * 14.0 + pos.y * 12.0;
	float y = pos.y + cos(ang) * 0.02;

	float x = cos(y * 14.0);
	x *= x;
	x *= x;

	return mix(brown, brown * 0.6, x);
}

vec3 leafColor(vec3 pos, float index) {

	{ // Move leaf to its untransformed coordinates.
		vec2 ang = LeafAngles[int(index)];

		pos.y -= PalmTrunkLength;
		pos *= PalmLeavesRot;

		pos = rotateY(pos, ang.x);
		pos = rotateZ(pos, ang.y);
	}
	vec3 col = vec3(0.13, 0.46, 0.07);

	float x = pos.y * 1.9 - abs(pos.z) * 0.2;
	x = x + hash(x * 0.0005) * 0.12;

	float t = sin(x * 10.0);
	t *= t;

	return mix(col, col * 0.6, t);
}

vec3 coconutColor(vec3 pos, vec3 nor) {
	pos *= PalmLeavesRot;
	vec3 col = CoconutColor * (0.2 + 0.8 * texcube(iChannel1, pos * vec3(1.0, 0.2, 0.5), nor));

	return col;
}

float clouds(vec3 rd) {
	vec2 uv = rd.xz / (rd.y + 0.2);
	uv *= 70.0;

	uv *= vec2(3.0, 3.0) * 0.005;
	vec2 st = uv;

	float t = iTime * 0.3;
	uv.x += t * 0.2;

	float f = 0.5 * noise(uv);
	uv *= 2.04;
	f += 0.25 * noise(uv);
	uv *= 2.01;
	f += 0.125 * noise(uv);
	float res = f;
	res = smoothstep(0.4, 1.0, res);

	st += vec2(12.0, 34.5);
	st.x += t * 0.3;
	float g = 0.5 * noise(st);
	st *= 2.00;
	g += 0.25 * noise(st);
	st *= 2.03;
	g += 0.125 * noise(st);
	st *= 2.04;
	g += 0.0625 * noise(st);

	res += 0.5 * smoothstep(0.5, 0.9, g);

	return res * 0.8;
}

vec3 background(vec3 rd, float sun) {
	float t = abs(rd.y + 0.4) * 0.9;
	t = min(t, 0.8);

	// sky
	vec3 col = mix(vec3(0.9132, 1.002, 1.2), vec3(0.0, 0.3, 1.2), t) * 1.2;

	// sun
	col = mix(col, vec3(1.0, 1.0, 0.549) * 1.2, pow(sun, 512.0));

	// clouds
	float clo = clouds(rd);
	col = mix(col, vec3(1.0), clo);

	return min(col, 1.0);
}

#define _dist(v)  ((v).x)  /* distance */
#define _mate(v)  ((v).y)  /* material */
#define _styl(v)  ((v).z)  /* style    */

vec3 render(vec3 ro, vec3 rd) {
	initLeafAngles();

	vec3 lig = normalize(-LightDir);
	float sun = clamp(dot(lig, rd), 0.0, 1.0);

	vec3 bg = background(rd, sun);
	vec3 col = bg;

	vec3 res = castRay(ro, rd);

	if (_mate(res) > M_NONE) {
		vec3 pos = ro + _dist(res) * rd;
		vec3 nor = calcNormal(pos);

		bool isWater = false;
		float spe = 0.0;

		if (_mate(res) == M_WATER) {
			isWater = true;

			vec3 rrd = reflect(rd, nor);   // reflected ray direction
			vec3 rro = pos + rrd * 0.01;   // reflected ray origin
			vec3 rres = castRay(rro, rrd); // reflected result

			if (_mate(rres) == M_NONE || _mate(rres) == M_WATER) {
				col = background(rrd, sun);
			} else {
				ro = rro;
				rd = rrd;
				res = rres;
				pos = ro + _dist(res) * rd;
				nor = calcNormal(pos);
			}
		}
		if (_mate(res) <= M_SAND + 0.3) {

			vec3 p = pos * 0.3 * 0.9;
			col = vec3(1.0, 0.827, 0.129) * (1.0 - 0.15 * texcube(iChannel0, p, nor));

			nor = bumpMap(iChannel0, p, nor, 0.0008);
			vec3 ref = reflect(rd, nor);
			spe = pow(clamp(dot(ref, lig), 0.0, 1.0), 4.0);

		} else if (_mate(res) <= M_TRUNK) {

			col = trunkColor(pos);
			nor = trunkBumpMap(pos, nor, 0.35);

		} else if (_mate(res) <= M_LEAF) {

			col = leafColor(pos, _styl(res));
			vec3 ref = reflect(rd, nor);
			spe = pow(clamp(dot(ref, lig), 0.0, 1.0), 2.0);

		} else if (_mate(res) <= M_COCONUTS) {

			col = coconutColor(pos, nor);
		}

		// lighting
		float occ = calcAO(pos, nor);

		float amb = clamp(0.5 + 0.5 * nor.y, 0.0, 1.0);
		float dif = clamp(dot(nor, lig), 0.0, 1.0);
		float fre = pow(clamp(1.0 + dot(nor, rd), 0.0, 1.0), 2.0);

		dif *= softshadow(pos, lig, 0.02, PalmTrunkLength * 2.0, 240.0);

		vec3 brdf = vec3(0.0);

		brdf += 1.00 * dif * vec3(1.00, 0.90, 0.60);
		brdf += 0.40 * spe * vec3(1.00, 0.90, 0.60) * dif;
		brdf += 0.50 * amb * vec3(0.50, 0.70, 1.00) * occ;
		brdf += 0.40 * fre * vec3(1.00, 1.00, 1.00) * occ;

		col = col * brdf;

		if (isWater) {
			col = mix(vec3(0.0, 0.345, 0.612), col, 0.5);
            //col = mix(vec3(0.157, 0.718, 0.922), col, 0.5);
			float t = _dist(res);
			col = mix(col, bg, 1.0 - exp(-0.00007 * t * t * t));
		}
	}
	col += 0.15 * vec3(1.0, 0.898, 0.0) * pow(sun, 32.0);

	return vec3(clamp(col, 0.0, 1.0));
}

void getCamera(vec2 pix, out vec3 ro, out vec3 rd) {
	const float fov = 0.64;

	vec2 coord = (-iResolution.xy + 2.0 * pix) / iResolution.y;
	coord *= fov;

	vec2 mo = iMouse.xy / iResolution.xy;
	float t = iTime * 0.2 + mo.x * 4.0;

	vec3 target = vec3(0.0, 3.0, 0.0);
	ro = vec3(0.0, 2.0, 8.0);

	float s = -0.1 + clamp(mo.y, 0.0, 1.0);
	ro = rotateX(ro, s);
	ro = rotateY(ro, t);

	vec3 dir = normalize(target - ro);

	vec3 up = vec3(0.0, 1.0, 0.0);
	up = rotateX(up, s);
	up = rotateY(up, t);

	up = normalize(up);

	vec3 right = normalize(cross(dir, up));

	rd = normalize(dir + coord.x * right + coord.y * up);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 ro, rd;
	getCamera(fragCoord, ro, rd);

	vec3 col = render(ro, rd);

	col = pow(col, vec3(0.4545));
	fragColor = vec4(col, 1.0);
}

