// Digit drawing function by P_Malin (https://www.shadertoy.com/view/4sf3RN)

float SampleDigit(const in float n, const in vec2 vUV)
{		
	if(vUV.x  < 0.0) return 0.0;
	if(vUV.y  < 0.0) return 0.0;
	if(vUV.x >= 1.0) return 0.0;
	if(vUV.y >= 1.0) return 0.0;

	float data = 0.0;

	     if(n < 0.5) data = 7.0 + 5.0*16.0 + 5.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 1.5) data = 2.0 + 2.0*16.0 + 2.0*256.0 + 2.0*4096.0 + 2.0*65536.0;
	else if(n < 2.5) data = 7.0 + 1.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 3.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 4.5) data = 4.0 + 7.0*16.0 + 5.0*256.0 + 1.0*4096.0 + 1.0*65536.0;
	else if(n < 5.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 6.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 1.0*4096.0 + 7.0*65536.0;
	else if(n < 7.5) data = 4.0 + 4.0*16.0 + 4.0*256.0 + 4.0*4096.0 + 7.0*65536.0;
	else if(n < 8.5) data = 7.0 + 5.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;
	else if(n < 9.5) data = 7.0 + 4.0*16.0 + 7.0*256.0 + 5.0*4096.0 + 7.0*65536.0;

	vec2 vPixel = floor(vUV * vec2(4.0, 5.0));
	float fIndex = vPixel.x + (vPixel.y * 4.0);

	return mod(floor(data / pow(2.0, fIndex)), 2.0);
}

float PrintInt(const in vec2 uv, const in float value )
{
	float res = 0.0;
	float maxDigits = 1.0+ceil(log2(value)/log2(10.0));
	float digitID = floor(uv.x);
	if( digitID>0.0 && digitID<maxDigits )
	{
        float digitVa = 10.0; // mod( floor( value/pow(10.0,maxDigits-1.0-digitID) ), 10.0 );
        res = SampleDigit( digitVa, vec2(fract(uv.x), uv.y) );
	}

	return res;	
}

vec4 circle(vec2 p, vec2 center, float radius)
{
	return mix(vec4(1,1,1,0), vec4(1,0,0,1), smoothstep(radius + 0.005, radius - 0.005, length(p - center)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 xy = uv;
    vec2 dDim = vec2(120,68);  
//    vec2 PDxy = vec2(20.0,30.0)/dDim;
    vec2 PDxy = vec2(135.0,20.0)/dDim;

    float m_fps = 2.0;
    float d0 = round(fract(float(iFrame)/2.0/m_fps)+0.001);
    float d1 = round(fract(float(iFrame-1)/2.0/m_fps)+0.001);
    float v = 0.85;  
    float p = 0.02;  
    float w = 0.9;
    float mag = 0.5;
    vec2 parity = -sign(uv.x-0.5)*vec2(1.0,0.0);	
    vec2 dxdy = iMouse.xy/iResolution.xy-0.5;
    float verg = 0.0;//-0.01+dxdy.x;    

    if(true)
        uv.x = fract(uv.x*2.0);		
      	dDim = dDim*vec2(0.5,1.0);	
    	p *= 2.0;

    uv.x += parity.x*verg;
    uv -= 0.5;

    if(sin(2.0*pi/p*uv.x) > w)
        fragColor = vec4(1,1,1,1);
	else
		fragColor = vec4(0,0,0,1);

    float r = 0.1*mag;
    float s = 1.0;
    float l = .02;
    float d = -0.6;
    float vOffset = -0.15*mag;

    if(circle(uv,vec2(0.0,-vOffset),r).x>0.5 && circle(uv,vec2(0.0,-vOffset),r).y<0.5){
        if ( ( (cos(2.0*pi/l*(uv.x )) > d ) ) && ( abs( uv.y + vOffset) < .005) ){       
            fragColor = 0.8*vec4(1,1,0,1);
        }else{
        	fragColor = vec4(1,0,0,1);
        }
    }

    float pos = s*fract(iTime*v/s)-0.5;
    if(circle(uv,vec2(pos,vOffset),r).x>0.5 && circle(uv,vec2(pos,vOffset),r).y<0.5){
        if ( ( (cos(2.0*pi/l*(uv.x - pos)) > d ) ) && ( abs( uv.y - vOffset ) < .005) ){       
            fragColor = 0.8*vec4(1,1,0,1);
        }else{
        	fragColor = vec4(1,0,0,1);
        }
    }

    uv += 0.5;
    uv.x -= parity.x*verg;
    //if((1.0-uv.x)<PDxy.x && (uv.x)>0.0 && (uv.y)<PDxy.y && (uv.y)>0.0 && xy.x >0.5){		
     //   fragColor = vec4(d0,d0,d0,1.0);
    //}

    if((1.0-uv.x)<2.0*PDxy.x && (uv.x)>0.0 && (uv.y)<0.3*PDxy.y && (uv.y)>0.0 && xy.x >0.0){		
        fragColor = vec4(d0,d0,d0,1.0);
    }

    float col = PrintInt( (uv-vec2(0.01,0.85))*20.0, iFrameRate );
    fragColor += vec4(col,col,col,1.0);
}

// tunnel code borrowed from https://www.shadertoy.com/view/Xslyzf
// Bananas added by Del.

vec4 Banana(vec2 p)
{
    const vec4 col1 = vec4(0.0,.1,.0,1.0);
    const vec4 col2 = vec4(0.9,0.8,0.5,1.0);
    vec2 uv = p.xy;
    vec2 d = uv*(3.0);
    d.x *= 5.0;
    d.y *= 1.8;
    d.x += sin(d.y + iTime*0.2)*1.2;
    d.y += sin(d.x + iTime*0.2)*1.2;
    float v1=length(0.5-fract(d.xy))+0.40;
    return mix(col2,col1,v1*v1*v1*v1)*1.5;
}


void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
	g = (g+g-f.xz)/f.y;
    float t = iTime * .25;
    g.y -= 0.28;
    float a = atan(g.x,g.y)/1.27;
    float d = max(max(abs(g.x),abs(g.y)), min(abs(g.x)+g.y, length(g)));
    vec2 k = vec2(a,1.5/d + t);
    f = Banana(k.xy*0.25)*clamp(d,0.0,1.0);
}

precision highp float;
 vec2  resolution, mouse;
 float time;
const vec3 V  = vec3(0,.001,300);
const vec3 BG = vec3(0);
const vec3 Amb= vec3(.01);
const vec3 PI = vec3(1.5707963,3.1415927,6.2831853);
const float BPM = 120.;
vec2 uv;
float tick;

float rnd(vec3 s){s=fract(s*443.8975);s+=dot(s,s.yzx+19.19);return fract(s.x*s.y*s.z);}
vec4  gamna(vec3 c){return vec4(pow(c,vec3(1./2.2)),1);}
vec3  hsv(float h,float s,float v){return((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;}
mat3  camera(vec3 p, vec3 t, vec3 h){vec3 w=normalize(p-t),u=normalize(cross(w,h));return mat3(u,normalize(cross(u,w)),w);}
float dfPln(vec3 p, vec3 n, float d){return dot(p,n)+d;}
float dfBox(vec3 p, vec3 b, float r){return length(max(abs(p)-b,0.))-r;}

float trick(vec3 p) {
    float d = V.z;
    for (float i=0.; i<3.; i+=1.) {
        d = min(d, dfBox(p-vec3(i*1.08,.78-i*.11,0), vec3(.71-i*.11), .09));
    }
    return d;
}

float map(vec3 p, bool trick) {
    float d = dfPln(p, vec3(0,1,0), 0.);
    for (float i=0.; i<3.; i+=1.) {
        d = min(d, dfBox(p-vec3(i*1.08,.78-i*.11,0), vec3(.71-i*.11), .09));
        d = min(d, dfBox(p-vec3(4.,.5,i), vec3(.4), .09));
        d = min(d, dfBox(p-vec3(4.,i+.5,4.), vec3(.4), .09));
    }
    d = min(d, dfBox(p-vec3(3.,.5,0.), vec3(.4), .09));
    d = min(d, dfBox(p-vec3(4.,.5,3.), vec3(.4), .09));
    if (trick) {
        d = min(d, min(dfBox(p-vec3(4.,3.5,4.), vec3(.4), .09),
                       dfBox(p-vec3(4.,4.5,4.), vec3(.39), .09)));
    }
    return d;
}

vec3 background(vec3 pos, vec3 dir, inout float bld) {
    bld = 0.;
    return BG;
}

vec3 diff(vec3 nml, vec3 lit, vec3 col){return max(dot(nml,lit)*col,0.);}
float shad(vec3 pos, vec3 lit){float s=V.z,t=.05,d;for(int i=20;i!=0;--i){t+=max(d=map(pos+lit*t, true),.05);s=min(s,d/t);if(t>20.)break;}return clamp(s*.2,0.,1.);}
float occl(vec3 pos, vec3 nml){float s=0.;for(float t=.01;t<.8;t+=.05){s+=t-map(pos+nml*t, true);}return clamp(1.-s*.1,0.,1.);}

bool tricktrace(vec3 pos, vec3 dir) {
    float t = 0., d;
    for (int i=80; i!=0; --i) {
        t += (d = trick(pos + dir * t));
        if (d < V.y) break;
        if (t > V.z) return true;
    }
    return false;
}

vec3 trace(inout vec3 pos, inout vec3 dir, inout float bld) {
    float t = 0., d;
    bool trick = tricktrace(pos, dir);
    for (int i=80; i!=0; --i) {
        t += (d = map(pos + dir * t, trick));
        if (d < V.y) break;
        if (t > V.z) return bld * background(pos, dir, bld);
    }
    vec3 p = pos + dir * t;
    vec3 n = normalize(vec3(map(p+V.yxx, true),map(p+V.xyx, true),map(p+V.xxy, true))-map(p, true));
    vec3 c = vec3(1);
    vec3 ldir = normalize(vec3(sin(tick/4.*PI.z)*5.+16.,10.,-15.) - p);
    vec3 lcol = diff(n, ldir, vec3(10)) * shad(p, ldir) * occl(p, n) + Amb;
    vec3 ldir2 = normalize(vec3(-5.,10.,15.) - p);
    vec3 lcol2 = diff(n, ldir2, vec3(10)) * shad(p, ldir2) * occl(p, n) + Amb;
    vec3 col = c * (lcol + lcol2);
    float b=bld*.2;
    pos = p + n*V.y;
    dir = reflect(dir, n);
    bld *= .8;
    return mix(col, BG, clamp((length(p.xz)-1.)/16.,0.,1.))*b;
}

vec3 render(in vec3 pos, in vec3 dir) {
    float b = 1.;
    vec3 col = trace(pos, dir, b);
    if (b > V.y) col += trace(pos, dir, b);
    if (b > V.y) col += trace(pos, dir, b);
    return col;
}

vec4 entryPoint(vec2 fragCoord) {
    uv = (fragCoord * 2.-resolution) / resolution.y;
    tick = time * BPM / 60.;

    float scan = 1., dist = 0.;
    vec3 pos = vec3(10.,10.,10.);
    if (iMouse.z > .5) {
        scan = (mod(fragCoord.y, 2.)+1.) * (1.-length(uv)*.3);
        dist += (.0003/(uv.y+fract(tick/16.)*2.-1.));
        vec2 r = mouse/resolution*2.-1.;
        pos = vec3(10.+r.x*12.,10.+r.y*4.,10.-r.x*12.);
    }
    vec3 dir = camera(pos, vec3(2,0,2), vec3(0,1,0)) * normalize(vec3(uv+vec2(dist,0),-3));
    return gamna(render(pos, dir)) * scan;
}

void mainImage(out vec4 flagColor,in vec2 flagCoord) {
    resolution = iResolution.xy;
    time = iTime;
    mouse = iMouse.xy;
    flagColor = entryPoint(flagCoord);
}

#define ORANGE vec3(1.0, 0.4, 0.1)
#define YELLOW vec3(1.0, 0.8, 0.3)
const vec2 PALM_CENTER = vec2(.35, .75);
const vec2 SUN = vec2(1.4, .05);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 dst = uv - PALM_CENTER;

    float head_rad = .2;
    head_rad += .1 * sin(atan(dst.x, dst.y) * 10.0 - 20. * dst.x + sin(iTime));
    head_rad += .0125 * sin(atan(dst.y, dst.x) * 100.0);
    float head = smoothstep(head_rad, head_rad + .02, length(dst));

    float trunk_width = .0125 + cos(dst.y * 250.) * 0.001;
    trunk_width += exp(-20. * uv.y);
    float trunk = smoothstep(trunk_width, trunk_width + .005,
                             abs(dst.x - sin(dst.y * 2.) * .2));
    trunk = step(0.5, trunk + step(0., dst.y));

    float lsun = length(uv * vec2(iResolution.x/iResolution.y, 1.) - SUN);

    float f = 1.0 - smoothstep(.1, .15, lsun);
