// An example of how to compute a distance estimation for an ellipse (which provides
// constant thickness to its boundary). This is achieved by dividing the implicit 
// description by the modulo [modulus] of its gradient. The same process can be applied to any
// shape defined by an implicity formula (ellipses, metaballs, fractals, mandelbulbs).
//
// More info here:
// http://www.iquilezles.org/www/articles/distance/distance.htm

float r;

float ellipse2(vec2 p,float r) // approx. distance between p and nearest point on ellipse
{
    vec2 s = vec2(1.,3.);  // shape of ellipse
    float f = length(p*s); // definition of ellipse
    return abs(f-r)*f      // f(x,y)... 
        /length(p*s*s);    // ... divided by analytical gradient
}

float f(vec2 p)
{
	float  r = length(p);float a = atan(p.y,p.x);
    // return r-1.+0.5*sin(3.*a+2.*r*r);   
    vec2 s = vec2(1.,3.);  // shape of ellipse
    float f = length(p*s); // definition of ellipse
    return f;
}

vec2 grad( in vec2 x )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

float color( in vec2 x )
{
    float v = f( x );
    vec2  g = grad( x );
    float de = abs(v)/length(g);
    return step( 1.2, de );
}

