const float pi = 3.1415926535897932384626433832795;
const float M_120Deg = pi*2.0/3.0;
const float M_240Deg = M_120Deg*2.0;

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.+vec3(0,4,2),6.) -3. ) -1., 0.,1.);
    return c.z + c.y * (rgb-.5)*(1.-abs(2.*c.z-1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 translated = 2.0*(fragCoord - iResolution.xy/2.0);
    translated /= ( iResolution.x<iResolution.y ? iResolution.x : iResolution.y );

    float dist = length( translated );
    float innerRadius = sin(iTime)/6.0+0.3;

    vec3 hsl = vec3( iTime/3.0 + atan( translated.x, translated.y )/(pi*2.0), 1.0, (dist>innerRadius?0.5-(dist-innerRadius)/(2.0*innerRadius):dist/(innerRadius*2.0)));
    fragColor = vec4( hsl2rgb( hsl ), 1.0 );
}

