vec3 hsvtorgb(float h, float s, float v){
	float r = v;
	float g = v;
	float b = v;
	if (s > 0.0f) {
	    h *= 6.0f;
	    int i = int(h);
	    float f = h - float(i);
	    switch (i) {
	        default:
	        case 0:
	            g *= 1.0 - s * (1.0 - f);
	            b *= 1.0 - s;
	            break;
	        case 1:
	            r *= 1.0 - s * f;
	            b *= 1.0 - s;
	            break;
	        case 2:
	            r *= 1.0 - s;
	            b *= 1.0 - s * (1.0 - f);
	            break;
	        case 3:
	            r *= 1.0 - s;
    	        g *= 1.0 - s * f;
	            break;
	        case 4:
	            r *= 1.0 - s * (1.0 - f);
	            g *= 1.0 - s;
	            break;
	        case 5:
	            g *= 1.0 - s;
	            b *= 1.0 - s * f;
	            break;
	    }
	}
    return vec3(r, g, b);
}
vec3 toon(vec3 col){
    float r = col.x;
    float g = col.y;
    float b = col.z;
    float max = r > g ? r : g;
	max = max > b ? max : b;
	float min = r < g ? r : g;
	min = min < b ? min : b;
	float h = max - min;
	if (h > 0.0f) {
	    if (max == r) {
	        h = (g - b) / h;
	        if (h < 0.0f) {
	            h += 6.0f;
	        }
	    } else if (max == g) {
	        h = 2.0f + (b - r) / h;
	    } else {
	        h = 4.0f + (r - g) / h;
	    }
	}
	h /= 6.0f;
	float s = (max - min);
	if (max != 0.0f)
	    s /= max;
	float v = max;

    v = floor(v * 4.0) / 4.0;

    return hsvtorgb(h, s, v);
}
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}
float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
int stop = 0;
vec3 stoppos = vec3(0.0);
float map(vec3 p){
    float d = length(max(abs(p) - vec3(1.0),0.0));
    vec3 p1 = p;
    float the = iTime / 2.1;
    p1.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    d = smin(d, length(p1 - vec3(1.2,1.2,1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(1.2,1.2,-1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,-1.2)) - 0.7, 3.0);
    if(d < 0.001) col= vec3(0.0, 1.0, 1.0);
    d = min(d, dot(p,vec3(0.0, 1.0, 0.0)) + 1.0);
    return d;
}
vec3 grad(vec3 p) {
    vec2 d = vec2(0.0, 0.001);
	float dFx = map(p + d.yxx) - map(p - d.yxx);
	float dFy = map(p + d.xyx) - map(p - d.xyx);
	float dFz = map(p + d.xxy) - map(p - d.xxy);
	return normalize(vec3(dFx, dFy, dFz));
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    vec3 p;
    for(int i = 0; i < 256; ++i){
    	p = o + r * t;
        float d = map(p);
        if(d < 0.001){
            stop = 1;
        	break;
        }
        t += d * 0.9;
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv - vec2(0.0, PI / 3.0), 1.0));

    float the = iTime;
    vec3 o = vec3(0.0,10.0, -10.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    o.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float light = 0.0;
    if(stop == 1){
    	vec3 nor = grad(stoppos);
        light = 0.6 * max(dot(nor, vec3(1.0,0.8,0.4)), 0.0) + 0.3;
        stop = 0;
        trace(stoppos + nor * 0.002, normalize(vec3(1.0,0.7,0.4)));
        if(stop == 1) light = 0.1;
    }

    vec3 fc = vec3(light) * col;
    fc = toon(fc);
    fragColor = vec4(fc,1.0);
}

