/**/

#define MAX_ITERATIONS 256

#define RAY_T_MIN 0.001
#define RAY_T_MAX 1.0e30

#define EPSILON 0.01

// Polynomial smooth minimum by iq
float smin(float a, float b, float k) {
  float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
  return mix(a, b, h) - k*h*(1.0-h);
}

struct Light
{
    vec3 point;
};

struct Material 
{
    float ambient;
	float diffuse;
    float specular;
    float a; // a >> 1.0
};


float phong (in vec3 L, in vec3 N, in vec3 R, in vec3 V, in Light light, in Material mat)
{
    float diffuse = mat.diffuse*dot(L, N);
    float specular = mat.specular*pow(dot(R, V), mat.a);

	return diffuse + specular + mat.ambient;
}

struct Sphere 
{
	vec3 center;
    float r;
};


struct Plane
{
    vec3 center;
    vec3 normal;
};

float opU(float d1, float d2)
{
    return min(d1,d2);
}

float opS( float d1, float d2)
{
    return max(-d1,d2);
}

float opI(float d1, float d2)
{
    return max(d1,d2);
}

float sphereSDF (vec3 point, Sphere s)
{
	return distance(point, s.center)-s.r;
}

float opBlendSS (vec3 p, Sphere s1, Sphere s2, float k)
{
    float d1 = sphereSDF(p, s1);
    float d2 = sphereSDF(p, s2);
    return smin(d1, d2, k);
}

float sceneSDF (vec3 p)
{
    Sphere s1 = Sphere(vec3(sin(-iTime)*3., 1.5, 0), 2.0);
    Sphere s2 = Sphere(vec3(sin(iTime)*3., -1.5, 0), 2.0);
    vec3 c = vec3(15.,15.,15.);
    vec3 q = mod(p,c)-0.5*c;
    return opBlendSS(q, s1, s2, 1.5);
}

vec3 estimateNormal(vec3 point) {
    return normalize(vec3(
        sceneSDF(vec3(point.x + EPSILON, point.y, point.z)) - sceneSDF(vec3(point.x - EPSILON, point.y, point.z)),
        sceneSDF(vec3(point.x, point.y + EPSILON, point.z)) - sceneSDF(vec3(point.x, point.y - EPSILON, point.z)),
        sceneSDF(vec3(point.x, point.y, point.z  + EPSILON)) - sceneSDF(vec3(point.x, point.y, point.z - EPSILON))
    ));
}

float trace (vec3 eye, vec3 dir)
{

    float t = RAY_T_MIN;
    int i = 0;    
    while(i < MAX_ITERATIONS && t < RAY_T_MAX)
    {
        float radius = sceneSDF(eye + (dir*t));
        if (radius < RAY_T_MIN)
        {
            break;
        }
        t += radius;
        i++;

    if (i==MAX_ITERATIONS || t == RAY_T_MAX)
        return RAY_T_MAX;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    const float PI = 3.14159;

    vec3 eye = vec3(0,0,5);

    vec3 forward = vec3(0,0,-1);
    vec3 up = vec3(0,1,0);
    vec3 right = cross(forward, up);

    float fov = PI/4.0;

    float aspect = iResolution.y / iResolution.x;

    float w = tan(fov);
    float h = w * aspect;

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 point = uv*2.0-1.0;

    vec3 dir = normalize(forward + point.x * w * right + point.y * h * up);

    Light light = Light(vec3(.0, .0, 3.));

    Material mat = Material(0.0, 0.3, 0.7, 10.0);

    float t = trace(eye, dir);
    vec3 P = eye + (dir*t); // P is point of intersection

    vec3 L = normalize(light.point - P);
    vec3 V = dir;
    vec3 N = estimateNormal(P);
    vec3 R = reflect(N, L);

    vec3 color = vec3(phong(L, N, R, V, light, mat));

    /*
    if (!(trace(P + N*0.01, L) == RAY_T_MAX))
    {
        color *= vec3(0.13);
    }
	*/

    //color = N+1.0 /2.0;
    fragColor = vec4(color, 1.0);
}

float PI = 3.1415926535897932384626433832795;

vec2 brickTile(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    float s = sin(iTime);

    if(s > 0.0) {
        _st.x += step(1., mod(_st.y,2.0)) * fract(iTime/PI*2.0);
    } else {
        _st.y += step(1., mod(_st.x,2.0)) * fract(iTime/PI*2.0); 
    }


    return fract(_st);
}

float box(vec2 _st, vec2 _size){
    _size = vec2(0.5)-_size*0.5;
    vec2 uv = smoothstep(_size,_size+vec2(1e-4),_st);
    uv *= smoothstep(_size,_size+vec2(1e-4),vec2(1.0)-_st);
    return uv.x*uv.y;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

