    st.x *= iResolution.x / iResolution.y;
    st /= vec2(2.15,0.65)/1.5;
    st = brickTile(st,5.0);

    vec3 color = vec3(box(st,vec2(0.9)));
	color = mix(vec3(0.85, 0.84, 0.79),
                vec3(0.82, 0.44, 0.31),
