void mainImage( out vec4 O, vec2 U )
{
	U /= iResolution.xy;
    float x = fract( U.x * N +iTime), // cut in N cycles
          L = 1.-delay,
	      y = 6.28 * min(1.,  x / L ),   // remap and clamp in each cycle
          z = sin(y);
    O = vec4( abs( .5+.2*z - U.y ) < .01 );
