const float pi = 3.14159265359;
const float triangleScale = 0.816497161855865; // ratio of edge length and height
const vec3 orange = vec3(0.937, 0.435, 0.0);

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

vec4 getTriangleCoords(vec2 uv) {
    uv.y /= triangleScale;
    uv.x -= uv.y / 2.0;
    vec2 center = floor(uv);
    vec2 local = fract(uv);

    center.x += center.y / 2.0;
    center.y *= triangleScale;

    if (local.x + local.y > 1.0) {
    	local.x -= 1.0 - local.y;
        local.y = 1.0 - local.y;
        center.y += 0.586;
        center.x += 1.0; 
    } else {
        center.y += 0.287;
    	center.x += 0.5;
    }

    return vec4(center, local);
}

vec4 getLoader(vec4 triangle) {
    if (length(triangle.xy) > 1.6) {
        return vec4(0.0);
    }

    float angle = atan(triangle.x, triangle.y);
    float seed = rand(triangle.xy);
    float dst = min(triangle.z, min(triangle.w, 1.0 - triangle.z - triangle.w)) * 15.0;
    float glow = dst < pi ? pow(sin(dst), 1.5) : 0.0;

    return vec4(mix(orange, vec3(1.0), glow * 0.07), pow(0.5 + 0.5 * sin(angle - iTime * 6.0 + seed), 2.0));
}

float getBackground(vec4 triangle) {
    float dst = min(triangle.z, min(triangle.w, 1.0 - triangle.z - triangle.w)) - 0.05;

    if (triangle.y > 1.9 || triangle.y < -2.4 || dst < 0.0) {
        return 0.0;
    }

    float value = pow(0.5 + 0.5 * cos(-abs(triangle.x) * 0.4 + rand(triangle.xy) * 2.0 + iTime * 4.0), 2.0) * 0.08;    
    return value * (dst > 0.05 ? 0.65 : 1.0);
}

vec3 getColor(vec2 uv) {
    uv *= 2.0 / iResolution.y;

    vec3 background = vec3(getBackground(getTriangleCoords(uv * 6.0 - vec2(0.5, 0.3))));
 	vec4 loader = getLoader(getTriangleCoords(uv * 11.0));

    vec3 color = mix(background, loader.rgb, loader.a);
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ) {
    fragCoord = fragCoord - 0.5 * iResolution.xy;
	fragColor.rgb = 0.25 * (getColor(fragCoord)
                            + getColor(fragCoord + vec2(0.5, 0.0))
                            + getColor(fragCoord + vec2(0.5, 0.5))
                            + getColor(fragCoord + vec2(0.0, 0.5)));
}

vec2 brickTile(vec2 _st, float _zoom){
    _st *= _zoom;
	// http://thebookofshaders.com/09/
    // Here is where the offset is happening
    float pari = step(1., mod(_st.y,2.0));
    _st.x += pari * 0.5;

	float frequency = 0.4;
    //_st.x += fract(iTime * frequency) * pari;
    _st.x += iTime * frequency * ( pari - step(pari, .1) );

    return fract(_st);
}

vec3 elica(vec2 st, vec2 orig, float resize, float smoothness, float nPale){
    // https://thebookofshaders.com/07/
    // Use polar coordinates instead of cartesian
    // This technique is a little restrictive but very simple.
    // It consists of changing the radius of a circle depending 
    // on the angle to achieve different shapes.
    vec2 toCenter = vec2(orig)-st;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*resize;

    float f = cos(angle*nPale);
    return vec3( 1.-smoothstep(f,f+smoothness,radius) );;
}


vec3 when_eq(vec3 x, vec3 y) {
  return 1.0 - abs(sign(x - y));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 st = fragCoord/iResolution.y;
    vec3 color = vec3(1.0);

    vec3 orange = vec3(0.9, 0.5, 0.0);
    vec3 azul = vec3(0.2, 0.6, 0.7);
    vec3 yellow = vec3(0.8, 0.7, 0.0);

    // Apply the brick tiling
    st = brickTile(st,6.0);

    orange *= elica(st, vec2(0.44), 4.6, 1.2, 3.0);
    azul *= elica(st, vec2(0.47), 4.8, 1.9, 6.0);
    yellow *= elica(st, vec2(0.5), 4.6, 1.2, 9.0);

    color = orange +azul + yellow;

    fragColor = vec4(color,1.0);
}

// Internal

struct State {
    bool isSmooth;
    float smoothness;
    bool isFill;
    vec4 fill;
    bool isStroke;
    vec4 stroke;
    float strokeWeight;
    float strokeCap;
    int ellipseMode;
    int rectMode;
    mat4 invMatrix;
    vec2 coord;
};

State p_states[10];
int p_states_length;
State p_state;
vec4 p_fragColor;
vec2 p_fragCoord;

// premultiplied
void p_pixel(vec4 color) {
    vec4 src = vec4(color.rgb * color.a, color.a);
    vec4 dst = p_fragColor;
    p_fragColor = src + dst * (1.0 - src.a);
}

void p_fill(float value) {
    vec4 color = p_state.fill;
    color.a *= value;
    if (p_state.isFill && color.a > 0.0) {
        p_pixel(color);
    }
}

void p_stroke(float value) {
    vec4 color = p_state.stroke;
    color.a *= value;
    if (p_state.isStroke && color.a > 0.0) {
        p_pixel(color);
    }
}

void p_updateCoord() {
    p_state.coord = (p_state.invMatrix * vec4(p_fragCoord, 0.0, 1.0)).xy;
}

float p_step(float edge, float x) {
    if (p_state.isSmooth) {
        float hs = p_state.smoothness * 0.5;
        return smoothstep(edge - hs, edge + hs, x); 
    } else {
        return step(edge, x);
    }
}

float p_not(float a) {
    return 1.0 - a;
}

float p_and(float a, float b) {
    return a * b;
}

float p_or(float a, float b) {
    return 1.0 - ((1.0 - a) * (1.0 - b));
}

float p_line(float x, float y, vec2 n, float o) {
    vec2 pos = vec2(x, y);
    n = normalize(n);
    vec2 v = p_state.coord - pos;
    float dist = dot(n, v);
    return p_not(p_step(o, dist));
}

float p_cone(float x, float y, float start, float stop, float o) {
    vec2 vstart = vec2(cos(start), sin(start));
    vec2 vstop = vec2(cos(stop), sin(stop));

    vec2 nstart = vec2(vstart.y, -vstart.x);
    vec2 nstop = vec2(-vstop.y, vstop.x);

    float coneStart = p_line(x, y, nstart, o);
    float coneStop = p_line(x, y, nstop, o);
    float cone = 0.;
    if (cross(vec3(vstart, 0.), vec3(vstop, 0.)).z > 0.) {
        cone = p_and(coneStart, coneStop);
    } else {
        cone = p_or(coneStart, coneStop);
    }
    return cone;
}

float p_circle(float x, float y, float d, float o) {
    float r = abs(d) * 0.5;
    float dist = distance(vec2(x, y), p_state.coord);
    return p_not(p_step(r + o, dist));
}

float p_ellipse(float x, float y, float w, float h, float o) {
    float hw = abs(w) * 0.5 + o;
    float hh = abs(h) * 0.5 + o;
    vec2 dist =  p_state.coord - vec2(x, y);
    if (hw < hh) {
        dist.y *= hw / hh;
        return p_not(p_step(hw, length(dist)));
    } else {
        dist.x *= hh / hw;
        return p_not(p_step(hh, length(dist)));
    }
}

float p_rect(float x, float y, float w, float h, float o) {
    if (w < 0.0) {
        x += w;
        w *= -1.0;
    }
    if (h < 0.0) {
        y += h;
        h *= -1.0;
    }
    return p_and(p_and(p_step(x - o, p_state.coord.x), 
                       p_not(p_step(x + w + o, p_state.coord.x))),
                 p_and(p_step(y - o, p_state.coord.y), 
                       p_not(p_step(y + h + o, p_state.coord.y))));
}

