#define HALF_PI 1.57079632679489661923
#define PI 3.14159265358979323846
#define QUARTER_PI 0.7853982
#define TAU 6.28318530717958647693
#define TWO_PI 6.28318530717958647693

// Constants / angleMode
#define DEGREES 0
