}

// void colorMode

void fill(vec4 color) {
    p_state.isFill = true;
    p_state.fill = color;
}

void noFill() {
    p_state.isFill = false;
}

void stroke(vec4 color) {
    p_state.isStroke = true;
    p_state.stroke = color;
}

void noStroke() {
    p_state.isStroke = false;
}

// Shape / 2D Primitives

void arc(float x, float y, float w, float h, float start, float stop, int mode) {
    if (p_state.ellipseMode == CENTER) {
        // nothing / default
    } else if (p_state.ellipseMode == RADIUS) {
        w *= 2.0;
        h *= 2.0;
    } else if (p_state.ellipseMode == CORNER) {
        x += w * 0.5;
        y += h * 0.5;
    } else if (p_state.ellipseMode == CORNERS) {
        w = w - x;
        h = h - y;
        x += w * 0.5;
        y += h * 0.5;
    }

    float fill    = p_ellipse(x, y, w, h, 0.0);
    float istroke = p_not(p_ellipse(x, y, w, h, -p_state.strokeWeight * 0.5));
    float ostroke = p_ellipse(x, y, w, h, p_state.strokeWeight * 0.5);

    vec2 vstart = vec2(cos(start) * w * 0.5, sin(start) * h * 0.5);
    vec2 vstop = vec2(cos(stop) * w * 0.5, sin(stop) * h * 0.5);
    start = atan(vstart.y, vstart.x);
    stop = atan(vstop.y, vstop.x);

    float sx = x + vstart.x;
    float sy = y + vstart.y;
    vec2 v = vstop - vstart;
    vec2 n = vec2(-v.y, v.x);

    if (mode == DEFAULT) {
        float cone = p_cone(x, y, start, stop, 0.);
        p_fill(p_and(fill, cone));
        p_stroke(p_and(p_and(istroke, ostroke), cone));

    } else if (mode == OPEN) {
        float line = p_line(sx, sy, n, 0.);
        p_fill(p_and(fill, line));
        p_stroke(p_and(p_and(istroke, ostroke), line));

    } else if (mode == CHORD) {
        float fillLine = p_line(sx, sy, n, 0.);
        float istrokeLine = p_not(p_line(sx, sy, n, -p_state.strokeWeight * 0.5));
        float ostrokeLine = p_line(sx, sy, n, p_state.strokeWeight * 0.5);
        p_fill(p_and(fillLine, fill));
        p_stroke(p_and(p_or(istroke, istrokeLine), p_and(ostroke, ostrokeLine)));

    } else if (mode == PIE) {
        float fillCone = p_cone(x, y, start, stop, 0.);
        float istrokeCone = p_not(p_cone(x, y, start, stop, -p_state.strokeWeight * 0.5));
        float ostrokeCone = p_cone(x, y, start, stop, p_state.strokeWeight * 0.5);
        p_fill(p_and(fill, fillCone));
        p_stroke(p_and(p_or(istroke, istrokeCone), p_and(ostroke, ostrokeCone)));

    }
}

void circle(float x, float y, float d) {
    if (p_state.ellipseMode == CENTER) {
        // nothing / default
    } else if (p_state.ellipseMode == RADIUS) {
        d *= 2.0;
    } else if (p_state.ellipseMode == CORNER) {
        x += d * 0.5;
        y += d * 0.5;
    }

    float fill    = p_circle(x, y, d, 0.0);
    float istroke = 1.0 - p_circle(x, y, d, -p_state.strokeWeight * 0.5);
    float ostroke = p_circle(x, y, d, p_state.strokeWeight * 0.5);
    p_fill(fill);
    p_stroke(p_and(istroke, ostroke));
}

void ellipse(float x, float y, float w, float h) {
    if (p_state.ellipseMode == CENTER) {
        // nothing / default
    } else if (p_state.ellipseMode == RADIUS) {
        w *= 2.0;
        h *= 2.0;
    } else if (p_state.ellipseMode == CORNER) {
        x += w * 0.5;
        y += h * 0.5;
    } else if (p_state.ellipseMode == CORNERS) {
        w = w - x;
        h = h - y;
        x += w * 0.5;
        y += h * 0.5;
    }

    float fill    = p_ellipse(x, y, w, h, 0.0);
    float istroke = 1.0 - p_ellipse(x, y, w, h, -p_state.strokeWeight * 0.5);
    float ostroke = p_ellipse(x, y, w, h, p_state.strokeWeight * 0.5);
    p_fill(fill);
    p_stroke(p_and(istroke, ostroke));
}

void line(float x1, float y1, float x2, float y2) {
    vec2 v = vec2(x2 - x1, y2 - y1);
    vec2 n = vec2(v.y, -v.x);
    float istroke = 1.0 - p_line(x1, y1, n, -p_state.strokeWeight * 0.5);
    float ostroke = p_line(x1, y1, n, p_state.strokeWeight * 0.5);
    float bstroke = p_line(x1, y1, -v, 0.0);
    float estroke = p_line(x2, y2, v, 0.0);
    p_stroke(p_and(p_and(istroke, ostroke), p_and(bstroke, estroke)));
}

void point(float x, float y) {
    float stroke = p_circle(x, y, p_state.strokeWeight, 0.0);
    p_stroke(stroke);
}

void rect(float x, float y, float w, float h) {
    if (p_state.rectMode == CORNER) {
        // nothing / default
    } else if (p_state.rectMode == CORNERS) {
        w = w - x;
        h = h - y;
    } else if (p_state.rectMode == CENTER) {
        x -= w * 0.5;
        y -= h * 0.5;
    } else if (p_state.rectMode == RADIUS) {
        x -= w;
        y -= h;
        w *= 2.0;
        h *= 2.0;
    }

    float fill    = p_rect(x, y, w, h, 0.0);
    float istroke = 1.0 - p_rect(x, y, w, h, -p_state.strokeWeight * 0.5);
    float ostroke = p_rect(x, y, w, h, p_state.strokeWeight * 0.5);
    p_fill(fill);
    p_stroke(p_and(istroke, ostroke));
}

// Shape / Attributes

void noSmooth() {
    p_state.isSmooth = false;
}

void yesSmooth() {
    p_state.isSmooth = true;
}

void ellipseMode(int mode) {
    p_state.ellipseMode = mode;
}

void rectMode(int mode) {
    p_state.rectMode = mode;
}

// void strokeCap

// void strokeJoin

void strokeWeight(float weight) {
    p_state.strokeWeight = weight;
}

// Transform

void applyInvMatrix(mat4 invMatrix) {
    p_state.invMatrix = invMatrix * p_state.invMatrix;
    p_updateCoord();
}

void rotate(float angle) {
    mat4 im = mat4(1.0);
    im[0][0] = cos(-angle);
    im[0][1] = sin(-angle);
    im[1][0] = -sin(-angle);
    im[1][1] = cos(-angle);
    applyInvMatrix(im);
}

void scale(float x, float y) {
    mat4 im = mat4(1.0);
    im[0][0] = 1. / x;
    im[1][1] = 1. / y;
    applyInvMatrix(im);
}

void translate(float x, float y) {
    mat4 im = mat4(1.0);
    im[3][0] = -x;
    im[3][1] = -y;
    applyInvMatrix(im);
}

// Structure

void push() {
    p_states[p_states_length] = p_state;
    p_states_length++;
}

void pop() {
    if (p_states_length > 0) {
        p_states_length--;
        p_state = p_states[p_states_length];
    }
}

// End of Library

void draw() {
    vec4 black   = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 red     = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 green   = vec4(0.0, 1.0, 0.0, 1.0);
    vec4 blue    = vec4(0.0, 0.0, 1.0, 1.0);
    vec4 yellow  = vec4(1.0, 1.0, 0.0, 1.0);
    vec4 cyan    = vec4(0.0, 1.0, 1.0, 1.0);
    vec4 magenta = vec4(1.0, 0.0, 1.0, 1.0);
    vec4 white   = vec4(1.0, 1.0, 1.0, 1.0);

    clear();
    background(black);
    fill(red);
    stroke(green);
    strokeWeight(2.0);
    translate(iMouse.x, iMouse.y);

    circle(0., 0., 50.);
    rectMode(CENTER);

    for (int i=0; i<10; i++) {
        push();
        rotate(iTime + float(i));
        translate(100. + sin(iTime * 5. + float(i) * 2.) * 10., 0.);
        rect(0., 0., 10., 10.);
        rotate(iTime * 2.);
        translate(20., 0.);
        point(0., 0.);
        pop();
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    p_state.isSmooth = true;
    p_state.isFill = false;
    p_state.fill = vec4(0.0);
    p_state.isStroke = false;
    p_state.stroke = vec4(0.0);
    p_state.strokeWeight = 1.0;
    p_state.smoothness = 1.0;
    p_state.ellipseMode = CENTER;
    p_state.rectMode = CORNER;
    p_state.invMatrix = mat4(1.0);
    p_state.coord = fragCoord;

    p_fragCoord = fragCoord;
    p_states_length = 0;

    draw();

    fragColor = p_fragColor;
}

// City Fractal
// By Hazel Quantock

// disable this if running at high resolution
#define ANTIALIASING 1

// enable this to smooth out the frame rate (this seems to work better in Chrome than Firefox)
//#define FPS30 1

// ray march distance multiplier, use range 1-10
#define DRAWDIST 10

#define WIDESCREEN 1
#define POSTPROCESS 1


uint Hash( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uint k = n * n * n * 48271U;
    return k & 0xffffffffU;
}


const float roadRad = .2;

// buildings: x = horizontal distance, y = vertical distance
vec2 Buildings( vec2 uv )
{
//    uv -= .05; // make thick & thin roads
    uv -= .5; // less repetetive buildings! (nice)

    uvec2 iuv = uvec2(abs(uv));

    // DAMMIT! They're all the same building!
    // can skip the wrap in the last stage, but it's only 1.68 smaller than the next wrap
    // so we get 2x2 blocks of buildings, of 2 (maybe 4) different heights
    uint seed = Hash(iuv.x+(iuv.y<<8U));
    float h = float(seed&0xffffffU)/float(0xffffffU);

    // => most buildings have a mirror running through them, so I need a mirror that syncs seeds for straddling buildings
/* e.g: 3D "buildings" pattern, reflection = 90 degree rotation...
=> won't get same angle both sides => will slice through buildings into the ground
Which will break the SDFs (creating flat spots and shallower gradients - fairly safe but annoying)
*/    
/*
It would be easy if we were spawning (not just pickig) tiles
each tile could check its already picked neighbours so would never create invalid transition
though obviously wouldn't be deterministic
*/
/*
I wonder if there's something we can do by colouring in an intermediate buffer...
*/

    vec2 d = abs(fract(uv)-.5)-(.5-roadRad);
    float f = max(d.x,d.y);
    if ( f > .0 ) f = length(max(d,0.));

    return vec2(f,h);
}

// tiled reflection - repeat [0,1] on x & y
vec2 Wrap( vec2 uv )
{
    // 0,1,0,1,0,1,0,1 triangle wave
//    return abs(mod(uv-.5,2.)-1.)-.5;
    // allow precession whilst also partly wrapping
    //0,1,2,1,2,3,2,3,4,3,4,5 - 1:1 gradient so scale isn't affected
    vec2 fl = floor(uv/3.);
    vec2 fr = uv-3.*fl;
    return fl + min(fr,4.-fr);
}

vec2 Rot( vec2 uv, float a )
{
    return uv*cos(a) + uv.yx*vec2(1,-1)*sin(a);
}


vec2 Map( vec2 uv )
{
    int n = 4; // lower = cheaper
    float a = 1.275;
    float s = 4.1;

    // adjust our view
    //uv *= exp(-sin(iTime/3.)+1.);
    //uv += vec2(.618,.382)*.1*iTime;
    float scale = 3.;
	uv /= scale * pow(s,float(n)); // mul by all the scales we'll accumulate in the loop
    uv = Rot(uv,.5-a);

	for ( int i=0; i < n; i++ )
    {
        //uv += -.07;
        uv *= s;
	    uv = Rot(uv,a);
        uv = Wrap(uv);
    }

    vec2 d = Buildings( uv ); // .5/.0 look okay, other values mess up roads (but .05 gives thick & thin roads
    d.x *= scale;
    return d;
}


float SDF( vec3 pos )
{
    vec2 d = Map(pos.xz);

    const float minH = .3;
    const float houseH = 2. - minH;
    const float scraperH = 6. - houseH;
    const float maxH = minH+houseH+scraperH;
    float h = d.y*houseH + pow(d.y,20.)*scraperH + minH;
    if ( d.y < .1 ) h = 0.; // parks

    // need to constrain the gradient - because we don't know how tall adjacent buildings are
    // but this confuses the ambient occlusion, making roofs a bit darker
    float roof = min( pos.y-h, roadRad*2. );

    float sdf = max( d.x, roof );

    // floor
    sdf = min( sdf, pos.y );

    sdf = max( sdf, pos.y-maxH ); // max h, let it ramp up above roadRad so the sky looks good

    return sdf;
}


vec3 mainImage2( vec2 fragCoord )
{
    vec3 colour;

	vec2 uv = (fragCoord.xy - iResolution.xy*.5) / iResolution.x;

#if defined(WIDESCREEN)
    float sh = iResolution.x/2.4;
#else
    float sh = iResolution.y;
#endif
	vec3 ray = normalize(vec3((fragCoord-iResolution.xy*.5)/sh,2.+smoothstep(.1,.4,sin(iTime/8.))));

    vec2 a = vec2(.29,iTime/50.);
    ray.zy = ray.zy*cos(a.x)+ray.yz*sin(a.x)*vec2(1,-1);
    ray.xz = ray.xz*cos(a.y)+ray.zx*sin(a.y)*vec2(1,-1);

    vec3 pos = vec3(iTime*3.,15,sin(iTime/20.)*100.)+vec3(-20,0,-70);
/*	vec3 ray = normalize(vec3((fragCoord-iResolution.xy*.5)/iResolution.y,5.));

    vec2 a = vec2(.16+.02*sin(iTime*.5),.3+.05*sin(iTime*.2));
    ray.zy = ray.zy*cos(a.x)+ray.yz*sin(a.x)*vec2(1,-1);
    ray.xz = ray.xz*cos(a.y)+ray.zx*sin(a.y)*vec2(1,-1);

    vec3 pos = vec3(iTime*5.,12.+sin(iTime*.3),sin(iTime*.21)*5.);*/


    float t = 0.;
    float h = 1.;
    for ( int i=0; i < 50*DRAWDIST; i++ ) // this increases the quality of the horizon
    {
        h = SDF(pos+t*ray);
        t += h;
        if ( h < .001 ) break;
    }

   	pos += t*ray;

    if ( h < .1 )
    {
        vec2 d = vec2(-1,1)*.02;
        vec3 grad = SDF(pos+d.xxx)/d.xxx+
                    SDF(pos+d.xyy)/d.xyy+
                    SDF(pos+d.yxy)/d.yxy+
                    SDF(pos+d.yyx)/d.yyx;
        vec3 n = normalize(grad);

        float ao = //smoothstep(-1.,1.,max(pos.y,Map(pos.xz).x))
                   1.-.7*clamp( pow( max(.0,1.-mix( SDF(pos+n*1.5)/1.5, SDF(pos+n*.4)/.4, .2 )), 2. ), .0, 1. );
        vec3 ambient = vec3(.2,.3,.5) * ao;

        vec3 sunDir = normalize(vec3(-3,1,-2));
        vec3 sun = vec3(2,1.8,1.6)*max(.0,dot(n,sunDir));

        float ts = .013 *.01/(SDF(pos+sunDir*.01)); // correct by actual gradient!)
        float hs = 1.;
        for ( int i=0; i < 10*DRAWDIST; i++ ) // keep this number as low as I can get away with
        {
            hs = SDF(pos+ts*sunDir);
            ts += hs;
            if ( hs < .01 ) break;
        }
        // sun shadow
        if ( hs < .01 ) sun *= 0.;

        vec2 map = Map(pos.xz);

        // building colour
        vec3 paint = fract(map.y*161.8f*vec3(1.,7.,49.)); // pseudo random
        vec3 albedo = mix( pow(paint*vec3(1,1,.9),vec3(8.)), vec3(.2), .8 ); // tone it down

        // road colour
        vec3 road = mix( vec3(.25), vec3(.1), smoothstep(.23,.24,map.x) );
        road = mix( paint*.1+vec3(.1,.2,.0), road, smoothstep(-.3,.0,map.x) );
        //road = mix( road, vec3(.8), smoothstep(.015,.005,abs(map.x-.58)) ); // these are nice but they make the buildings look worse by comparison
        //road = mix( road, vec3(1,.9,0), smoothstep(.015,.005,abs(map.x-.27)) );
        albedo = mix( road, albedo, smoothstep(.0,.01,pos.y) );

        colour.rgb = albedo*(ambient+sun);
    }
    else
    {
        colour.rgb = vec3(.1);
    }

	// atmosphere
    colour.rgb = mix(vec3(.5,.75,1.),colour.rgb,exp2(-t*vec3(.1,.3,1.)/500.));

    return colour;
}


void mainImage( out vec4 colour, in vec2 fragCoord )
{
#if defined( WIDESCREEN )
    if ( abs(fragCoord.y-iResolution.y*.5) > .5*iResolution.x/2.4 )
    {
        colour = vec4(0);
        return;
    }
#endif

#if defined( FPS30 )
    if ( (iFrame&1) != 0 ) discard; // run at 30fps - use this if frame rate is bad
#endif

#if defined( ANTIALIASING ) // better quality, good for video capture
    // rotated grid AA - really breaks up the straight lines
    colour.rgb = (
		mainImage2(fragCoord+vec2(-3,-1)/8.) +
		mainImage2(fragCoord+vec2(-1, 3)/8.) +
		mainImage2(fragCoord+vec2( 1,-3)/8.) +
		mainImage2(fragCoord+vec2( 3, 1)/8.)
        )/4.;
#else
    colour.rgb = mainImage2(fragCoord);
#endif

#if defined(POSTPROCESS)
#if defined(WIDESCREEN)
    float sh = iResolution.x/2.4;
#else
    float sh = iResolution.y;
#endif

	// vignette
	colour.rgb *= smoothstep(1.6,.4,length((fragCoord-iResolution.xy*.5)/sh));

	// contrast
	colour.rgb *= 1.5;
	float grey = dot(colour.rgb,vec3(.2126,.7152,.0722));
	float bottom = .03;
	colour.rgb *= (sqrt(grey*grey+bottom*bottom)-bottom)/grey; // squash dark tones together to increase contrast
#endif

    colour.rgb = pow(colour.rgb,vec3(.4545));

    colour.a = 1.;
}

