float PrCylDf (vec3 p, float r, float h);
float PrEllipsDf (vec3 p, vec3 r);
float PrEllCylDf (vec3 p, vec2 r, float h);
vec2 PixToHex (vec2 p);
vec2 HexToPix (vec2 h);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
float Hashfv2 (vec2 p);
float Noisefv2 (vec2 p);
float Fbm3 (vec3 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 sunDir, dukConf, qHit, dispCloud;
vec2 gId;
float tCur, dstFarO, dstFarW, dstWat, szFac;
int idObj;
const float pi = 3.14159;
const float sqrt3 = 1.7320508;
const int idBdy = 1, idWng = 2, idHead = 3, idBk = 4, idMast = 5, idSail = 6;

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = 0.5 * p - vec2 (0., tCur);
  h = 0.3 * sin (q.y) + 0.25 * sin (dot (q, vec2 (0.1, 1.2))) +
     0.2 * sin (dot (q, vec2 (-0.2, 1.4)));
  q = p;
  wFreq = 0.5;
  wAmp = 0.03;
  tw = 0.1 * tCur * vec2 (1., -1.);
  for (int j = 0; j < 4; j ++) {
    q *= qRot;
    t4 = q.xyxy * wFreq + tw.xxyy;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    t = 1. - sqrt (v4.xz * v4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return h;
}

float WaveRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFarW;
  for (int j = 0; j < 50; j ++) {
    p = ro + s * rd;
    h = p.y - WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (6., h);
    if (s > dstFarW) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 6; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - WaveHt (p.xz));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 WaveNf (vec3 p, float d)
{
  vec2 e;
  e = vec2 (max (0.1, 5e-5 * d * d), 0.);
  return normalize (vec3 (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy), WaveHt (p.xz + e.yx)), e.x).xzy);
}

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 r, cs;
  float dMin, d, s;
  p.xz -= HexToPix (gId);
  p.y -= dukConf.x - 0.02 * szFac;
  p.yz = Rot2D (p.yz, -3. * dukConf.z);
  p.xy = Rot2D (p.xy, 10. * dukConf.y);
  dMin = dstFarO / szFac;
  p /= szFac;
  q = p;
  r = vec2 (0.04, 0.06 + 0.01 * clamp (q.z, -0.4, 0.4));
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - 0.1), length (vec2 (s, q.y)) - 0.1);
  DMINQ (idBdy);
  q = p;  q.x = abs (q.x);  q -= vec3 (0.1, 0.06, 0.12);
  cs = vec2 (cos (0.3), sin (0.3));
  q.yz = Rot2Cs (q.yz, cs);
  cs.y = - cs.y;
  q.xy = Rot2Cs (q.xy, cs);
  q.xz = Rot2Cs (q.xz, cs);
  q = q.yxz;
  r = vec2 (0.06, 0.1 + 0.016 * clamp (q.z, -0.4, 0.4));
  s = (length (q.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (q.y) - 0.014), length (vec2 (s, q.y)) - 0.014);
  DMINQ (idWng);
  q = p;  q.yz -= vec2 (0.15, -0.08);
  d = PrEllipsDf (q, vec3 (0.08, 0.07, 0.1));
  DMINQ (idHead);
  q = p;  q.yz -= vec2 (0.14, -0.19);
  d = PrEllCylDf (q, vec2 (0.03, 0.008), 0.02);
  DMIN (idBk);
  q = p;
  d = min (PrCylDf ((q - vec3 (0., 0.25, 0.06)).xzy, 0.007, 0.25),
     PrCylDf (vec3 (q.x, abs (q.y - 0.33) - 0.1, q.z - 0.055).yzx, 0.006, 0.155));
  DMIN (idMast);
  q = p;  q.y -= 0.33;  q.z -= 3. * q.y * q.y + 0.025;
  d = max (length (max (abs (q) - vec3 (0.15, 0.1, 0.003), 0.)),
     0.2 - length (q.xy + vec2 (0., 0.25)));
  DMINQ (idSail);
  return 0.9 * dMin * szFac;
}

void EvalDukConf ()
{
  vec4 ht;
  vec2 e, g;
  float s;
  s = length (vec3 (gId.xy, gId.x + gId.y));
  if (s == 0.) szFac = 3.;
  else if (s <= 2.) szFac = 2.2;
  else szFac = 1.5;
  e = vec2 (0.1 * szFac, 0.);
  g = HexToPix (gId);
  ht = vec4 (WaveHt (g - e), WaveHt (g + e), WaveHt (g - e.yx), WaveHt (g + e.yx));
  dukConf = vec3 (0.25 * dot (ht, vec4 (1.)) + 0.1 * (Hashfv2 (17. * gId) - 0.5),
     vec2 (ht.xz - ht.yw));
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, gIdP;
  float dHit, d, s, eps;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = 1. / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = (sqrt3/2.) - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2]));
  pM = HexToPix (PixToHex (ro.xz));
  gIdP = vec2 (-99.);
  eps = 0.0005;
  dHit = eps;
  for (int j = 0; j < 150; j ++) {
    hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
    s = min (hv.x, min (hv.y, hv.z));
    p = ro + dHit * rd;
    gId = PixToHex (p.xz);
    if (length (vec3 (gId.xy, gId.x + gId.y)) <= 5.) {
      if (gId.x != gIdP.x || gId.y != gIdP.y) {
        EvalDukConf ();
        gIdP = gId;
      }
      d = ObjDf (p);
    } else d = dstFarO;
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + 0.01;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
    }
    if (d < eps || dHit > dstFarO) break;
  }
  if (d >= eps) dHit = dstFarO;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec4 ObjCol ()
{
  vec4 col4;
  vec4 cBdy;
  float s;
  s = length (vec3 (gId.xy, gId.x + gId.y));
  if (s == 0.) cBdy = vec4 (1., 1., 0.1, 0.3);
  else if (s <= 2.) cBdy = vec4 (0.9, 0.9, 1., 0.3);
  else cBdy = vec4 (0.8, 0.4, 0.2, 0.3);
  if (idObj == idBdy) {
    col4 = cBdy * (1. - smoothstep (0.02, 0.06, qHit.y) *
       smoothstep (0., 0.14, qHit.z) * 0.1 * SmoothBump (0.3, 0.5, 0.05,
       mod (50. * qHit.x, 1.)));
  } else if (idObj == idWng) {
    col4 = cBdy * (1. - step (0.004, qHit.y) *
       smoothstep (0., 0.04, qHit.z) * 0.2 * SmoothBump (0.3, 0.5, 0.05,
       mod (100. * qHit.x, 1.)));
  } else if (idObj == idHead) {
    s = length (qHit.yz - vec2 (0.02, -0.05));
    if (s > 0.02) col4 = cBdy;
    else col4 = (abs (s - 0.01) < 0.003) ? vec4 (1., 1., 1., 0.3) : vec4 (0.3, 0.3, 1., 0.1);
  } else if (idObj == idBk) {
    col4 = vec4 (1., 0.5, 0., 0.2);
  } else if (idObj == idMast) {
    col4 = vec4 (0.6, 0.3, 0., 0.);
  } else if (idObj == idSail) {
    col4 = vec4 (1.1, 1.1, 1.1, 0.) * (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.03, mod (16. * qHit.x, 1.)));
  }
  return col4;
}

vec3 CloudCol (vec3 ro, vec3 rd)
{
  vec4 cSum, c;
  vec3 s1, s2;
  float sd, t;
  s1 = 0.003 * (ro + dispCloud + rd * (300. - ro.y) / rd.y);
  s2 = 0.003 * (rd * 10. / rd.y);
  cSum = vec4 (0.);
  for (float ns = 0.; ns < 100.; ns ++) {
    c.a = 0.8 * smoothstep (0.5, 1., Fbm3 (s1 + s2 * ns));
    c = vec4 (1., 0.95, 0.9, c.a) - vec4 (0.7, 0.65, 0.7, cSum.a) * c.a;
    cSum += vec4 (c.rgb * c.a, c.a);
    if (cSum.a > 0.99) break;
  }
  cSum.rgb /= cSum.a + 0.001;
  sd = max (dot (rd, sunDir), 0.);
  cSum.rgb += vec3 (0.5) * mix (pow (sd, 4.), - pow (sd, 16.), smoothstep (0.8, 1., cSum.a));
  t = pow (1. - 0.7 * rd.y, 16.);
  return mix (vec3 (0.05, 0.2, 0.5) + t * vec3 (0.45, 0.4, 0.3) +
     0.3 * vec3 (1., 0.9, 0.8) * (pow (sd, 2.) + 4. * pow (sd, 256.)), cSum.rgb, cSum.a * (1. - t));
}

float CloudDens (vec3 ro, vec3 rd)
{
  vec3 s1, s2;
  float attSum;
  s1 = 0.003 * (ro + dispCloud + rd * (300. - ro.y) / rd.y);
  s2 = 0.003 * (rd * 10. / rd.y);
  attSum = 0.;
  for (float ns = 0.; ns < 100.; ns += 10.) {
    attSum += 0.8 * (1. - attSum) * smoothstep (0.5, 1., Fbm3 (s1 + s2 * ns));
    if (attSum > 0.99) break;
  }
  return clamp (1. - attSum, 0., 1.);
}

float GRayDens (vec3 ro, vec3 rd)
{
  vec3 p, s1, s2;
  float d, sd, gDens;
  sd = 0.08 * max (dot (rd, sunDir), 0.);
  gDens = 0.;
  if (sd > 0.01) {
    s1 = ro + 200. * rd;
    s2 = 40. * rd;
    for (float ns = 0.; ns < 32.; ns ++) {
      p = s1 + s2 * ns;
      gDens += sd * (1. - gDens) * smoothstep (0.8, 1., CloudDens (p, sunDir));
      if (p.y > 400. || gDens > 0.95) break;
    }
  }
  return gDens;
}

vec3 WaveCol (vec3 ro, vec3 rd)
{
  vec3 col;
  float sd;
  sd = max (dot (rd, sunDir), 0.);
  col = vec3 (0.15, 0.2, 0.2) * (0.7 * ro.y + 1.) +
     CloudDens (ro + 300. * rd, rd) * (mix (vec3 (0., 0.1, 0.2), vec3 (0.2, 0.2, 0.3), rd.y) +
     (1. - rd.y) * vec3 (0.6, 0.6, 0.5) * (pow (sd, 4.) + 2. * pow (sd, 256.)));
  col *= 0.7 + 0.3 * pow (max (dot (rd, sunDir), 0.), 2.);
  col = mix (col, vec3 (0.6, 0.8, 0.9) + vec3 (0.2, 0.2, 0.1) * pow (sd, 4.),
     1. - exp (- pow (0.005 * dstWat, 1.5)));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, colW, roo, rdo, vn, ltDir;
  float dstObj, reflFac;
  ltDir = normalize (vec3 (1., 1., -1.));
  dispCloud = vec3 (0., 0., -40. * tCur);
  roo = ro;
  rdo = rd;
  reflFac = 1.;
  col = vec3 (0.);
  if (rd.y < 0.) dstWat = WaveRay (ro, rd);
  else dstWat = dstFarW;
  dstObj = ObjRay (ro, rd);
  if (dstWat < min (dstObj, dstFarW)) {
    ro += dstWat * rd;
    rd = reflect (rd, VaryNf (ro, WaveNf (ro, dstWat), 0.1));
    dstObj = ObjRay (ro, rd);
    reflFac = 0.5;
  }
  if (dstObj < dstFarO) {
    ro += dstObj * rd;
    EvalDukConf ();
    objCol = ObjCol ();
    vn = ObjNf (ro);
    col = objCol.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       objCol.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
  } else if (dstWat < dstFarW) {
    reflFac = 0.;
  } else {
    col = CloudCol (roo, rdo) + 0.9 * vec3 (1., 0.8, 0.6) * GRayDens (roo, rdo);
  }
  if (reflFac < 1.) {
    roo += dstWat * rdo;
    col = mix (WaveCol (roo, reflect (rdo, VaryNf (32. * roo, WaveNf (roo, dstWat), 0.05))),
       col, reflFac);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, u;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, zmFac, f;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  ro = vec3 (3., 5., -20.);
  zmFac = 2.4;
  if (mPtr.z > 0.) {
    ro.x += 10. * mPtr.x;
    zmFac += 1.5 * (mPtr.y + 0.5);
  }
  vd = normalize (vec3 (0., 2., 0.) - ro);
  u = - vd.y * vd;
  f = 1. / sqrt (1. - vd.y * vd.y);
  vuMat = mat3 (f * vec3 (vd.z, 0., - vd.x), f * vec3 (u.x, 1. + u.y, u.z), vd);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  sunDir = normalize (vec3 (0.5, 0.25, 1.));
  dstFarO = 30.;
  dstFarW = 300.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

