float PrBoxDf (vec3 p, vec3 b);
float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float s);
float PrCylDf (vec3 p, float r, float h);
float PrCylAnDf (vec3 p, float r, float w, float h);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float RgbToVal (vec3 c);
float Hashfv3 (vec3 p);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 bSize, qHit, cId;
vec2 aCs[3], crCs[3], crMid[3];
float tCur, dstFar, crRad, crLen, aRot, stepCnt;
int idObj;
bool colImg, marchImg;
const float pi = 3.14159;
const int idWhl = 1, idSpk = 2, idCrnk = 3, idAx = 4, idPis = 5, idCrod = 6, idCyl = 7,
   idCylEnt = 8, idValv = 9, idPipes = 10, idSup = 11, idBase = 12, idFlr = 13, idCol = 14,
   idRail = 15, idLt = 16;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float HallDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p;
  d = max (0.5 * bSize.y - 0.4 - abs (q.y),
    - PrBox2Df (vec2 (q.x, abs (q.z) - 0.5 * bSize.z), vec2 (16., 8.)));
  DMINQ (idFlr);
  d = length (abs (q.xz) - vec2 (16.6, 11.9)) - 0.6;
  DMIN (idCol);
  q = p;  q.z = abs (q.z) - 11.8;  q.y -= -4.6;
  d = min (min (PrCylDf (q.zyx, 0.15, 16.1),
     PrCylDf (vec3 (abs (abs (q.x) - 7.) - 3.5, q.y + 2.5, q.z).xzy, 0.1, 2.5)),
     PrCylDf (vec3 (q.x, abs (q.y + 2.5) - 1., q.z).zyx, 0.1, 16.1));
  q = p;  q.xz = abs (q.xz) - vec2 (16.4, 19.4);  q.y -= -4.6;
  d = min (d, min (min (PrCylDf (q, 0.15, 7.7),
     PrCylDf (vec3 (q.x, q.y + 2.5, abs (q.z) - 2.).xzy, 0.1, 2.5)),
     PrCylDf (vec3 (q.x, abs (q.y + 2.5) - 1., q.z), 0.1, 7.7)));
  DMIN (idRail);
  q = p;  q.xz = abs (q.xz);  q -= vec3 (6., 9.6, 6.);
  d = PrCylDf (q.xzy, 2., 0.2);
  DMINQ (idLt);
  return dMin;
}

float CrnkDf (vec3 p, float dMin)
{
  vec3 q;
  float d, dz;
  for (int k = 0; k < 3; k ++) {
    dz = float (k - 1) * 4.;
    q = p;  q.x -= 8.;
    q.xy = Rot2Cs (q.xy, aCs[k]);
    q.z += dz;
    d = min (PrBoxDf (vec3 (q.x + 0.5 * crRad, q.y, abs (q.z) - 0.5), vec3 (0.5 * crRad, 0.2, 0.1)),
       PrCylDf (vec3 (abs (q.x + 0.5 * crRad) - 0.5 * crRad, q.y, abs (q.z) - 0.5), 0.6, 0.1));
    DMIN (idCrnk);
    d = PrCylDf (vec3 (q.x + crRad, q.yz), 0.3, 0.65);
    DMIN (idAx);
    q = p;  q.xz -= vec2 (8., - dz);
    q.xy = Rot2Cs (q.xy + crMid[k], crCs[k]);
    d = min (PrCylDf (vec3 (abs (q.y) - 0.12, q.zx), 0.15, crLen - 0.5),
       PrCylDf (vec3 (abs (q.x) - crLen, q.yz), 0.6, 0.15));
    DMIN (idCrod);
    q = p;  q.xz -= vec2 (3.5 - (crMid[k].x + crLen * crCs[k].x), - dz);
    d = PrCylDf (q.yzx, 0.25, 3.7);
    DMIN (idPis);
    d = PrCylDf ((q - vec3 (0.7, 1.7, 0.)).yzx, 0.07, 3.);
    DMIN (idPis);
    q.x -= 4.5;
    d = PrCylDf (q, 0.3, 0.5);
    DMIN (idAx);
    d = min (min (PrCylDf ((q + vec3 (0.8, 0., 0.)).yzx, 0.6, 0.13),
       PrCylDf ((q + vec3 (0.8, -0.8, 0.)).xzy, 0.08, 0.95)),
       PrCylDf (vec3 (q.xy, abs (q.z) - 0.35), 0.7, 0.1));
    DMIN (idCrnk);
  }
  return dMin;
}

float EngDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  p.y += 4.6;
  d = min (PrBoxDf (p + vec3 (0., 4., 0.), vec3 (13., 1., 6.)),
     PrBoxDf (vec3 (abs (p.x + 9.) - 2., p.y + 2.5, p.z), vec3 (1., 1.4, 5.)));
  DMIN (idBase);
  q = p;  q.x -= 8.;
  q.xy = Rot2Cs (q.xy, aCs[0]);
  q.z = abs (q.z) - 7.;
  d = min (PrCylAnDf (q, 4., 0.2, 0.6), PrCylDf (q, 0.6, 0.6));
  DMINQ (idWhl);
  q.xy = Rot2D (q.xy, 2. * pi * (floor (6. * atan (q.y, - q.x) / (2. * pi) + 0.5)) / 6.);
  d = PrCylDf (vec3 (q.x + 2.2, q.y, abs (q.z) - 0.35).zyx, 0.2, 1.7);
  DMIN (idSpk);
  d = max (PrCylDf (p - vec3 (8., 0., 0.), 0.3, 7.8), min (0.35 - abs (mod (p.z + 2., 4.) - 2.),
     6. - abs (p.z)));
  DMIN (idAx);
  dMin = CrnkDf (p, dMin);
  q = p;  q.x -= -8.9;  q.z = mod (q.z + 2., 4.) - 2.;
  d = max (PrCylDf ((q + vec3 (-3.5, 0., 0.)).yzx, 0.7, 0.2), abs (p.z) - 6.);
  DMINQ (idCylEnt);
  d = max (PrCylDf (q.yzx, 1.5, 3.5), abs (p.z) - 6.);
  DMINQ (idCyl);
  q = p + vec3 (8.9, -1.7, 0.);  q.z = mod (q.z + 2., 4.) - 2.;
  d = max (PrCylDf (q.yzx, 0.5, 2.5), abs (p.z) - 6.);
  DMINQ (idValv);
  q = vec3 (abs (p.x + 8.9) - 1.5, p.y - 2.5, p.z);
  d = min (min (max (PrCylDf ((vec3 (q.x, q.y, mod (q.z + 2., 4.) - 2.)).xzy, 0.35, 1.25),
     abs (p.z) - 6.), PrCylDf (vec3 (q.x, q.y - 1.3, q.z), 0.35, 4.)),
     PrSphDf (vec3 (q.x, q.y - 1.3, abs (q.z) - 4.), 0.35));
  q = p + vec3 (8.9, -6.3, 0.);
  d = min (d, min (PrCylDf ((q + vec3 (-1.5, 0., 2.)).xzy, 0.35, 2.5),
     PrCylDf ((q + vec3 (1.5, 0., -2.)).xzy, 0.35, 2.5)));
  d = min (d, length (vec2 (abs (q.x) - 1.5, q.y - 2.5)) - 0.4);
  DMIN (idPipes);
  q = p;  q.x -= 8.;  q.z = abs (abs (p.z) - 4.) - 1.7;
  d = min (PrBoxDf (q + vec3 (0., 1.6, 0.), vec3 (0.5, 1.5, 0.2)), PrCylDf (q, 0.5, 0.3));
  DMIN (idSup);
  q = p + vec3 (-8., -1.8, -2.3);
  d = PrCylDf ((q + vec3 (0., 0.6, 0.)).xzy, 0.12, 0.8);
  q.xz = Rot2D (q.xz, 4. * aRot);
  q.xz = Rot2D (q.xz, 2. * pi * (floor (4. * atan (q.z, - q.x) / (2. * pi) + 0.5)) / 4.);
  q.xy = Rot2D (q.xy, -0.25 * pi);
  d = min (d, PrCylDf ((q + vec3 (0.4, -0.1, 0.)).yzx, 0.05, 0.4));
  DMIN (idAx);
  d = PrSphDf (q + vec3 (0.7, -0.1, 0.), 0.15);
  DMIN (idPis);
  return dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  dMin = HallDf (p, dMin);
  dMin = EngDf (p, dMin);
  return dMin;
}

void SetEngConf ()
{
  aRot = -(0.1 + 0.3 * Hashfv3 (cId + 11.)) * 2. * pi * tCur;
  aCs[0] = vec2 (cos (aRot), sin (aRot));
  aCs[1] = vec2 (cos (aRot + pi * 2./3.), sin (aRot + pi * 2./3.));
  aCs[2] = vec2 (cos (aRot + pi * 4./3.), sin (aRot + pi * 4./3.));
  for (int k = 0; k < 3; k ++) {
    crMid[k].y = -0.5 * crRad * aCs[k].y;
    crCs[k] = vec2 (cos (asin (crMid[k].y / crLen)), crMid[k].y / crLen);
    crMid[k].x = crLen * crCs[k].x + crRad * aCs[k].x;
  }
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi, s, cIdP;
  float dHit, d, eps;
  eps = 0.0005;
  dHit = eps;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  ro /= bSize;
  rd /= bSize;
  rdi = 1. / rd;
  cIdP = vec3 (-99.);
  stepCnt = 0.;
  for (int j = 0; j < 180; j ++) {
    p = ro + dHit * rd;
    cId = floor (p);
    if (cId.x != cIdP.x || cId.y != cIdP.y || cId.z != cIdP.z) {
      SetEngConf ();
      cIdP = cId;
    }
    s = (cId + step (0., rd) - p) * rdi;
    d = min (ObjDf (bSize * (p - cId - 0.5)), abs (min (min (s.x, s.z), s.y)) + eps);
    dHit += d;
    ++ stepCnt;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  p -= bSize * (cId + 0.5);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p, cIdP;
  float sh, d, h;
  sh = 1.;
  cIdP = vec3 (-99.);
  d = 0.1;
  for (int j = 0; j < 16; j ++) {
    p = ro + d * rd;
    cId = floor (p / bSize);
    if (cId.x != cIdP.x || cId.y != cIdP.y || cId.z != cIdP.z) {
      SetEngConf ();
      cIdP = cId;
    }
    h = ObjDf (p - bSize * (cId + 0.5));
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += 0.3;
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 ShGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 ShStagGrid (vec2 p, vec2 g)
{
  vec2 q, sq, ss;
  q = p * g;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn, lVec, rg;
  vec2 vf;
  float dstObj, a, s, fFade;
  bool fxz;
  crRad = 2.;
  crLen = 5.;
  bgCol = mix (0.6 * vec3 (0.4, 0.4, 0.5), vec3 (0.21), smoothstep (-0.01, 0.01, rd.y));
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    cId = floor (ro / bSize);
    SetEngConf ();
    vn = ObjNf (ro);
    vf = vec2 (0.);
    fFade = exp (32. * min (0., 0.7 - dstObj / dstFar));
    if (idObj == idFlr) {
      if (vn.y > 0.99) {
        col4 = vec4 (0.4, 0.4, 0.5, 0.1);
        rg = ShGrid (ro.xz);
        col4.rgb *= mix (1., rg.y, fFade) * (1. - 0.3 * Fbm2 (2. * ro.xz));
        if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z * fFade);
        else vn.yx = Rot2D (vn.yx, rg.x * fFade);
        col4 *= (1. - 0.5 * smoothstep (12., 16., length (qHit.xz)));
        vf = vec2 (32., 1.);
      } else if (vn.y < -0.99) {
        col4 = vec4 (vec3 (0.3) * (1. - 0.3 * smoothstep (3., 7.,
           length (abs (qHit.xz) - 6.))), -1.);
      } else {
        col4 = vec4 (0.3, 0.3, 0.3, 0.1);
        vf = vec2 (32., 1.);
      }
    } else if (idObj == idBase) {
      col4 = vec4 (0.6, 0.3, 0.2, 0.1);
      if (abs (vn.y) < 0.01) {
        rg = ro;
        rg.y += 0.5;
        fxz = (abs (vn.x) > 0.99);
        rg = ShStagGrid ((fxz ? rg.zy : rg.xy), vec2 (1., 2.));
        col4.r *= rg.y;
        col4.rgb *= 1. - 0.3 * Fbm2 (2. * (fxz ? ro.zy : ro.xy));
        rg.xz *= sign (fxz ? vn.x : vn.z);
        if (fxz) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      } else {
        rg = ShGrid (ro.xz);
        col4.r *= rg.y;
        col4.rgb *= 1. - 0.3 * Fbm2 (2. * ro.xz);
        if (vn.y > 0.99) {
          if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z);
          else vn.yx = Rot2D (vn.yx, rg.x);
        }
      }
      vf = vec2 (32., 1.);
    } else if (idObj == idCol) {
      col4 = vec4 (0.8, 0.8, 0.75, 0.05);
      vf = vec2 (32., 1.);
    } else if (idObj == idCyl) {
      col4 = vec4 (0.8, 0.8, 0.9, 0.3);
      a = atan (qHit.z, - qHit.y) / (2. * pi);
      if (abs (vn.x) > 0.99) {
        col4.rgb *= 1. - 0.2 * Fbm2 (4. * qHit.yz);
        col4.rgb *= (1. - 0.5 * SmoothBump (0.2, 0.4, 0.01, mod (16. * a + 0.5, 1.)) *
           SmoothBump (0.05, 0.13, 0.01, 1. - length (qHit.yz) / 1.5));
      } else {
        col4.rgb *= 1. - 0.2 * Fbm2 (4. * vec2 (8. * a, qHit.x));
        col4.rgb *= (1. - 0.5 * SmoothBump (0.03, 0.06, 0.01, 1. - abs (qHit.x) / 3.5));
        a = mod (32. * a, 1.);
        if (abs (qHit.x) < 3.3) vn.yz = Rot2D (vn.yz, 0.4 * SmoothBump (0.25, 0.75, 0.2, a) *
           sign (a - 0.5));
      }
    } else if (idObj == idWhl) {
      if (abs (vn.z) < 0.01) {
        s = length (qHit.xy);
        qHit.xy = vec2 (8. * atan (qHit.x, - qHit.y) / pi, qHit.z);
        if (s > 4.1) {
          s = mod (4. * qHit.z, 1.);
          vn.z = -0.2 * SmoothBump (0.25, 0.75, 0.15, s) * sign (s - 0.5) * sign (ro.z);
          vn = normalize (vn);
        }
      }
      col4 = vec4 (0.5, 0.5, 0.55, 0.05) * (1. + 0.2 * Noisefv2 (128. * qHit.xy));
    } else if (idObj == idSpk) {
      col4 = 1.1 * vec4 (0.5, 0.5, 0.55, 0.2);
    } else if (idObj == idCrnk) {
      col4 = vec4 (0.5, 0.5, 0.6, 0.2);
    } else if (idObj == idAx) {
      col4 = vec4 (0.6, 0.4, 0.1, 0.3);
    } else if (idObj == idPis) {
      col4 = vec4 (0.5, 0.5, 0.2, 0.3);
    } else if (idObj == idCrod) {
      col4 = vec4 (0.6, 0.6, 0.5, 0.3);
    } else if (idObj == idCylEnt) {
      col4 = vec4 (0.7, 0.7, 0.8, 0.2) * (1. - 0.5 * step (length (qHit.yz), 0.33));
    } else if (idObj == idValv) {
      col4 = vec4 (0.7, 0.7, 0.8, 0.5) * (1. - 0.5 * step (0., vn.x) *
         step (length (qHit.yz), 0.13));
      vf = vec2 (32., 0.3);
    } else if (idObj == idPipes) {
      col4 = vec4 (0.6, 0.4, 0.1, 0.1);
      vf = vec2 (32., 0.3);
    } else if (idObj == idSup) {
      col4 = vec4 (0.2, 0.4, 0.1, 0.05);
      vf = vec2 (32., 1.);
    } else if (idObj == idRail) {
      col4 = vec4 (0.2, 0.4, 0.2, 0.1);
      vf = vec2 (32., 0.2);
    } else if (idObj == idLt) {
      qHit.xz = smoothstep (0.05, 0.1, abs (qHit.xz));
      col4 = vec4 (vec3 (1., 1., 0.8) * 0.5 * (1. - vn.y) * (0.7 + 0.3 * qHit.x * qHit.z), -1.);
    }
    if (col4.a >= 0.) {
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      lVec = normalize (vec3 (1., 1.3, 1.));
      col = 0.2 * col4.rgb;
      for (float sx = -1.; sx <= 1.; sx += 2.) {
        for (float sz = -1.; sz <= 1.; sz += 2.) {
          col += 0.5 * col4.rgb * max (dot (vn, lVec * vec3 (sx, 1., sz)), 0.) +
             col4.a * pow (max (dot (normalize (lVec * vec3 (sx, 1., sz) - rd), vn), 0.), 64.);
        }
      }
      col *= 0.7 + 0.3 * ObjSShadow (ro, vec3 (0., 1., 0.));
    } else col = col4.rgb;
    col = clamp (mix (bgCol, col, fFade), 0., 1.);
  } else col = bgCol;
  if (! colImg) col = pow (vec3 (1., 0.59, 0.18) * RgbToVal (col), vec3 (0.9));
  if (marchImg) col = HsvToRgb (vec3 (0.7 * (1. - stepCnt / 180.), 1., 1.));
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvs, ori, ca, sa;
  float el, az, asp;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  bSize = vec3 (40., 20., 40.);
  az = 0.;
  el = -0.05 * pi;
  colImg = false;
  marchImg = false;
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y < -0.4) colImg = true;
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y > 0.4) marchImg = true;
  if (mPtr.z > 0. && ! colImg && ! marchImg) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  } else {
    az = 0.5 * pi * (2. * mod (floor (0.07 * tCur), 2.) - 1.) *
       SmoothBump (0.2, 0.8, 0.1, mod (0.07 * tCur, 1.));
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -1.) + vec3 (0., 8., 3. * tCur);
  asp = canvas.x / canvas.y;
  uv.xy /= 1.9;
  rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uv.x / asp)) * asp, uv.y, 1.));
  dstFar = 180.;
  col = ShowScene (ro, rd);
  if (! colImg && ! marchImg) {
    uvs *= uvs;
    col = mix (0.2 * vec3 (1., 0.59, 0.18), col, 1. - smoothstep (0.9, 1.,
       length (uvs * uvs * uvs)));
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

float RgbToVal (vec3 c)
{
  return max (c.r, max (c.g, c.b));
}

const float cHashM = 43758.54;

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// emulated quadruple precision GLSL library
// created by Henry Thasler (thasler.org/blog)
// based on the QD library (http://crd-legacy.lbl.gov/~dhbailey/mpdist/)

// inline double quick_two_sum(double a, double b, double &err)
vec2 quick_2sum(float a, float b)
{
    float s = a + b;                       // double s = a + b;
    return vec2(s, b-(s-a));       // err = b - (s - a);
}

/* Computes fl(a+b) and err(a+b).  */
// inline double two_sum(double a, double b, double &err)
vec2 two_sum(float a, float b)
{
    float v,s,e;

    s = a+b;                               // double s = a + b;
    v = s-a;                               // double bb = s - a;
    e = (a-(s-v))+(b-v);   // err = (a - (s - bb)) + (b - bb);

    return vec2(s,e);
}

vec2 split(float a)
{
    float t, hi;
    t = 8193. * a;
    hi = t - (t-a);
    return vec2(hi, a-hi);
}

vec3 three_sum(float a, float b, float c)
{
    vec2 tmp;
    vec3 res;// = vec3(0.);
    float t1, t2, t3;
    tmp = two_sum(a, b); // t1 = qd::two_sum(a, b, t2);
    t1 = tmp.x;
    t2 = tmp.y;

    tmp = two_sum(c, t1); // a  = qd::two_sum(c, t1, t3);
    res.x = tmp.x;
    t3 = tmp.y;

    tmp = two_sum(t2, t3); // b  = qd::two_sum(t2, t3, c);
    res.y = tmp.x;
    res.z = tmp.y;

    return res;
}

//inline void three_sum2(double &a, double &b, double &c)
vec3 three_sum2(float a, float b, float c)
{
    vec2 tmp;
    vec3 res;// = vec3(0.);
    float t1, t2, t3;       // double t1, t2, t3;
    tmp = two_sum(a, b); // t1 = qd::two_sum(a, b, t2);
    t1 = tmp.x;
    t2 = tmp.y;

    tmp = two_sum(c, t1); // a  = qd::two_sum(c, t1, t3);
    res.x = tmp.x;
    t3 = tmp.y;

    res.y = t2 + t3;      // b = t2 + t3;
    return res;
}

vec2 two_prod(float a, float b)
{
    float p, e;
    vec2 va, vb;

    p=a*b;
    va = split(a);
    vb = split(b);

    e = ((va.x*vb.x-p) + va.x*vb.y + va.y*vb.x) + va.y*vb.y;
    return vec2(p, e);
}

vec4 renorm(float c0, float c1, float c2, float c3, float c4)
{
    float s0, s1, s2 = 0.0, s3 = 0.0;
    vec2 tmp;

    // if (QD_ISINF(c0)) return;

    tmp = quick_2sum(c3,c4); // s0 = qd::quick_two_sum(c3, c4, c4);
    s0 = tmp.x;
    c4 = tmp.y;

    tmp = quick_2sum(c2,s0); // s0 = qd::quick_two_sum(c2, s0, c3);
    s0 = tmp.x;
    c3 = tmp.y;

    tmp = quick_2sum(c1,s0); // s0 = qd::quick_two_sum(c1, s0, c2);
    s0 = tmp.x;
    c2 = tmp.y;

    tmp = quick_2sum(c0,s0); // c0 = qd::quick_two_sum(c0, s0, c1);
    c0 = tmp.x;
    c1 = tmp.y;

    s0 = c0;
    s1 = c1;

    tmp = quick_2sum(c0,c1); // s0 = qd::quick_two_sum(c0, c1, s1);
    s0 = tmp.x;
    s1 = tmp.y;

    if (s1 != 0.0) {
        tmp = quick_2sum(s1,c2); // s1 = qd::quick_two_sum(s1, c2, s2);
        s1 = tmp.x;
        s2 = tmp.y;

        if (s2 != 0.0) {
            tmp = quick_2sum(s2,c3); // s2 = qd::quick_two_sum(s2, c3, s3);
            s2 = tmp.x;
            s3 = tmp.y;
            if (s3 != 0.0)
                s3 += c4;
            else
                s2 += c4;
        } else {
            tmp = quick_2sum(s1,c3); // s1 = qd::quick_two_sum(s1, c3, s2);
            s1 = tmp.x;
            s2 = tmp.y;
            if (s2 != 0.0){
                tmp = quick_2sum(s2,c4); // s2 = qd::quick_two_sum(s2, c4, s3);
                s2 = tmp.x;
                s3 = tmp.y;}
            else{
                tmp = quick_2sum(s1,c4); // s1 = qd::quick_two_sum(s1, c4, s2);
                s1 = tmp.x;
                s2 = tmp.y;}
        }
    } else {
        tmp = quick_2sum(s0,c2); // s0 = qd::quick_two_sum(s0, c2, s1);
        s0 = tmp.x;
        s1 = tmp.y;
        if (s1 != 0.0) {
            tmp = quick_2sum(s1,c3); // s1 = qd::quick_two_sum(s1, c3, s2);
            s1 = tmp.x;
            s2 = tmp.y;
            if (s2 != 0.0){
                tmp = quick_2sum(s2,c4); // s2 = qd::quick_two_sum(s2, c4, s3);
                s2 = tmp.x;
                s3 = tmp.y;}
            else{
                tmp = quick_2sum(s1,c4); // s1 = qd::quick_two_sum(s1, c4, s2);
                s1 = tmp.x;
                s2 = tmp.y;}
        } else {
            tmp = quick_2sum(s0,c3); // s0 = qd::quick_two_sum(s0, c3, s1);
            s0 = tmp.x;
            s1 = tmp.y;
            if (s1 != 0.0){
                tmp = quick_2sum(s1,c4); // s1 = qd::quick_two_sum(s1, c4, s2);
                s1 = tmp.x;
                s2 = tmp.y;}
            else{
                tmp = quick_2sum(s0,c4); // s0 = qd::quick_two_sum(s0, c4, s1);
                s0 = tmp.x;
                s1 = tmp.y;}
        }
    }

    return vec4(s0, s1, s2, s3);

}

vec4 renorm4(float c0, float c1, float c2, float c3)
{
    float s0, s1, s2 = 0.0, s3 = 0.0;
    vec2 tmp;
    // if (QD_ISINF(c0)) return;

    tmp = quick_2sum(c2,c3); // s0 = qd::quick_two_sum(c2, c3, c3);
    s0 = tmp.x;
    c3 = tmp.y;

    tmp = quick_2sum(c1,s0); // s0 = qd::quick_two_sum(c1, s0, c2);
    s0 = tmp.x;
    c2 = tmp.y;

    tmp = quick_2sum(c0,s0); // c0 = qd::quick_two_sum(c0, s0, c1);
    c0 = tmp.x;
    c1 = tmp.y;

    s0 = c0;
    s1 = c1;
    if (s1 != 0.0) {
        tmp = quick_2sum(s1,c2); // s1 = qd::quick_two_sum(s1, c2, s2);
        s1 = tmp.x;
        s2 = tmp.y;

        if (s2 != 0.0){
            tmp = quick_2sum(s2,c3); // s2 = qd::quick_two_sum(s2, c3, s3);
            s2 = tmp.x;
            s3 = tmp.y;}
        else{
            tmp = quick_2sum(s1,c3); // s1 = qd::quick_two_sum(s1, c3, s2);
            s1 = tmp.x;
            s2 = tmp.y;}
    } else {
        tmp = quick_2sum(s0,c2); // s0 = qd::quick_two_sum(s0, c2, s1);
        s0 = tmp.x;
        s1 = tmp.y;
        if (s1 != 0.0){
            tmp = quick_2sum(s1,c3); // s1 = qd::quick_two_sum(s1, c3, s2);
            s1 = tmp.x;
            s2 = tmp.y;}
        else{
            tmp = quick_2sum(s0,c3); // s0 = qd::quick_two_sum(s0, c3, s1);
            s0 = tmp.x;
            s1 = tmp.y;}
    }

    return vec4(s0, s1, s2, s3);
}

vec3 quick_three_accum(float a, float b, float c)
{
    vec2 tmp;
    float s;
    bool za, zb;

    tmp = two_sum(b, c); // s = qd::two_sum(b, c, b);
    s = tmp.x;
    b = tmp.y;

    tmp = two_sum(a, s); // s = qd::two_sum(a, s, a);
    s = tmp.x;
    a = tmp.y;

    za = (a != 0.0);
    zb = (b != 0.0);

    if (za && zb)
        return vec3(a,b,s);

    if (!zb) {
        b = a;
        a = s;
    } else {
        a = s;
    }

    return vec3(a,b,0.);
}

// inline qd_real qd_real::ieee_add(const qd_real &a, const qd_real &b)
vec4 qs_ieee_add(vec4 _a, vec4 _b)
{
    vec2 tmp=vec2(0.);
    vec3 tmp3=vec3(0.);
    int i, j, k;
    float s, t;
    float u, v;   // double-length accumulator
    float x[4] = float[4](0.0, 0.0, 0.0, 0.0);
    float a[4], b[4];

    a[0] = _a.x;
    a[1] = _a.y;
    a[2] = _a.z;
    a[3] = _a.w;

    b[0] = _b.x;
    b[1] = _b.y;
    b[2] = _b.z;
    b[3] = _b.w;

    i = j = k = 0;
    if (abs(a[i]) > abs(b[j]))
        u = a[i++];
    else
        u = b[j++];
    if (abs(a[i]) > abs(b[j]))
        v = a[i++];
    else
        v = b[j++];

    tmp = quick_2sum(u,v); // u = qd::quick_two_sum(u, v, v);
    u = tmp.x;
    v = tmp.y;

    while (k < 4) {
        if (i >= 4 && j >= 4) {
            x[k] = u;
            if (k < 3)
                x[++k] = v;
            break;
        }

        if (i >= 4)
            t = b[j++];
        else if (j >= 4)
            t = a[i++];
            else if (abs(a[i]) > abs(b[j])) {
                t = a[i++];
            } else
                t = b[j++];

        tmp3 = quick_three_accum(u,v,t)  ; // s = qd::quick_three_accum(u, v, t);
        u = tmp3.x;
        v = tmp3.y;
        s = tmp3.z;

        if (s != 0.0) {
            x[k++] = s;
        }
    }

    // add the rest.
    for (k = i; k < 4; k++)
        x[3] += a[k];
    for (k = j; k < 4; k++)
        x[3] += b[k];

    // qd::renorm(x[0], x[1], x[2], x[3]);
    // return qd_real(x[0], x[1], x[2], x[3]);
    return renorm4(x[0], x[1], x[2], x[3]);
}

// inline qd_real qd_real::sloppy_add(const qd_real &a, const qd_real &b)
vec4 qs_sloppy_add(vec4 a, vec4 b)
{
    float s0, s1, s2, s3;
    float t0, t1, t2, t3;

    float v0, v1, v2, v3;
    float u0, u1, u2, u3;
    float w0, w1, w2, w3;

    vec2 tmp;
    vec3 tmp3;

    s0 = a.x + b.x;       // s0 = a[0] + b[0];
    s1 = a.y + b.y;       // s1 = a[1] + b[1];
    s2 = a.z + b.z;       // s2 = a[2] + b[2];
    s3 = a.w + b.w;       // s3 = a[3] + b[3];  

    v0 = s0 - a.x;        // v0 = s0 - a[0];
    v1 = s1 - a.y;        // v1 = s1 - a[1];
    v2 = s2 - a.z;        // v2 = s2 - a[2];
    v3 = s3 - a.w;        // v3 = s3 - a[3];

    u0 = s0 - v0;
    u1 = s1 - v1;
    u2 = s2 - v2;
    u3 = s3 - v3;

    w0 = a.x - u0;        // w0 = a[0] - u0;
    w1 = a.y - u1;        // w1 = a[1] - u1;
    w2 = a.z - u2;        // w2 = a[2] - u2;
    w3 = a.w - u3;        // w3 = a[3] - u3; 

    u0 = b.x - v0;        // u0 = b[0] - v0;
    u1 = b.y - v1;        // u1 = b[1] - v1;
    u2 = b.z - v2;        // u2 = b[2] - v2;
    u3 = b.w - v3;        // u3 = b[3] - v3;

    t0 = w0 + u0;
    t1 = w1 + u1;
    t2 = w2 + u2;
    t3 = w3 + u3;

    tmp = two_sum(s1, t0); // s1 = qd::two_sum(s1, t0, t0);
    s1 = tmp.x;
    t0 = tmp.y;

    tmp3 = three_sum(s2, t0, t1); // qd::three_sum(s2, t0, t1);
    s2 = tmp3.x;
    t0 = tmp3.y;
    t1 = tmp3.z;

    tmp3 = three_sum2(s3, t0, t2); // qd::three_sum2(s3, t0, t2);
    s3 = tmp3.x;
    t0 = tmp3.y;
    t2 = tmp3.z;

    t0 = t0 + t1 + t3;

    // qd::renorm(s0, s1, s2, s3, t0);
    return renorm(s0, s1, s2, s3, t0); // return qd_real(s0, s1, s2, s3);
}

vec4 qs_add(vec4 _a, vec4 _b)
{
    return qs_sloppy_add(_a, _b);
    //  return qs_ieee_add(_a, _b);
}  

vec4 qs_mul(vec4 a, vec4 b)
{
    float p0, p1, p2, p3, p4, p5;
    float q0, q1, q2, q3, q4, q5;
    float t0, t1;
    float s0, s1, s2;
    vec2 tmp;
    vec3 tmp3;

    tmp = two_prod(a.x, b.x); // p0 = qd::two_prod(a[0], b[0], q0);
    p0 = tmp.x;
    q0 = tmp.y;

    tmp = two_prod(a.x, b.y); // p1 = qd::two_prod(a[0], b[1], q1);
    p1 = tmp.x;
    q1 = tmp.y;

    tmp = two_prod(a.y, b.x); // p2 = qd::two_prod(a[1], b[0], q2);
    p2 = tmp.x;
    q2 = tmp.y;

    tmp = two_prod(a.x, b.z); // p3 = qd::two_prod(a[0], b[2], q3);
    p3 = tmp.x;
    q3 = tmp.y;

    tmp = two_prod(a.y, b.y); // p4 = qd::two_prod(a[1], b[1], q4);
    p4 = tmp.x;
    q4 = tmp.y;

    tmp = two_prod(a.z, b.x); // p5 = qd::two_prod(a[2], b[0], q5);
    p5 = tmp.x;
    q5 = tmp.y;

    /* Start Accumulation */
    tmp3 = three_sum(p1, p2, q0); // qd::three_sum(p1, p2, q0);
    p1 = tmp3.x;
    p2 = tmp3.y;
    q0 = tmp3.z;

    /* Six-Three Sum  of p2, q1, q2, p3, p4, p5. */
    tmp3 = three_sum(p2, q1, q2); // qd::three_sum(p2, q1, q2);
    p2 = tmp3.x;
    q1 = tmp3.y;
    q2 = tmp3.z;

    tmp3 = three_sum(p3, p4, p5); // qd::three_sum(p3, p4, p5);
    p3 = tmp3.x;
    p4 = tmp3.y;
    p5 = tmp3.z;

    /* compute (s0, s1, s2) = (p2, q1, q2) + (p3, p4, p5). */
    tmp = two_sum(p2, p3); // s0 = qd::two_sum(p2, p3, t0);
    s0 = tmp.x;
    t0 = tmp.y;

    tmp = two_sum(q1, p4); // s1 = qd::two_sum(q1, p4, t1);
    s1 = tmp.x;
    t1 = tmp.y;

    s2 = q2 + p5;
    tmp = two_sum(s1, t0); // s1 = qd::two_sum(s1, t0, t0);
    s1 = tmp.x;
    t0 = tmp.y;
    s2 += (t0 + t1);

    /* O(eps^3) order terms */
    s1 += a.x*b.w + a.y*b.z + a.z*b.y + a.w*b.x + q0 + q3 + q4 + q5;

    return renorm(p0, p1, s0, s1, s2); // qd::renorm(p0, p1, s0, s1, s2);
}

float ds_compare(vec2 dsa, vec2 dsb)
{
    if (dsa.x < dsb.x) return -1.;
    else if (dsa.x == dsb.x)
    {
        if (dsa.y < dsb.y) return -1.;
        else if (dsa.y == dsb.y) return 0.;
            else return 1.;
            }
    else return 1.;
}

float qs_compare(vec4 qsa, vec4 qsb)
{
    if(ds_compare(qsa.xy, qsb.xy)<0.) return -1.; // if (dsa.x < dsb.x) return -1.;
    else if (ds_compare(qsa.xy, qsb.xy) == 0.) // else if (dsa.x == dsb.x)
    {
        if(ds_compare(qsa.zw, qsb.zw)<0.) return -1.; // if (dsa.y < dsb.y) return -1.;
        else if (ds_compare(qsa.zw, qsb.zw) == 0.) return 0.;// else if (dsa.y == dsb.y) return 0.;
            else return 1.;
            }
    else return 1.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    // animation
    float a = 0.4*iTime;
	float tz = 0.5 - 0.5 * cos(a);

        // array of zoom targets: x + yi
    vec2[] zoomTargets = vec2[] (
        vec2(-1.25067, 0.02012),
        vec2(-0.235125, 0.827215),
        vec2(-0.74635, 0.11022),
        vec2(-0.925, 0.266),
        vec2(-0.745428, 0.113009),
        vec2(-0.7479, 0.099983)
    );

    // select zoom target
    vec2 target = zoomTargets[int(a / 6.28318530718) % 6];
    vec2 targetNext = zoomTargets[int(a / 6.28318530718 + 1.0) % 6];


    vec4 zo = vec4(pow(0.5, 15.0 * tz), 0.0, 0.0, 0.0);

    vec4 cx = vec4(p.x, 0.0, 0.0, 0.0);
    vec4 cy = vec4(p.y, 0.0, 0.0, 0.0);

	cx = qs_add(qs_mul(cx, zo), vec4(target.x, 0.0, 0.0, 0.0));
	cy = qs_add(qs_mul(cy, zo), vec4(target.y, 0.0, 0.0, 0.0));

    float n = 0.0;

    vec4 tmp;
    vec4 zx = cx;
    vec4 zy = cy;
    vec4 two = vec4(2.0, 0., 0., 0.); 

    vec4 e_radius = vec4(4.0, 0., 0., 0.);   // no sqrt available so compare with radius^2 = 2^2 = 2*2 = 4

    for( int i=0; i <1500; i++ )
    {
		tmp = zx;
        zx = qs_add(qs_add(qs_mul(zx, zx), -qs_mul(zy, zy)), cx);
        zy = qs_add(qs_mul(qs_mul(zy, tmp), two), cy);

        if( qs_compare(qs_add(qs_mul(zx, zx), qs_mul(zy, zy)), e_radius) > 0.)
        {
            // http://linas.org/art-gallery/escape/escape.html
            n += 1. - log(log(length(vec2(zx.x, zy.x)))) /log(2.);     
            break;   
        }
        n += 1.0;
    }


    if(n < 1500.0 ) {
        fragColor = vec4((-cos(n * vec3(0.025, 0.08, 0.12)) + 1.0) * 0.5, 1.0);

    } else {
        // black center
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }

}

