    normalize(vec3(-PHI-1.0,0, 1)),
    normalize(vec3(1, PHI+1.0, 0)),
    normalize(vec3(-1, PHI+1.0, 0)),

    normalize(vec3(0, PHI, 1)),
    normalize(vec3(0, -PHI, 1)),
    normalize(vec3(1, 0, PHI)),
