float cosh_shad(in float ang) {
    float res;
    res = (exp(ang)+exp(-ang))/2.0;
    return(res);
}

float sinh_shad(in float ang) {
    float res;
    res = (exp(ang)-exp(-ang))/2.0;
    return(res);
}

float atanh_shad(in float ang) {
    float res;
    res = log((1.0+ang)/(1.0-ang))/2.0;
    return(res);
}

float acosh_shad(in float ang) {
    float res;
    res = log(ang + sqrt(ang*ang-1.0));
    return(res);
}

