vec2 cexp(vec2 z)
{
    return exp(z.x) * vec2(cos(z.y), sin(z.y));
}

vec2 clog(vec2 z)
{
    return vec2(log(length(z)), atan(z.y, z.x));
}

vec2 cpow(vec2 z, float p)
{
    return cexp(clog(z) * p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy *2.) / iResolution.xy - vec2(1);

    uv *= 1.;

    float tspeed = 0.4;
    float t = iTime * tspeed;

    vec2 graduv = -cpow(uv.yx, -2.);	// Gradient is -1/z 
    uv = cpow(uv, -1.);					// Function is 1/z

    uv += t;			// Scroll view 

    float dex = fract(length(uv.x));	// Get x isolines
    float dey = fract(length(uv.y));	// Get y isolines

    // Increase width with increasing gradient (aka the lines get denser) to reduce aliasing problems
    #define W (sqrt(length(graduv))*0.02)

    float linex = smoothstep(0.5 - W, 0.5, dex) - smoothstep(0.5, 0.5 + W, dex);
    float liney = smoothstep(0.5 - W, 0.5, dey) - smoothstep(0.5, 0.5 + W, dey);

	fragColor = vec4(vec3(linex + liney), 1.);
}

// All competitor's shaders here:
// https://github.com/KoltesDigital/Shader-Showdown-Paris/tree/master/2017-12-08
// https://www.youtube.com/watch?v=ugs3-B3Tng0

mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

float de(vec3 p) {

	//p.xy *= r2d(1/1e5*fGlobalTime);

	p.x = abs(p.x) - 25.;

	p.xz *= r2d(3.14 / 4.);

	p = mod(p + 30., 60.) - 30.;

	vec3 q = p;

	p.y += sin(p.x*3.);

	float r = 15. + 3. * pow(.5 + .5*sin(15. * iTime), 4.);

    // iq's heart formula: https://www.youtube.com/watch?v=aNR4n0i2ZlM
	p.z *= 2. - p.y / 15.;
	p.y = 4. + 1.2 * p.y - abs(p.x) * sqrt(max((20. - abs(p.x)) / 15., 0.));

	float sph = length(p) - r;
	float sph2 = length(q) - 9.;

	q.y -= 7.;
	q.x -= 7.;
	float sph4 = length(q.xy + cos(iTime)) - 3.;
	q.x += 14.;
	float sph5 = length(q.xy - cos(iTime)) - 3.;

	float d = max(sph, -sph2);
	d = max(d, -sph4);
	d = max(d, -sph5);

	return d / 3.;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv -= 0.5;
	uv /= vec2(iResolution.y / iResolution.x, 1);

	vec3 ro = vec3(cos(iTime) * 20., iTime * 50., -50. * sqrt(abs(tan(iTime))));
	vec3 rd = normalize(vec3(uv, -1)), p;
	p = ro;

	float it = 0.;
	for (float i =0.; i < 1.; i += .01) {
		it = i;
		float d = de(p);
		if (d < .01) break;
		p += rd*d;
	}

	vec3 c = mix(vec3(.9, .3, .5), vec3(.2, .1, .2), it);
	c = mix(c, vec3(.2, .1, .2), 1. - exp(-.005 * length(ro - p)));

	fragColor = vec4(c, 1);
}

#define UV_CENTERIZE true
#define UV_SIZE 1.2

// this will adapt smoothing values to different resolutions
#define UV_DENSITY (UV_SIZE/iResolution)

// ----- CIRCLE 
// fill
float g_circle_fill(vec2 p, vec2 center, float radius)
{
	return smoothstep(radius + UV_DENSITY.x * 5., radius, length(p - center));
}

// line 
float g_circle_line(vec2 p, vec2 center, float radius, float th)
{
    float delta = UV_DENSITY.x * 5.;
    float rad1 = radius - th;
    float rad2 = radius + th;
    float l = length(p - center);
	return min(smoothstep(rad1 - delta, rad1, l),
              smoothstep(rad2 + delta, rad2, l));
}

float g_circle_line(vec2 p, vec2 center, float radius){
	return g_circle_line(p, center, radius, 0.); 
}

