//Voronoise by IQ
vec3 hash3( vec2 p )
{
    vec3 q = vec3( dot(p,vec2(127.1,311.7)), 
				   dot(p,vec2(269.5,183.3)), 
				   dot(p,vec2(419.2,371.9)) );
	return fract(sin(q)*43758.5453);
}

float noise( in vec2 x, float u, float v )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    float k = 1.0 + 63.0*pow(1.0-v,4.0);
    float va = 0.0;
    float wt = 0.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2  g = vec2( float(i), float(j) );
        vec3  o = hash3( p + g )*vec3(u,u,1.0);
        vec2  r = g - f + o.xy;
        float d = dot(r,r);
        float w = pow( 1.0-smoothstep(0.0,1.414,sqrt(d)), k );
        va += w*o.z;
        wt += w;
    }

    return va/wt;
}



float box(in vec2 p, vec2 size)
        {
            vec2 d = (abs(p) - size);
            return ((min(max(d.x , d.y ), 0.0) + length(max(d, vec2(0.0)))));
        }

float triangle(vec2 p, float width, float height)
        {
            vec2 n = normalize(vec2(height, width * 0.5));
            return max(abs(p).x * n.x + (p.y * n.y) - (height * n.y), -p.y);
        }

float disc(in vec2 p, in float radius)
        {
            return (length(p ) - radius);
        }

float ellipse(vec2 p,float rad, float offset,float ns1 )
{
    offset = rad-offset;
    float left =Subtract(box(p-vec2(2,0),vec2(2.0,20.0)),disc(p-vec2(offset,0.0),rad)+ns1);
    float right =Subtract(box(p+vec2(2,0),vec2(2.0,20.0)),disc(p+vec2(offset,0.0),rad)+ns1);
    return Merge(left,right);
}

float RenderLeafStem(vec2 uv, vec2 pos)
