// 2d sdf + lighting code taken from https://www.shadertoy.com/view/MsXBWH by wutzhuhn
// repeating flowers added by Del 10/12/17
#define roundnes 0.035

#define PI 3.14159265
#define DEG2RAD ((PI * 2.0) / 360.0)

float subtract(float a, float b)
{
	return min(max(a, -b), -roundnes) + length(max(vec2(roundnes) + vec2(a, -b), vec2(0.0)));
}

float merge(float a, float b)
{
	return max(min(a, b), roundnes) - length(max(vec2(roundnes) - vec2(a, b), vec2(0.0)));
}

mat2 rotate(float a)
{
    float sa = sin(a), ca = cos(a);
	return mat2(ca, -sa, sa, ca);
}

float box(vec2 p, vec2 size)
{
	vec2 d = abs(p) - size + roundnes;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - roundnes;
}


// http://mercury.sexy/hg_sdf/
// Repeat around the origin by a fixed angle.
// For easier use, num of repetitions is use to specify the angle.
float pModPolar(inout vec2 p, float repetitions)
{
	float angle = 2.*PI/repetitions;
	float a = atan(p.y, p.x) + angle/2.;
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.)) c = abs(c);
	return c;
}

// render 16 flowers with 9 petals each...
float shapeDist(vec2 p)
{
    float d1 = box(p+vec2(0.0,0.0), vec2(1.0, 1.0));		// main box to subtract
    vec2 p2 = p * rotate(iTime*0.25*PI);
    float offset = pModPolar(p2,16.0);
    vec2 p3 = (p2-vec2(0.45,0.0)) * rotate(iTime*0.8);
	pModPolar(p3,9.0);    
    float d2 = box(p3-vec2(0.05,0.0), vec2(0.04, 0.02));

    // Mouse click box! :)
    if (iMouse.z>0.5)
    {
        vec2 mousep = (  iMouse.xy -.5*iResolution.xy ) / iResolution.y;
        float d4 = box(p-mousep,vec2(0.1,0.1));
        d2 = merge(d2,d4);
    }

    float dist = subtract(d1,d2);
    return dist;
}

float maskSharp(vec2 p)
{
    return clamp(shapeDist(p) * 100.0, 0.0, 1.0);
}

float maskBlurry(vec2 p)
{
    return clamp((shapeDist(p ) + 0.003) * 75.0, 0.0, 1.0);
}

vec3 normalAt(vec2 p)
{
    float ps = 1.0 / iResolution.x;
    float x = maskBlurry(p - vec2( ps, 0.0)) - maskBlurry(p + vec2( ps, 0.0));
    float y = maskBlurry(p - vec2(0.0,  ps)) - maskBlurry(p + vec2(0.0,  ps));
    return normalize(vec3(x, y, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = ( fragCoord -.5*iResolution.xy ) / iResolution.y;
    vec3 color = vec3(0.1,0.3,0.1);

    float val = maskSharp(p);
    float shade = 0.0;
    if (val>0.0)
    {
        float z = (0.5+sin(iTime*3.5))*0.1;
        vec3 lightPos = vec3(0.0, 0.0, 0.2+z);
        vec3 toLight = lightPos - vec3(p, 0.0);
        vec3 normal = normalAt(p);
		shade = 0.3 + maskSharp(p) * 0.7;
        float col = clamp(dot(normalize(toLight), normal), 0.0, 1.0) / length(toLight) * shade;
        color = vec3(0.6,0.2,0.9)*col;
    }
	fragColor = vec4(color, 1.0);
}

