}



float defineACircle(vec2 center, vec2 currentPixel, float radius){
    float toReturn = 0.0;

    if((distance(center , currentPixel) - radius)/ radius< 1.0) toReturn = 1.0;

 	return toReturn;  
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    const float PI = 3.14159;

    // Variables

    float tilingX = 10.0;
    float tilingY = 10.0;
    float smoothnesOfLines = 0.06;
    float radarSmoothness = 1.4;
    float RadarLineThickness = 0.01;
    float noiseAmount = 0.18;

    float rotationSpeed = 0.2;

    vec4 colorBackground = vec4(0.0, 0.0, 0.0, 0.0);
    vec4 linesBackground = vec4(0.8, 0.5 , 0.1, 1.0);
    vec4 radarLineColor = vec4(1.0, 1.0, 0.1, 1.0);

    vec2 circleMiddle = vec2(0.5, 0.5);



    // Here you map your screen to a uv space between 1 nad 0 
	vec2 uv = fragCoord.xy / iResolution.xy;


    // Horizontal

    float repeatingCoordinateSystemHorizontal = fract(uv.x * tilingX);

    float distanceToLineToBeDrawnHorizontal = abs( repeatingCoordinateSystemHorizontal - 0.5 ) ;

    float lerpFactorX = clamp( distanceToLineToBeDrawnHorizontal / smoothnesOfLines, 0.0, 1.0);

    vec4 endcolor =  mix(colorBackground, linesBackground, 1.0 - lerpFactorX);


    // Vertical

    float repeatingCoordinateSystemVertical = fract(uv.y * tilingY);
    float distanceToLineToBeDrawnVertical =  abs( repeatingCoordinateSystemVertical - 0.5 ) ;

    float lerpFactorY = clamp( distanceToLineToBeDrawnVertical / smoothnesOfLines, 0.0, 1.0);

    endcolor =  mix(endcolor, linesBackground, 1.0 - lerpFactorY);

    // Add the Dots 

    // dot one button left
    float directionX = rand (vec2(floor(iTime*0.01), floor(iTime*0.1)));
    float directionY = rand (vec2( 10.0 + floor(iTime*0.01), 30.0 + floor(iTime*0.1)));

    float dotsLerpFactor =  defineACircle( vec2(fract(iTime*0.2) *directionX ,fract(iTime*0.2) * directionY), uv, 0.024);


    // dot two up right

    directionX = rand (vec2(10.0 + floor(iTime*0.01), 15.0 +  floor(iTime*0.1)));
    directionY = rand (vec2( 35.0 + floor(iTime*0.01), 9.0 + floor(iTime*0.1)));

    dotsLerpFactor += defineACircle( vec2(1.0 -fract(iTime*0.2) *directionX , 1.0 - fract(iTime*0.2) * directionY), uv, 0.02);




    // dot three up left

    directionX = rand (vec2(-17.0 + floor(iTime*0.01), -4.0 +  floor(iTime*0.1)));
    directionY = rand (vec2( 135.0 + floor(iTime*0.01), 33.0 + floor(iTime*0.1)));

    dotsLerpFactor += defineACircle( vec2(fract(iTime*0.2) *directionX , 1.0 - fract(iTime*0.2) * directionY), uv, 0.016);


     // dot four down right

    directionX = rand (vec2(787.0 + floor(iTime*0.01), 240.0 +  floor(iTime*0.1)));
    directionY = rand (vec2( 532.0 + floor(iTime*0.01), 241.0 + floor(iTime*0.1)));

    dotsLerpFactor += defineACircle( 1.0 - vec2(fract(iTime*0.2) *directionX , fract(iTime*0.2) * directionY), uv, 0.028);


    vec4 dotsColor = mix(vec4(0.0, 0.0, 0.0, 0.0), linesBackground, dotsLerpFactor);
    endcolor +=dotsColor;

    // Vector to Middle of the circle 

    vec2 vectorToMiddle = uv - circleMiddle;

    float angleToPixel = atan(vectorToMiddle.y/vectorToMiddle.x);

    if((vectorToMiddle.x < 0.0) && (vectorToMiddle.y>0.0)) angleToPixel +=1.0* PI;
    if((vectorToMiddle.x < 0.0) && (vectorToMiddle.y<0.0)) angleToPixel +=1.0* PI;
    if((vectorToMiddle.x > 0.0) && (vectorToMiddle.y<0.0)) angleToPixel +=2.0* PI;

    float cacheForRadarLine = 0.0;
    float distanceToLawToDraw =  angleToPixel - radians( fract( - iTime * rotationSpeed) * 360.0);

    float lerpFactorCircularLine = (1.0- distanceToLawToDraw) / radarSmoothness;

    if(lerpFactorCircularLine > 1.0 ) {

        if(lerpFactorCircularLine < 1.0 + RadarLineThickness)  cacheForRadarLine = 1.0;
        lerpFactorCircularLine = 0.0;

    }



    // Blend Colors

    vec4 radarColor  =  mix(vec4(0.0, 0.0, 0.0, 1.0), linesBackground,  lerpFactorCircularLine);

     endcolor += radarColor;

    endcolor += (linesBackground * 0.5);

    if(distanceToLawToDraw + 0.3<0.0) endcolor *=lerpFactorCircularLine ;

    // Draw 


    endcolor = mix (endcolor, radarLineColor, cacheForRadarLine);


    // Blend over the static noise on screen
    vec4 noise = vec4 (rand(uv * iTime), rand(uv* iTime), rand(uv* iTime), 1.0);

    vec4 toBlend = mix(vec4(0.8, 0.8, 0.8, 1.0), noise, 0.2);

    endcolor *=toBlend;

    endcolor += mix(vec4(0.0, 0.0, 0.0, 1.0), noise, noiseAmount);



	fragColor =endcolor;
}

#define sat(a) clamp(a, 0.0, 1.0)

vec2 dmin(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

float smoothmin(float a, float b, float k) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec2 rot(vec2 p, float a) {
    float s = sin(a);
    float c = cos(a);

    return mat2(c, s, -s, c)*p;
}

float cylinder(vec3 p, vec2 h) {
    vec2 d = abs(vec2(length(p.xz), p.y)) - h;
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float segment(vec3 p, vec3 a, vec3 b, float r) {
    vec3 pa = p - a; 
    vec3 ba = b - a;

    float h = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);

    return length(pa - ba*h) - r;
}

float straw(vec3 p) {
    p.x += 0.6;
    p.y -= 1.2;

    float s = cylinder(p, vec2(0.12 + 0.02*sin(100.0*p.y)*step(0.65, p.y), 0.8));

    p.y -= 0.97;
    p.x += 0.3;
	p.xy = rot(p.xy, -1.0);

    p.xy = rot(p.xy + vec2(0, 0.2), 0.2*sin(14.0*iTime)) - vec2(0, 0.2);
    float us = cylinder(p, vec2(0.12 + 0.02*sin(100.0*p.y)*step(0.15, -p.y), 0.4));
    us = max(us, -cylinder(p, vec2(0.09, 0.5)));
    s = min(s, us);

    return s;
}

float head(vec3 p) {
    p.y -= 1.0;

    float c = length(p) - 1.0;
    c = max(c, -(length(p) - 0.8));
    c = max(c, p.y - 0.3);

    p.x += 1.0;
    p.y += 0.3;

    float h = length(vec2(length(p.xy) - 0.5, p.z)) - 0.1;
    h = max(h, p.x - 0.4);

    h = smoothmin(h, c, 0.1);

    p.yz = rot(p.yz, sign(p.z)*0.3);
    float s = length((p - vec3(1.6, -0.37, 0))*vec3(1.6, 1.3, 0.7)) - 0.3;
    h = max(h, -s);

    return h;
}

float eyes(vec3 p) {
    vec3 op = p;

    p.y -= 1.07;
    p.x -= 0.65;

    p.y *= 0.7;
    p.z *= 0.7;

	p.z = abs(p.z) - 0.14;

    float e = length(p) - 0.4;
    e = max(e, p.y - 0.16);

    return max(e, -(length(op) - 0.8));
}

float innereyes(vec3 p) {    
    p.y -= 1.07;
    p.x -= 0.65;

    p.y *= 0.7;
    p.z *= 0.7;

    p.xy = rot(p.xy, 0.3);

    p.y *= 0.7;
    p -= vec3(0.3, -0.0, -0.0);

    p.z += 0.07*cos(10.0*iTime)*smoothstep(-0.2, 1.0, sin(iTime));
    p.z = abs(p.z) - 0.25;

    float e = length(p) - 0.15;
    return e;
}

float nose(vec3 p) {
    p.xy -= vec2(0.9, 0.6);
    return length(p*vec3(0.8, 1.2, 0.8)) - 0.1;
}

float torso(vec3 p) {
    vec3 q = p;

    p.xz *= 1.0 - 0.4*smoothstep(-1.1, 0.3, -p.y);
    float s = cylinder(p, vec2(0.45, 0.7));

    q.y += 0.4;
    s = min(s, max(length(q) - 0.83, q.y - 0.1));
    return s;
}

float legs(vec3 p) {
    p.z = -abs(p.z) + 0.4;


    float b = -0.1*cos(14.0*iTime);
    float l = segment(p + vec3(0, b, 0), 
                      vec3(0), 
                      vec3(0.1, -0.5, -0.3), 
                      0.17);

    l = min(l, segment(p + vec3(0, b, 0), 
                       vec3(0.1, -0.5, -0.3), 
                       vec3(-0.2, -1.0 + 1.2*b, 0), 0.17));

    p.yz = rot(p.yz, -0.4);
    return min(l, cylinder(p - vec3(0, 0.5 - b, 0), vec2(0.25, 0.5)));
}

float shoes(vec3 p) {
    p.y += 0.7;

    float b = -0.1*cos(14.0*iTime);
    p.z = -abs(p.z) + 0.4;

    vec3 heel = vec3(-0.2, 0.1-b, 0);
    vec3 toes = vec3(0.3, 0, -0.4);


    float sh = segment(p*vec3(0.8, 1.0, 0.8), heel, toes, 
                       0.25 + 0.14*smoothstep(0.0, 0.7, p.x));

    return sh;
}

float arms(vec3 p) {
    p.y -= 0.2;
    p.z = -abs(p.z) + 0.85;

    p.z -= 0.75;
    p.yz = rot(p.yz, -0.6*smoothstep(-1.0, 0.5, p.y));
    p.z += 0.75;
    float a = segment(p, vec3(0), vec3(0, -0.7, -0.3), 0.08);

    return a;
}

float hands(vec3 p) {
    p *= 0.9;
    p.y -= 0.4;
    p.z = -abs(p.z) + 1.07;

    float s = length((p - vec3(0, 0.1, 0))*vec3(1.2, 1.4, 1.2)) - 0.35;
    s = min(s, length((p - vec3(0, 0.4, 0))*vec3(1, 1.6, 1)) - 0.2);

    s = smoothmin(s, segment(p - vec3(0.0, 0.16, 0.17), 
                             vec3(0), 
                             vec3(-0.1, -0.2, 0.2), 0.08), 0.1);

    s = smoothmin(s, segment(p - vec3(0.0, 0.16, 0.17),
                             vec3(-0.1, -0.2, 0.2),
                             vec3(-0.1, -0.3, 0.1), 0.08), 0.01);

    p.z = -abs(p.z) + 0.12;
    p.z = -abs(p.z) + 0.05;

    p.x -= 0.1;
    p.y += 0.05;
    s = smoothmin(s, segment(p, vec3(0), vec3(0, -0.12, 0), 0.07), 0.1);
    s = smoothmin(s, segment(p, vec3(0, -0.12, 0), vec3(-0.2, -0.1, 0), 0.07), 0.02);
    return s;
}

