#define LEGS 2.0
#define HANDS 3.0
#define TORSO 4.0
#define HEAD  5.0
#define STRAW 6.0
#define EYES 7.0
#define INNEREYES 8.0
#define NOSE 9.0

vec2 de(vec3 p) {
    vec2 f = vec2(min(p.y + 1.0, p.x + 3.0), 0.0);
    vec2 sh = vec2(shoes(p), SHOES);

    p.y -= 0.2*cos(14.0*iTime);
	p.y -= 0.6;
    vec2 l = vec2(legs(p), LEGS);
    vec2 ds = vec2(hands(p), HANDS);
    p.y -= 1.35;

    vec2 t = vec2(smoothmin(torso(p), arms(p), 0.4), TORSO);

    p.y -= 0.6;
    vec2 h = vec2(head(p), HEAD);
    vec2 s = vec2(straw(p), STRAW);
    vec2 e = vec2(eyes(p), EYES);
    vec2 ie = vec2(innereyes(p), INNEREYES);
    vec2 m = vec2(nose(p), NOSE);

    vec2 ret = dmin(f, sh);
    ret = dmin(ret, dmin(l, t));
    ret = dmin(ret, dmin(h, s));
    ret = dmin(ret, e);
    ret = dmin(ret, ie);
    ret = dmin(ret, ds);
    ret = dmin(ret, m);

    return ret;
}

vec2 trace(vec3 o, vec3 d, float x) {
    float t = 0.0;
    float m = -1.0;

    for(int i = 0; i < 100; i++) {
        vec2 d = de(o + d*t);
        if(d.x < 0.001 || t >= x) break;
        t += d.x*0.85;
        m = d.y;
    }

    return vec2(t, t < x ? m : -1.0);
}

vec3 normal(vec3 p) {
    vec2 h = vec2(0.001, 0.0);
    vec3 n = vec3(
        de(p + h.xyy).x - de(p - h.xyy).x,
        de(p + h.yxy).x - de(p - h.yxy).x,
        de(p + h.yyx).x - de(p - h.yyx).x
    );
    return normalize(n);
}


float ao(vec3 p, vec3 n) {
    float o = 0.0, s = 0.005, w = 1.0;
    for(int i = 0; i < 15; i++) {
        float d = de(p + n*s).x;
        o += (s - d)*w;
        w *= 0.98;
        s += s/float(i + 1);
    }
    return 1.0 - sat(o);
}

mat3 camera(vec3 e, vec3 l) {
    vec3 w = normalize(l - e);
    vec3 u = normalize(cross(vec3(0, 1, 0), w));
    vec3 v = normalize(cross(w, u));

    return mat3(u, v, w);
}

vec3 materail(vec3 p, float m) {
    p.y -= 0.2*cos(14.0*iTime);

    if(m == 0.0) {
        return vec3(1.5);
    } else if(m == STRAW) {
        p.xy = rot(p.xy, -1.0*step(4.4, p.y));
    	vec3 m = vec3(1.5, 0, 0);
        m += vec3(0.5, 2.0, 2.0)*smoothstep(0.3, 0.8, abs(cos(10.0*p.y)));

        return m;
    } else if(m == TORSO) return vec3(0.1) + vec3(1, 0, 0)*step(-1.63, -p.y);
    else if(m == HANDS) return vec3(2.0);
    else if(m == EYES) return vec3(2.0);
    else if(m == INNEREYES) return vec3(0.1);
    else if(m == NOSE) return vec3(1.5, 0.1, 0.1);
    else if(m == SHOES) return vec3(1.09, 0.54, 0.14);
    else if(m == LEGS) {
        p.z = -abs(p.z) + 0.4;
        p.yz = rot(p.yz, -0.4);

        p.y -= 0.1*cos(14.0*iTime);;

        if(p.y > 0.52) return vec3(1.1, 0.1, 0.1);
        else return vec3(1);
	} else return vec3(1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
    vec2 mo = iMouse.z == 0.0
        ? vec2(1.5, 1.0)
        : (-iResolution.xy + 4.0*iMouse.xy)/iResolution.y;

    float at = 0.5*iTime;

    vec3 ro = vec3(7.0*sin(mo.x), 1.0 + 2.0*mo.y, 7.0*cos(mo.x));
    vec3 rd = camera(ro, vec3(0, 2.5, 0))*normalize(vec3(p, 1.97));

    vec3 col = vec3(0);
    vec3 lig = normalize(vec3(0.8, 0.7, -0.6));

    vec2 t = trace(ro, rd, 50.0);
    if(t.x < 50.0) {
        vec3 pos = ro + rd*t.x;
        vec3 nor = normal(pos);
        vec3 ref = reflect(rd, nor);

        float occ = ao(pos, nor);
        float sha = step(15.0, trace(pos+nor*0.001, lig, 15.0).x);

        col += 0.5*occ;
        col += sat(dot(lig, nor))*occ*sha;
        col += pow(sat(1.0 + dot(rd, nor)), 2.0)*occ;
        col += 2.0*pow(sat(dot(lig, ref)), 20.0)*occ;

        col *= materail(pos, t.y);
    }

    col = mix(vec3(0), col, exp(-0.1*t.x));

    col = 1.0 - exp(-0.5*col);
    col = pow(col, vec3(1.0/2.2));

    fragColor = vec4(col, 1);
}

// variant of  https://shadertoy.com/view/llsfRj
// application from https://shadertoy.com/view/Xlffzj

#define S(v) smoothstep(2./iResolution.y, 0., v )

// --- direct transforms
vec2 Mobius(vec2 p, vec2 z1, vec2 z2)
{
	z1 = p - z1; p -= z2;
	return mat2(z1,z1.y,-z1.x) * p / dot(p, p);
}

vec2 spiralZoom(vec2 p, vec2 offs, float n, float spiral, float spiral2, float zoom, vec2 phase)
{
	p -= offs;
	float a = atan(p.y, p.x)/6.283 + iTime/32.;
	float d = length(p);
	return mat2(n,spiral2, spiral,-zoom) * vec2(a, log(d)) + phase;
}

// --- inverse transforms
vec2 iMobius(vec2 p, vec2 z1, vec2 z2)     
{                               
    float s = sign(p.x), sb = sign(p.y),             // make unique sol: s=1,x1,y1 otherwise 4
          l = length(p), t = p.y/p.x,                // solve  u = v*l ; tan(u,v) = p.y/p.x
          c = s / sqrt(1.+t*t),                      // c = cos(atan( ) )
        v = length(z1-z2) / sqrt( 1.+ l*l -2.*l*c ), // c = (u+v-|z1z2| ) / 2uv
        u = v*l;                                   
    vec2  a = 2.*(z1-z2);                            // solve |P-z1| = u ; |P-z2| = v
    float b = u*u-v*v + dot(z2,z2)-dot(z1,z1);       // ||-||: a.P + b = 0
    // y = -( b + a.x* x ) / a.y                     // normalize by a.y
    b /= a.y; a /= a.y;                              // ( in Shane example a.y was = 0 ! )
    float A = 1. +  a.x*a.x,                         //  |P-z1| = u , P = (x,y)
          B =     2.* b*a.x -2.*dot(vec2(1,-a.x),z1),
          C =           b*b +2.*b*z1.y + dot(z1,z1) - u*u,
          D = max(0., B*B - 4.*A*C),
         x1 = (-B+sb*sqrt(D))/(2.*A), y1 = -( b + a.x*x1 );
     //  x2 = (-B-sb*sqrt(D))/(2.*A), y2 = -( b + a.x*x2 );
  // if (abs(A)<1e-5) { x1 = x2 = -C/B; y1 = y2 = -( b + a.x*x1 ) / a.y; } // degenerate case
	return vec2(x1,y1);                    
}

vec2 ispiralZoom(vec2 p, vec2 offs, float n, float spiral, float spiral2, float zoom, vec2 phase)
{
    p = inverse(mat2(n,spiral2, spiral,-zoom)) * (p-phase) ;
    p.x = (p.x - iTime/32.) * 6.283;             // ( p.x, p.y )  = ( a, log(d) )
    return exp(p.y) * vec2(cos(p.x),sin(p.x)) + offs;
}

// --- demo configuration 
vec2 Z1 = vec2(-.875, -.525),  // in Shane example Z1.y=Z2.y was causing a degenerescence
     Z2 = vec2(.375, -.125);
float spiral = 3.14159*.2,
      spiral2 = 2.5; // ~= 3.14159*.8 . was 1

vec2 MobiusSpiral(vec2 q)      // total direct transform
{
    q = Mobius(q, Z1, Z2);
    return spiralZoom(q, vec2(-.5), 5., spiral, spiral2, .5, vec2(-1, 1)*iTime*.125);
}
float DrawInvMobiusSpiral(vec2 q, vec2 uv0, float r) // total inverse transform + draw iso-X
{
    vec2 v; float s = 0.;
    q = ispiralZoom(q, vec2(-.5), 5., spiral, spiral2, .5, vec2(-1, 1)*iTime*.125);
    v = iMobius(q, Z1, Z2);  
    return S(length(uv0-v) - r ) ; //* (.5+.5* cos( 3.14 * length(uv0-v) / r / 2.) );
}

void mainImage(out vec4 O, vec2 U)
{
	vec2 R = iResolution.xy,
        uv0 = (U -.5*R) / R.y,
    uv = MobiusSpiral(uv0);
#define f(v) (fract(v/spiral2+.5)-.5)*spiral2                    // suppress the wrapping glitch
    mat2 M = inverse(mat2(f(dFdx(uv)),f(dFdy(uv)))) / R.y;       // measure compression, for LOD

    O -= O;
    float l,a, r=0.; vec2 q;
    for (int i=0; i<2; i++) {
        //q = floor(uv*4.)/4.;

        l = log2(20.*length(M[1]));
        a = fract(l);
        l = exp2(floor(l));                                      // compute H-LOD
        q = vec2( floor(uv.x*l+float(i)) / l, uv.y );            // H isolines in screen space
        O += (1.-O)* (1.-a)* DrawInvMobiusSpiral(q, uv0, r );
        q = vec2( floor(uv.x*(l*2.)+float(i)) / (l*2.), uv.y );  // inter-LOD fading
        O += (1.-O)* a* DrawInvMobiusSpiral(q, uv0, r );
#if 1   
        l = log2(46.*length(M[0])); // was 100
        a = fract(l);
        l = exp2(floor(l));                                      // compute V-LOD
        q = vec2( uv.x, floor(uv.y*l+float(i)) / l );            // V isolines in screen space
        O += (1.-O)* (1.-a)* DrawInvMobiusSpiral(q, uv0, r );
        q = vec2( uv.x, floor(uv.y*(l*2.)+float(i)) / (l*2.) );  // inter-LOD fading
        O += (1.-O)* a* DrawInvMobiusSpiral(q, uv0, r );
#endif

    }
    O = sqrt(O);                                                 // to sRGB
}

#define CELL vec2(0.1, 0.1)

float circle(vec2 coord, float r) {
    // Alternate crawl
    if(mod(coord.y, CELL.y * 2.0) < CELL.y) {
        coord.x += mod(iTime/30. * (1.0 + floor(coord / CELL).y), CELL.x);
    } else {
        coord.x += mod(iTime/30. * (CELL.y - floor(coord / CELL).y), CELL.x);
    }
    vec2 cell = floor(coord / CELL);
    vec2 center = cell * CELL + CELL/2.0;
    return r - distance(coord, center);
}

vec4 crawl(vec2 coord) {
    vec2 uv = coord;
    float circle1 = smoothstep(0.0, CELL.y/8.0, circle(coord,  CELL.y/3.5));
    float circle2 = smoothstep(0.0, CELL.y/4.0, circle(coord,  CELL.y/2.0));
    vec4 grad1 = vec4(uv.rg, 1.0, 1.0);
    vec4 grad2 = vec4(2.0 * abs(uv.x+sin(iTime)/2.0), 0.5, 0.5, 0.0);

    vec4 bg = vec4(0.1, 0.2, 0.4, 1.0);
    vec4 color = mix(bg, grad1, circle1);
    color = mix(color, grad2, circle2 * 0.5);
    return color;
}

vec4 ghost(vec2 coord, float times) {
    vec4 color = vec4(0.0);
    float m = 1.0;
    for(float i = 0.; i < times; i++) {
        color = mix(color, crawl(coord), m);
        coord.x -= CELL.x/(times);
        coord.y += CELL.y/(times);
        m -= (0.5 / (times));
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 coord = fragCoord / iResolution.y;
    fragColor = ghost(coord, 15. + sin(iTime * (coord.x / CELL.x)));
}

