// This sample demonstrate various relative sky luminance distributions from the paper
// "CIE General Sky Standard Defining Luminance Distributions" by Darula and Kittler 
// Referenced below as [Darula02]


#define USE_SOFT_TYPE_TRANSITION 1

const float kPiOverTwo = 1.5707963;

// Table (1) from [Darula02]
const vec2 kGradation_1 = vec2( 4.0, -0.70);
const vec2 kGradation_2 = vec2( 1.1, -0.80);
const vec2 kGradation_3 = vec2( 0.0, -1.00);
const vec2 kGradation_4 = vec2(-1.0, -0.55);
const vec2 kGradation_5 = vec2(-1.0, -0.32);
const vec2 kGradation_6 = vec2(-1.0, -0.15);

const vec3 kIndicatrix_1 = vec3( 0.0, -1.0, 0.00);
const vec3 kIndicatrix_2 = vec3( 2.0, -1.5, 0.15);
const vec3 kIndicatrix_3 = vec3( 5.0, -2.5, 0.30);
const vec3 kIndicatrix_4 = vec3(10.0, -3.0, 0.45);
const vec3 kIndicatrix_5 = vec3(16.0, -3.0, 0.30);
const vec3 kIndicatrix_6 = vec3(24.0, -2.8, 0.15);

vec2 TypeToAB[15] = vec2[]
(
    kGradation_1,
    kGradation_1,
    kGradation_2,
    kGradation_2,
    kGradation_3,
    kGradation_3,
    kGradation_3,
    kGradation_3,
    kGradation_4,
    kGradation_4,
    kGradation_4,
    kGradation_5,
    kGradation_5,
    kGradation_6,
    kGradation_6
);

vec3 TypeToCDE[15] = vec3[]
(
    kIndicatrix_1,
    kIndicatrix_2,
    kIndicatrix_1,
    kIndicatrix_2,
    kIndicatrix_1,
    kIndicatrix_2,
    kIndicatrix_3,
    kIndicatrix_4,
    kIndicatrix_2,
    kIndicatrix_3,
    kIndicatrix_4,
    kIndicatrix_4,
    kIndicatrix_5,
    kIndicatrix_5,
    kIndicatrix_6
);

// Formula (5) from [Darula02]
float LuminanceGradation(float Mu, vec2 AB)
{
    float A = AB.x;
    float B = AB.y;
    return (Mu < 0.0000001) ? 1.0 : 1.0 + A * exp(B / Mu);
}


// Figure (2) from [Darula02]
float LuminanceGradation_Relative(float Angle, vec2 AB)
{
    float CosAngle = cos(Angle);
	return LuminanceGradation(CosAngle, AB) / LuminanceGradation(1.0, AB);
}

// Formula (7) (8) and Figure (3) from [Darula02]
float ScatteringIndicatrix(float Angle, vec3 CDE)
{
    float C = CDE.x;
    float D = CDE.y;
    float E = CDE.z;
    float CosAngle = cos(Angle);
    return 1.0 + C * (exp(D * Angle) - exp(D * kPiOverTwo)) + E * CosAngle * CosAngle;
}

float ScatteringIndicatrix_Relative(float EyeToSun, float SunZenith, vec3 CDE)
{
    return ScatteringIndicatrix(EyeToSun, CDE) / ScatteringIndicatrix(SunZenith, CDE);
}

float Luminance_Relative(float EyeZenith, float SunZenith, float EyeToSun, vec2 AB, vec3 CDE)
{

    return ScatteringIndicatrix_Relative(EyeToSun, SunZenith, CDE) *
           LuminanceGradation_Relative(EyeZenith, AB);
}

float Luminance_Relative(float EyeZenith, float SunZenith, float EyeToSun, int Type)
{
    return Luminance_Relative(EyeZenith, SunZenith, EyeToSun, TypeToAB[Type], TypeToCDE[Type]);
}

float Plot(float F, float Y)
{
    float Y_Scale = 6.0;
    float Y_Shift = 2.0;
    F = log2(F);
    Y = (Y * 2.0 - 1.0);
    float Y_Delta = F - Y * Y_Scale;
    //return 1.0 - clamp(abs(F - Y * y_stretch - y_offset)  / fwidth(F - Y * y_stretch), 0.0, 1.0);
    //return 1.0 - smoothstep(0.0, 1.0, abs(Y_Delta - Y_Shift) * 0.5 / fwidth(Y_Delta));
    return 1.0 - clamp(abs(Y_Delta - Y_Shift) * 0.5 / fwidth(Y_Delta), 0.0, 1.0);
}

float Max6(float X0, float X1, float X2, float X3, float X4, float X5)
{
    return max(max(max(X0, X1), max(X2, X3)), max(X4, X5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float U = fragCoord.x / iResolution.x;
    float V = fragCoord.y / iResolution.y;

    float X = iMouse.x / iResolution.x;
    float Y = iMouse.y / iResolution.y;

    float ZeroToPi = kPiOverTwo * X * 2.0;
    float ZeroToPi_U = kPiOverTwo * U * 2.0;
    float ZeroToHalfPi_U = kPiOverTwo * U;

    // ZeroToHalfPi_U defines angular difference between EyeDir and Zenith
    // Corresponds to Figure (2) from [Darula02]
    float LG1 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_1);
    float LG2 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_2);
    float LG3 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_3);
    float LG4 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_4);
    float LG5 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_5);
    float LG6 = LuminanceGradation_Relative(ZeroToHalfPi_U, kGradation_6);

#if 0
    // ZeroToHalfPi_U defines difference between SunDir and Zenith
    // ZeroToPi defines difference between SunDir and EyeDir
    float SI1 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_1);
    float SI2 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_2);
    float SI3 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_3);
    float SI4 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_4);
    float SI5 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_5);
    float SI6 = ScatteringIndicatrix_Relative(ZeroToPi, ZeroToHalfPi_U, kIndicatrix_6);
#else
    // ZeroToPI_U defines angular difference between SunDir and EyeDir
    // Corresponds to Figure (3) from [Darula02]
    float SI1 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_1);
    float SI2 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_2);
    float SI3 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_3);
    float SI4 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_4);
    float SI5 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_5);
    float SI6 = ScatteringIndicatrix(ZeroToPi_U, kIndicatrix_6);
#endif


    float PlotR = Max6(Plot(LG1, V), Plot(LG2, V), Plot(LG3, V),
                       Plot(LG4, V), Plot(LG5, V), Plot(LG6, V));

    float PlotG = Max6(Plot(SI1, V), Plot(SI2, V), Plot(SI3, V),
                       Plot(SI4, V), Plot(SI5, V), Plot(SI6, V));

    float PlotB = 0.0;

    float EyeZenith = U * kPiOverTwo * 2.0;
    float EyeToSun  = X * kPiOverTwo * 2.0;    

    float NumTypes = 15.0;

    {
        Y = fract(iTime * 1.0 / NumTypes);

        float Type = floor(Y * NumTypes);
        float TypeLerp = fract(Y * NumTypes);
        for (int SunZenithIdx = 0; SunZenithIdx < 16; ++SunZenithIdx)
        {
            float SunZenith = (float(SunZenithIdx) / 16.0) * kPiOverTwo ;
#if USE_SOFT_TYPE_TRANSITION
            float Lum0 = Luminance_Relative(EyeZenith, SunZenith, EyeToSun, int(Type));
            float Lum1 = Luminance_Relative(EyeZenith, SunZenith, EyeToSun, (int(Type) + 1) % int(NumTypes));
            float Lum = mix(Lum0, Lum1, TypeLerp);
#else
            float Lum = Luminance_Relative(EyeZenith, SunZenith, EyeToSun, int(Type));
#endif
            PlotB = max(PlotB, Plot(Lum, V));
        }
    }
    fragColor = vec4(PlotR, PlotG, PlotB, 0.0);
}

// KIFS fork of https://www.shadertoy.com/view/MdlSRM

float stime, ctime;

void ry(inout vec3 p, float a)
{
  float ca = cos(a), sa = sin(a);
  vec3 q = p;
  p.x =  ca * q.x + sa * q.z;  
  p.z = -sa * q.x + ca * q.z; 
}  

