vec3 sunDir, shipConf, bDeck, qHit, cloudDisp, fogDisp;
float tCur, dstFar, tWav, szFac, cloudDens, fogDens;
int idObj;
const int idHull = 1, idRud = 2, idStruc = 3, idMast = 4, idSparT = 5, idSparL = 6, idSailT = 7,
   idSailA = 8, idSailF = 9, idFlag = 10, idRig = 11;
const float pi = 3.14159;

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  q = 0.25 * p + vec2 (0., tCur);
  h = 0.2 * sin (q.y) + 0.15 * sin (dot (q, vec2 (0.1, 1.2))) +
     0.1 * sin (dot (q, vec2 (-0.2, 1.4)));
  h *= 1.5 * (1. - smoothstep (0.8 * dstFar, dstFar, length (p)));
  q = p;
  wFreq = 0.5;
  wAmp = 0.07;
  tw = tWav * vec2 (1., -1.);
  for (int j = 0; j < 4; j ++) {
    q *= qRot;
    t4 = q.xyxy * wFreq + tw.xxyy;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    t = 1. - sqrt (v4.xz * v4.yw);
    t *= t;
    h += wAmp * dot (t, t);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return h;
}

float WaveRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 50; j ++) {
    p = ro + s * rd;
    h = p.y - WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (6., h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 6; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - WaveHt (p.xz));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 WaveNf (vec3 p, float d)
{
  vec2 e;
  e = vec2 (max (0.1, 5e-5 * d * d), 0.);
  return normalize (vec3 (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy), WaveHt (p.xz + e.yx)), e.x).xzy);
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qq, w;
  float dMin, d, fy, fz, gz, s, rRig, rSpar, yLim, zLim;
  rRig = 0.02;
  rSpar = 0.05;
  p.yz = Rot2D (p.yz, -0.4 * shipConf.z);
  p.xy = Rot2D (p.xy, 6. * shipConf.y);
  p.y -= shipConf.x + 4.1 * szFac;
  p /= szFac;
  dMin = dstFar / szFac;
  fy = 1. - 0.07 * p.y;
  fz = 1. - 0.14 * step (1., abs (p.z));
  zLim = abs (p.z) - 4.5;
  q = p;
  d = zLim;
  q.z = mod (q.z + 1.4, 2.8) - 1.2;
  d = max (d, PrCapsDf ((q - vec3 (0., 3.7 * (fz - 1.), 0.)).xzy, 0.1 * fy, 3.7 * fz));
  DMINQ (idMast);
  q = p;
  yLim = abs (q.y - 0.2 * fz) - 3. * fz;
  qq = q;
  qq.y = mod (qq.y - 3.3 * (fz - 1.), 2. * fz) - fz;
  qq.z = mod (qq.z + 1.4, 2.8) - 1.4 + 0.1 * fz;
  d = max (max (min (d, PrCylDf (vec3 (qq - vec3 (0., 0.05 * fy * fz, 0.1 * fz - 0.23)).xzy,
     0.15 * fy, 0.11 * fy * fz)), yLim), zLim);
  DMINQ (idMast);
  d = max (max (PrCapsDf (qq.yzx, 0.05, 1.23 * fy * fz), yLim), zLim);
  DMINQ (idSparT);
  q = p;
  d = min (d, min (PrEECapsDf (q, vec3 (0., -3.5, 4.3), vec3 (0., -2.6, 6.7), rSpar),
     PrEECapsDf (q, vec3 (0., -4., 4.1), vec3 (0., -2.9, 6.), rSpar)));
  d = min (d, min (PrEECapsDf (q, vec3 (0., -1.2, -3.), vec3 (0., -0.5, -4.5), rSpar),
     PrEECapsDf (q, vec3 (0., -2.7, -3.), vec3 (0., -2.7, -4.5), rSpar)));
  DMINQ (idSparL);
  q = p;
  qq = q;
  qq.y = mod (qq.y - 3.1 * (fz - 1.), 2. * fz) - fz;
  qq.z = mod (qq.z + 1.4, 2.8) - 1.4 + 0.2 * (fz - abs (qq.y)) * (fz - abs (qq.y)) - 0.1 * fz;
  d = max (max (max (PrBoxDf (qq, vec3 ((1.2 - 0.07 * q.y) * fz, fz, 0.01)),
     min (qq.y, 1.5 * fy * fz - length (vec2 (qq.x, qq.y + 0.9 * fy * fz)))),
     abs (q.y - 3. * (fz - 1.)) - 2.95 * fz), - PrBox2Df (qq.yz, vec2 (0.01 * fz)));
  d = max (d, zLim);
  DMINQ (idSailT);
  q = p;
  q.z -= -3.8;  q.y -= -1.75 - 0.2 * q.z;
  d = PrBoxDf (q, vec3 (0.01, 0.9 - 0.2 * q.z, 0.6));
  DMINQ (idSailA);
  q = p;
  q.yz -= vec2 (-1., 4.5);
  w = vec3 (1., q.yz);
  d = max (max (max (abs (q.x) - 0.01, - dot (w, vec3 (2.3, 1., -0.35))),
     - dot (w, vec3 (0.68, -0.74, -1.))), - dot (w, vec3 (0.41, 0.4, 1.)));
  DMINQ (idSailF);
  q = p;
  d = zLim;  
  gz = (q.z - 0.5) / 5. + 0.3;
  gz *= gz;
  gz = 1.05 * (1. - 0.45 * gz * gz);
  q.x = abs (q.x);
  q.z = mod (q.z + 1.4, 2.8) - 1.4;
  d = max (d, min (PrEECapsDf (q, vec3 (1.05 * gz, -3.25, -0.5), vec3 (1.4 * fz, -2.95, -0.05), 0.7 * rRig),
     PrEECapsDf (vec3 (q.xy, abs (q.z + 0.2) - 0.01 * (0.3 - 2. * q.y)), vec3 (gz, -3.2, 0.),
     vec3 (0.05, -0.9 + 2. * (fz - 1.), 0.), rRig)));
  q = p;
  d = min (d, PrEECapsDf (q, vec3 (0., -3., -4.45), vec3 (0., -2.7, -4.5), 0.8 * rRig));
  d = min (min (d, min (PrEECapsDf (q, vec3 (0., 2.45, 2.65), vec3 (0., -2.7, 6.5), rRig),
     PrEECapsDf (q, vec3 (0., 2.5, 2.65), vec3 (0., -3.2, 4.9), rRig))),
     PrEECapsDf (q, vec3 (0., 2.6, -3.), vec3 (0., -0.5, -4.5), rRig));
  q.x = abs (q.x);
  d = min (d, PrEECapsDf (q, vec3 (0.65, -3.5, 3.5), vec3 (0.05, -2.7, 6.4), rRig));
  s = step (1.8, q.y) - step (q.y, -0.2);
  d = min (min (d, min (PrEECapsDf (q, vec3 (0.95, 0.4, 2.7) + vec3 (-0.1, 1.7, 0.) * s,
     vec3 (0.05, 1.1, -0.15) + vec3 (0., 2., 0.) * s, rRig),
     PrEECapsDf (q, vec3 (1.05, 1., -0.1) + vec3 (-0.1, 2., 0.) * s,
     vec3 (0.05, 0.5, -2.95) + vec3 (0., 1.7, 0.) * s, rRig))),
     PrEECapsDf (q, vec3 (0.95, 0.4, -2.9) + vec3 (-0.1, 1.7, 0.) * s,
     vec3 (0.05, 0.9, -0.25) + vec3 (0., 2., 0.) * s, rRig));
  DMINQ (idRig);
  q = p;
  q.yz -= vec2 (3.4, 0.18);
  d = PrBoxDf (q, vec3 (0.01, 0.2, 0.3));
  DMINQ (idFlag);
  q = p;
  d = zLim;
  q.z = mod (q.z + 1.4, 2.8) - 1.4;
  q.yz -= vec2 (-3.4, -0.4);
  d = max (d, PrBoxDf (q, vec3 (0.3, 0.1, 0.5)));
  DMINQ (idStruc);
  q = p;
  q.x = abs (q.x);
  q.yz -= vec2 (-3.8, 0.5);
  fz = q.z / 5. + 0.3;
  fz *= fz;
  fy = 1. - smoothstep (-1.3, -0.1, q.y);
  gz = smoothstep (2., 5., q.z);
  bDeck = vec3 ((1. - 0.45 * fz * fz) * (1.1 - 0.5 * fy * fy) *
     (1. - 0.5 * smoothstep (-5., -2., q.y) * smoothstep (2., 5., q.z)),
     0.78 - 0.8 * gz * gz - 0.2 * (1. - smoothstep (-5.2, -4., q.z)), 5. * (1. + 0. * 0.02 * q.y));
  d = min (PrBoxDf (vec3 (q.x, q.y + bDeck.y - 0.6, q.z), bDeck),
     max (PrBoxDf (q - vec3 (0., 0.72, -4.6), vec3 (bDeck.x, 0.12, 0.4)),
     - PrBox2Df (vec2 (abs (q.x) - 0.4, q.y - 0.65), vec2 (0.2, 0.08))));
  d = max (d, - PrBoxDf (vec3 (q.x, q.y - 0.58 - 0.1 * fz, q.z), vec3 (bDeck.x - 0.07, 0.3, bDeck.z - 0.1)));
  q = p;
  d = max (d, - max (PrBox2Df (vec2 (q.y + 3.35, mod (q.z + 0.25, 0.5) - 0.25), vec2 (0.08, 0.1)),
     abs (q.z + 0.5) - 3.75));
  DMINQ (idHull);
  q = p;
  d = PrBoxDf (q + vec3 (0., 4.4, 4.05), vec3 (0.03, 0.35, 0.5));
  DMINQ (idRud);
  return 0.7 * dMin * szFac;
}

void EvalShipConf ()
{
  vec4 h;
  vec2 e;
  e = vec2 (2. * szFac, 0.);
  h = vec4 (WaveHt (- e), WaveHt (e), WaveHt (- e.yx), WaveHt (e.yx));
  shipConf = vec3 (0.25 * dot (h, vec4 (1.)), vec2 (h.xz - h.yw) / (2. * e.x));
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 80; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

void ShipCol (out vec4 col4, out vec2 vf)
{
  vec2 cg;
  vf = vec2 (0.);
  if (idObj == idHull) {
    if (abs (qHit.x) < bDeck.x - 0.08 && qHit.y > -3.6 && qHit.z > - bDeck.z + 0.62) {
      col4 = vec4 (0.5, 0.3, 0., 0.1) * (0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02, mod (8. * qHit.x, 1.)));
    } else if (qHit.y > -4.) {
      col4 = vec4 (0.7, 0.5, 0.1, 0.1);
      if (abs (qHit.z - 4.) < 0.25 && abs (qHit.y + 3.55) < 0.05) col4 *= 1.2;
      else if (qHit.z < -4. && abs (qHit.x) < 0.84 && abs (qHit.y + 3.62) < 0.125) {
        cg = step (0.1, abs (fract (vec2 (6. * qHit.x, 8. * (qHit.y + 3.62)) + 0.5) - 0.5));
        if (cg.x * cg.y == 1.) col4 = vec4 (0.8, 0.8, 0.2, -1.);
        else col4 *= 0.8;
      } else {
        col4 *= 0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.02, mod (8. * qHit.y, 1.));
        vf = vec2 (64., 0.3);
      } 
    } else if (qHit.y > -4.05) {
      col4 = vec4 (0.8, 0.8, 0.8, 0.1);
    } else {
      col4 = vec4 (0.3, 0.2, 0.1, 0.);
      vf = vec2 (64., 2.);
    }
  } else if (idObj == idRud) {
    col4 = vec4 (0.5, 0.3, 0., 0.);
  } else if (idObj == idStruc) {
    col4 = vec4 (0.4, 0.3, 0.1, 0.1);
    if (max (abs (qHit.x), abs (qHit.z + 0.22)) < 0.2) {
      cg = step (0.1, abs (fract (vec2 (10. * vec2 (qHit.x, qHit.z + 0.22)) + 0.5) - 0.5));
      if (cg.x * cg.y == 1.) col4 = vec4 (0.8, 0.8, 0.2, -1.);
    }
  } else if (idObj == idSailT) {
    qHit.x *= (1. + 0.07 * qHit.y) * (1. + 0.14 * step (1., abs (qHit.z)));
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.02, mod (4. * qHit.x, 1.)));
    if (abs (qHit.z) < 0.2 && abs (abs (length (qHit.xy - vec2 (0., 0.3)) - 0.35) - 0.15) < 0.07)
       col4 *= vec4 (1., 0.2, 0.2, 1.);
  } else if (idObj == idSailA) {
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.02, mod (5. * qHit.z, 1.)));
  } else if (idObj == idSailF) {
    col4 = vec4 (1., 1., 1., 0.) * (0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.02,
       mod (2.95 * qHit.y + 4. * qHit.z - 0.5, 1.)));
  } else if (idObj == idFlag) {
    col4 = vec4 (1., 1., 0.5, 0.1);
    if (abs (abs (length (qHit.yz) - 0.1) - 0.04) < 0.02) col4 *= vec4 (1., 0.2, 0.2, 1.);
  } else if (idObj == idMast) {
    col4 = vec4 (0.7, 0.4, 0., 0.1);
    if (length (qHit.xz) < 0.16 * (1. - 0.07 * qHit.y)) col4 *= 0.6 + 0.4 *
       SmoothBump (0.03, 0.97, 0.01, mod (2. * qHit.y / (1. + 0.14 * step (1., abs (qHit.z))), 1.));
    if (qHit.y > 3.65) col4 = vec4 (1., 0., 0., -1.);
  } else if (idObj == idSparT) {
    qHit.x *= (1. + 0.07 * qHit.y) * (1. + 0.14 * step (1., abs (qHit.z)));
    col4 = vec4 (0.7, 0.4, 0., 0.1) *  (0.6 + 0.4 * SmoothBump (0.08, 0.92, 0.01, mod (4. * qHit.x, 1.)));
  } else if (idObj == idSparL) {
    col4 = vec4 (0.7, 0.4, 0., 0.1);
    if (qHit.z > 6.65) col4 = vec4 (1., 1., 0.3, -1.);
  } else if (idObj == idRig) {
    col4 = vec4 (0.2, 0.15, 0.1, 0.);
    vf = vec2 (32., 0.5);
  }
}

vec3 SkyBg (vec3 rd)
{
  return mix (vec3 (0.45, 0.45, 0.6), vec3 (0.2, 0.2, 0.9), rd.y);
}

vec3 SkyHrzCol (vec3 ro, vec3 rd)
{
  vec3 p, q, dp, col;
  vec2 cSum;
  float cloudLo, cloudRngI, att, a, sd;
  const float nLay = 30.;
  cloudLo = 300.;  
  cloudRngI = 1./200.;  
  a = atan (rd.z, rd.x) / pi;
  if (rd.y < 0.02 * Fbm1 (64. * a + 1.) + 0.005) col = mix (vec3 (0.12, 0.14, 0.12),
     vec3 (0.2, 0.2, 0.23), 0.5 + 0.5 * dot (normalize (rd.xz), - normalize (sunDir.xz))) *
     (1. - 0.2 * Fbm2 (64. * vec2 (a, rd.y)));
  else {
    sd = max (dot (rd, sunDir), 0.);
    ro += cloudDisp;
    p = ro;
    p.xz += (cloudLo - p.y) * rd.xz / rd.y;
    p.y = cloudLo;
    dp = rd / (cloudRngI * rd.y * (2. - rd.y) * float (nLay));
    cSum = vec2 (0.);  
    att = 0.;
    for (float s = 0.; s < nLay; s ++) {
      q = p + s * dp;
      att += 0.04 * max (cloudDens - Fbm3 (0.0035 * q), 0.);
      cSum += att * (1. - cSum.y) * vec2 ((q.y - cloudLo) * cloudRngI, 1.);
      if (cSum.y > 1.) break;
    }
    cSum.x += 0.5 * min ((1. - cSum.y) * pow (sd, 3.), 1.) + 0.05;
    col = clamp (mix (SkyBg (rd) + vec3 (1., 1., 0.9) * (pow (sd, 8.) + pow (sd, 1024.)),
       vec3 (1.) * 2.8 * cSum.x, cSum.y), 0., 1.);
    col = mix (col, SkyBg (rd), pow (1. - rd.y, 32.));
  }
  return col;
}

vec3 FogCol (vec3 col, vec3 ro, vec3 rd, float dLim)
{
  float d, fFac, f, fn;
  fFac = 1.;
  ro += fogDisp;
  d = 1.;
  fn = fogDens * Fbm3 (0.5 * (ro + d * rd));
  for (int j = 0; j < 8; j ++) {
    d += 0.5 + 0.5 * d;
    f = fn;
    fn = fogDens * Fbm3 (0.5 * (ro + d * rd));
    col = mix (col, vec3 (0.8, 0.8, 0.75) * (1. - clamp (f - fn, 0., 1.)),
       clamp (fFac * f * smoothstep (0.9 * d, 2.3 * d, dLim), 0., 1.));
    fFac *= 1.25;
    if (d > dLim) break;
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo, rdo, row, vnw;
  vec2 vf;
  float dstObj, dstWat, dstFog, s, tCyc;
  bool watRefl, doLtn;
  tWav = 0.5 * tCur;
  tCyc = mod (0.02 * tCur, 1.);
  cloudDens = 0.4 + 0.4 * SmoothBump (0.2, 0.8, 0.15, tCyc);
  cloudDisp = tCur * vec3 (-15., 0., 5.);
  fogDisp = tCur * vec3 (0.2, 0., 0.2);
  fogDens = 0.25 * SmoothBump (0.35, 0.65, 0.15, tCyc);
  doLtn = (abs (tCyc - 0.65) < 0.1);
  watRefl = false;
  col4 = vec4 (0.);
  if (rd.y < 0.) dstWat = WaveRay (ro, rd);
  else dstWat = dstFar;
  szFac = 1.;
  EvalShipConf ();
  dstObj = ObjRay (ro, rd);
  roo = ro;
  rdo = rd;
  dstFog = min (min (dstObj, dstWat), dstFar);
  col = vec3 (0.);
  if (dstWat < min (dstObj, dstFar)) {
    ro += dstWat * rd;
    row = ro;
    vnw = VaryNf (row, WaveNf (row, dstWat), 0.1);
    rd = reflect (rd, vnw);
    dstObj = ObjRay (ro, rd);
    watRefl = true;
  }
  col = vec3 (0.22, 0.25, 0.33);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    ShipCol (col4, vf);
    if (vf.x > 0.) vn = VaryNf (vf.x * qHit, vn, vf.y);
    if (col4.a >= 0.) col = col4.rgb * (0.2 + 0.2 * max (vn.y + 0.1, 0.) + 
       0.2 * max (dot (vec3 (- sunDir.x, 0., - sunDir.z), vn), 0.) +
       0.6 * max (dot (vn, sunDir), 0.)) +
       col4.a * pow (max (dot (normalize (sunDir - rd), vn), 0.), 128.);
    else col = col4.rgb * (0.5 - 0.5 * dot (vn, rd));
  } else if (dstWat < dstFar) {
    s = WaveHt (row.xz);
    col *= mix (1. + 0.1 * s, 1.5, step (0.75, s + 0.4 * (Fbm2 (128. * row.xz) - 0.5)));
    vnw = VaryNf (4. * row, WaveNf (row, dstWat), 0.2);
  } else if (rdo.y < 0.) {
    row = roo - (roo.y / rdo.y) * rdo + vec3 (0.1 * tCur, 0., 0.);
    vnw = VaryNf (4. * row, vec3 (0., 1., 0.), 0.2);
    watRefl = true;
  } else {
    col = SkyHrzCol (roo, rdo);
  }
  if (watRefl) col = mix (0.8 * col, 0.25 * vec3 (1., 1., 0.8) * SkyHrzCol (row, reflect (rdo, vnw)),
     smoothstep (0.5, 1., 1. + rdo.y));
  if (fogDens > 0. && (col4.a >= 0. || watRefl)) col = FogCol (col, roo, rdo, dstFog);
  if (doLtn && col4.a >= 0.) {
    s = SmoothBump (0.6, 0.8, 0.05, Fbm1 (8. * tCur));
    col = HsvToRgb (RgbToHsv (col) * vec3 (1., 1. - 0.6 * s, (watRefl ? 0.4 + 0.6 * s : 0.4 + 1.1 * s)));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd;
  vec2 canvas, uv;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 4. * pi * (2. * mod (floor (0.004 * tCur), 2.) - 1.) *
     SmoothBump (0.25, 0.75, 0.25, mod (0.004 * tCur, 1.));
  el = 0.17 * pi - 0.12 * pi * cos (0.07 * pi * tCur);
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
    el = clamp (el, 0.05 * pi, 0.45 * pi);
  }
  zmFac = 5.2 - 2. * el;
  ro = 30. * vec3 (cos (el) * cos (az), sin (el), cos (el) * sin (az));
  vd = normalize (vec3 (0., 3.5, 2.) - ro);
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (- vd.y * vd.x, 1. - vd.y * vd.y, - vd.y * vd.z) / sqrt (1. - vd.y * vd.y), vd);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  sunDir = normalize (vec3 (1., 1., 1.));
  dstFar = 200.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrEECapsDf (vec3 p, vec3 v1, vec3 v2, float r)
{
  vec3 s, t;
  s = p - v1;
  t = v2 - v1;
  return length (s - clamp (dot (s, t) / dot (t, t), 0., 1.) * t) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

vec3 RgbToHsv (vec3 c)
{
  vec4 p, q;
  float d;
  const float e = 1.e-10;
  p = mix (vec4 (c.bg, vec2 (-1., 2./3.)), vec4 (c.gb, vec2 (0., -1./3.)), step (c.b, c.g));
  q = mix (vec4 (p.xyw, c.r), vec4 (c.r, p.yzx), step (p.x, c.r));
  d = q.x - min (q.w, q.y);
  return vec3 (abs (q.z + (q.w - q.y) / (6. * d + e)), d / (q.x + e), q.x);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define M_PI 3.14159266

#define ROTATIONS_PER_SECOND (1.0 / 37.345)

#define ZOOMS_PER_SECOND (1.0 / 23.384)
#define MAX_ZOOM 5.0
#define MIN_ZOOM 0.8

#define DX_PER_SECOND (1.0 / 25.894)
#define DY_PER_SECOND (1.0 / 17.127)
#define MAX_DX 0.3
#define MAX_DY 0.3

float swirl(in vec2 p, in int m, in float s)
{
    float d;

    if (p == vec2(0.0,0.0)) {
        return 0.0;
    }

    d = length(p);

    return cos(float(m)*(atan(p.y, p.x) + s*d))*0.5 + 0.5;
}

float sink(in vec2 p, in float r)
{
    float d = length(p);

    d = d/r - 1.0;
    d = max(d, 0.0);

    return 1.0 - 1.0/exp(d);
}

float ripple(in vec2 p, in float r)
{
    float d = length(p);

    return cos(d/r*M_PI)*0.5 + 0.5;
}

vec2 bulge(in vec2 p, in vec2 c, float r, float e)
{
    vec2 v;
    float d;

    v = p - c;
    d = length(v);
    if (d == 0.0) {
        return p;
    }

    v /= d;

    d = pow(d/r, e)*r;
    return c + v * d;
}

float fn(in vec2 p)
{
    float f;
    float a;
    vec2 d;

    p.y = 1.0 - p.y;

    d = p;

    p = bulge(p, vec2(0.5, 0.7), 0.6, 3.0);

    a = 0.1;
    f = ripple(d - vec2(0.4, 0.4), a)
        + ripple(d - vec2(0.8, 0.1), a)
        + ripple(d - vec2(0.9, 0.3), a)
        + ripple(d - vec2(0.3, 0.8), a)
        + ripple(d - vec2(0.7, 0.1), a)
        + ripple(d - vec2(0.4, 0.5), a);
    f /= 6.0;

    a *= sink(d - vec2(0.5, 0.7), 0.4);
    if (a > 0.0) {
        p.x += cos(f*2.0*M_PI*2.0)*a;
        p.y += sin(f*2.0*M_PI*2.0)*a;
    }

    a = 0.01;
    f = ripple(p - vec2(0.2, 0.4), a)
        + ripple(p - vec2(0.4, 0.2), a)
        + ripple(p - vec2(0.6, 0.8), a)
        + ripple(p - vec2(0.1, 0.2), a)
        + ripple(p - vec2(0.9, 0.2), a)
        + ripple(p - vec2(0.2, 0.3), a);
    f /= 6.0;

    p.x += cos(f*2.0*M_PI*2.0)*a;
    p.y += sin(f*2.0*M_PI*2.0)*a;

    f = pow(swirl(p - vec2(0.5, 0.7), 3, 12.0), 0.7);
    f *= sink(p - vec2(0.5, 0.7), 0.005);

    return f;
}

vec2 rotate(in vec2 p, in vec2 origin, in float rad)
{
    float s = sin(rad);
    float c = cos(rad);

    mat2 m = mat2(c, -s, s, c);

    return m * (p - origin) + origin;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.y;
    uv.x -= iResolution.x / (4.0 * iResolution.y);

    // Rotate around the center of interest at the desired rate.
    vec2 rotateAround = vec2(0.5, 0.5);
    uv = rotate(uv, rotateAround, iTime * 2.0 * M_PI * ROTATIONS_PER_SECOND);

    // Get a pair of sine waves with the desired periods and ranges for translation.
    vec2 d;
    d.x = sin(iTime * 2.0 * M_PI * DX_PER_SECOND) * MAX_DX;
    d.y = sin(iTime * 2.0 * M_PI * DY_PER_SECOND) * MAX_DY;
    uv += d;

    // Get a sine wave with the desired period on the range [0,1], starting zoomed out.
    float zoom = (-cos(iTime * 2.0 * M_PI * ZOOMS_PER_SECOND) + 1.0) / 2.0;
    uv /= mix(MIN_ZOOM, MAX_ZOOM, zoom);

    float f = fn(uv);

    fragColor = vec4(f, f, f, 1.0);
}

