//map functions
vec2 map(vec3 p){
    vec2 r=vec2(1.);

    cmp(obj1(p,vec3(0,0,0)),r);

    cmp(plane(p),r);

    return r;
}

//compute the normal
vec3 norm(vec3 p,float dis){
  vec2 e = vec2(0,1)/dis;
  float m= map(p).x;
  return -normalize(
      vec3(
          m-map(p+e.yxx).x,
          m-map(p+e.xyx).x,
          m-map(p+e.xxy).x
      )
  );  
}

// compute the soft shadow
float shadow(vec3 o,vec3 d,float mint,float maxt,float k){
    float t=0.;
    vec2 h=vec2(0);
    float r=1.;
    for(float i=0.;i<=64.;i++){
       h=map(o+t*d);
       r=min(r,h.x*k/t);
       t+=clamp(h.x,mint,0.3);
       if(t>maxt||h.x<mint){
          break;
       }

    }
    return r;
}

// based on Inigo Quilez's fog method
void fog(inout vec3 col,float dis,vec3 ro,vec3 rd,vec3 ld){
   float a=0.2;
   float b=0.14;
   float fv = a * exp(-ro.y*b) * (1.-exp( -dis*rd.y*b ))/rd.y;
   float sv =ld.y;//clamp(dot(-rd,ld),ld.y,ld.y);

   vec3 scol=vec3(0.0,0.5,0.9);  
   vec3 fogc= vec3(0.3,0.8,1);
   fogc =mix(fogc,scol,sv); 
   fv*=(1.-exp(-0.004*dis*dis));
   col=mix(col,fogc,fv);
}

void sun(inout vec3 col,vec3 p,vec3 lp,float t){
   p-=lp;

   float v = 0.3 /length(p.xy);

   v=t>40.?v:0.;

   if(t>=40.){
       col=vec3(0.3,0.3,1.)*clamp(lp.y,0.,1.);
   }
   //col+=vec3(v);

}

// ray tracer
vec2 rayHit(Ray r){
    float t=1.;
    vec2 h=vec2(0);

    for(float i=0.;i<FAR;i++){
        h=map(r.o+t*r.d);
        if(h.x<0.0001||t>60.){
            break;
        }
        t+=h.x;
    }
    return vec2(t,h.y);
}

// do light calculation
vec2 doLight(inout Ray r,float v,vec3 lp){

    vec3 p = r.o+v*r.d;
    vec3 ld = normalize(p-lp);
    vec3 n=norm(p,v);

    float nl = clamp(dot(n,-ld),0.0,1.);
    float rl =clamp(dot(r.d,normalize(reflect(normalize(lp),n))),0.,1.);
    float sl = shadow(lp,ld,0.0001,length(lp-p)*0.95,128.);
    float fc =clamp(sl,0.1,1.)*nl+0.1+rl*rl;

    return vec2(fc,v);
}

//set the camera 
void setCamera(inout Ray r,vec3 pos,vec3 tar,vec2 uv){
    vec3 look= normalize(tar);
    vec3 right = normalize( cross(vec3(0.0,1.0,0.0),look) );
    vec3 up = normalize( cross(look,right));
    vec3 rd = normalize(uv.x*right + uv.y*up + 0.6*look );
    r.o=pos;
    r.d=rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-vec2(0.5);
    float as =iResolution.x/iResolution.y;
    uv.x*=as;

    vec3 lp = vec3(10,10.*cos(iTime*0.2),10); 

    Ray ray;

    vec3 pos =vec3(0,2,-20);
    vec3 dir =vec3(0,-PI*0.03,1.);
    pos.xz*=rot2D(iTime*PI*0.1);
    dir.xz*=rot2D(iTime*PI*0.1);

    setCamera(ray,pos,dir,uv);

    vec2 v = rayHit(ray);
    vec2 res =doLight(ray,v.x,lp);
    vec3 col =vec3(res.x);

    textureMap(col,ray.o+ray.d*v.x,int(v.y));
    sun(col,ray.o+ray.d*v.x,lp,res.y);

    fog(col,v.x,ray.o,ray.d,normalize(-lp));
    col=pow(col,vec3(1.5));

	fragColor = vec4(col,1.0);
}

#define PI 3.14159
#define TAU (2. * PI)
#define HPI (PI / 2.)

#define SMOOTH(r,p) smoothstep(-.004, .004, p-(r)) 

float diskDf(vec2 p,float r)
{
    return length(p) - r;
}

float circleDf(vec2 p,float r, float t)
{
    float ht = t/2.;
    return max(diskDf(p,r + ht), - diskDf(p,r - ht));
}

//from https://www.shadertoy.com/view/4sSyDd
vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}

//from https://www.shadertoy.com/view/Ml33W7
float rectDist(vec2 p, vec2 dim) {
    return max(abs(p).x - dim.x*0.5, abs(p).y - dim.y*0.5);
}

mat2 rot(float a)
{
    float c = cos(a); float s = sin(a);
    return mat2(c,-s,s,c);
}

float bounce(float p, float b)
{
    return .5 - cos(p * PI) * .5 + sin(p * PI) * p * b ;
}

float multiplexer(float channel, float nbChannel, float t, float b)
{
    float ft = floor(t);
    float mt = t- ft;
    mt *= nbChannel;
    channel = clamp(mt - channel,0.,1.);
    channel = bounce(channel, b) ;
    return ft + channel;
}

float multiplexer(float channel,float nbChannel,float t)
{
    return multiplexer(channel, nbChannel, t, .0);
}

float noise(vec2 p)
{
    return fract(dot(sin(p.x * 123.12)*142.,cos(p.y *34.95)*165.47));
}

float sigil(vec2 p, float id)
{
    p.x = floor(p.x * 4.) / 4.;
    p.y = floor(p.y * 5.) / 5.;

    float f = step(.1,p.x) * step(p.x, .9) * step(.1,p.y) * step(p.y, .9);
    f *= step(noise(p + id), .5);

    return f;
}

float sigilSheet(vec2 p, float seedDelta)
{
    float rep = .1;

    vec2 ruv = mod(p,rep) / rep;
    vec2 idUV = floor(p/rep) * rep;

    float no = noise(idUV + rep*.5);
    float nbChar = 30.;
    no = floor(no * nbChar) / nbChar; 

    return sigil(ruv, no + seedDelta);
}

vec2 triChecker(vec2 p, float s)
{
    p.x += p.y * .5 ;
    vec2 m = mod(p,s); 
    vec2 base = p - m;
    p = m / s; 

    base.x *= 2. + step(p.x,p.y);

    return base;
}

#define NB_CHANNEL 31.
#define CHANNEL_DURATION .5
#define TOTAL_DURATION (NB_CHANNEL * CHANNEL_DURATION)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x /= iResolution.y / iResolution.x;

    float time = mod(iTime,TOTAL_DURATION) / TOTAL_DURATION;

    float t0 = multiplexer(0.,NB_CHANNEL,time, .1);
    float t3 = multiplexer(3.,NB_CHANNEL, time, .1);
    float t5 = multiplexer(5.,NB_CHANNEL, time);
    float t12 = multiplexer(12.,NB_CHANNEL, time);
    float t13 = multiplexer(13.,NB_CHANNEL,time);
    float t14 = multiplexer(14.,NB_CHANNEL,time,.2);
    float t16 = multiplexer(16.,NB_CHANNEL, time, .2);
    float t17 = multiplexer(17.,NB_CHANNEL,time);
    float t17_5 = multiplexer(17.5,NB_CHANNEL,time);
    float t18 = multiplexer(18.,NB_CHANNEL,time);
    float t19 = multiplexer(19.,NB_CHANNEL,time);
    float t21 = multiplexer(21.,NB_CHANNEL,time, .3);
    float t22 = multiplexer(22.,NB_CHANNEL,time);
    float t24 = multiplexer(24., NB_CHANNEL, time);
    float t26 = multiplexer(26., NB_CHANNEL, time);
    float t28 = multiplexer(28.,NB_CHANNEL,time, .25);
    float t30 = multiplexer(30.,NB_CHANNEL, time, .1);

    float distor = 0.;
    distor += sin(t16 *  PI);
    uv.x += sin(uv.y * iResolution.y) * .075 * distor;

    vec2 mirrored;

    float r1 = .25 + 2. * (1. - t0) + 2. * t28;
    float f = circleDf(uv, r1, .01);


    float raSpeed = .15;
    float a = atan(uv.y,uv.x);
    float an = (a + PI) / TAU;
    float aa = mod(an + .25 + time * raSpeed, 1.);
    float r3 = .32;
   	float st = sin(aa * TAU * 24.);
    st = SMOOTH(st,0.);

    an = mod(an + .25,1.);
    st *= step(1. - an, t13) * step(t26, 1. - an );

    float c3 = circleDf(uv,r3, st * .03 - .03);

    f = min(c3,f);
    float r4 = .45;
    float c4 = circleDf(uv,r4, st * .03 - .03);
    f = min(c4,f);

    float dist = length(uv);
    vec2 po = vec2(an, dist * .48) * 3.; po.y += .01;


    float tSigil = floor(time * 400. * step(t12, .0));

    float si =  sigilSheet(po, tSigil);

    si *= step(sin(t12 * TAU * 4.), .0);

    si *=  (1. - circleDf(uv,.385,.05));
    si *=  t5  * step(t26, 1. - an );

    f = min(f, 1. - si);

    float nbMirror = 3.;
    mirrored = modA(uv * rot(t21 * PI - t14 * HPI) ,nbMirror);
    float quadrant = floor(an * nbMirror);
    float tLine = multiplexer(4. + quadrant * .1 ,NB_CHANNEL, time);
    mirrored.x -=  .2 + (1. - tLine) * 2.;


    float line = rectDist(mirrored, vec2(.03 - t22 *.03,-.005));
    f = min(f,line);


    float top = .4; float bot = -.4;
    float height = top - bot;

    vec2 siUv = uv; ; siUv.x -= .025;
    si =  sigilSheet(siUv, 0.);
    float side = 1. -  si  * step(-.57,uv.x) * step(uv.x,-.47) * step(uv.y, .4) * step(-.4,uv.y);


    side = max(side, step(t17 * height, uv.y - bot));
    side = max(side , sin(t18 * TAU * 4.));

    f = min(f,side);

    siUv.x += .075;
    si =  sigilSheet(siUv, 8.);
    side = 1. -   si  * step(.47,uv.x) * step(uv.x,.57) * step(uv.y, .4) * step(-.4,uv.y);

    side = max(side, step(t17_5 * height, uv.y - bot));
    side = max(side , sin(t18 * TAU * 4.));

    f = min(f,side);

    float r2 = .15 + 2. * (1. - t3) + 2. * t30;
    f =  max(step(dist, r2),f);
    f = min(circleDf(uv, r2, .015), f);

    mirrored = modA(uv * rot( -t12 * PI + HPI - t30 * PI),3. + t17 * 3. - t28 * 2. - t24);

    float li = distance(mirrored.x, .03 + (t12 - t24) * .045) * 2.;

    f = min(f, li);

    f = SMOOTH(f,.01);


    uv = fragCoord.xy / iResolution.xy;
    uv -= .5;
    uv.x /= iResolution.y / iResolution.x;
    vec2 tri = triChecker(uv + iTime * .01, .07);
    f += noise(tri) * .1;

    fragColor = vec4(mix(vec4(0.),vec4(1.,1.,.7,1.)*.75,f));
}

float rad (float deg) {
    return deg * 0.01745329;
}

vec2 rot (vec2 coord, vec2 centre, float angle) {
    mat2 rm = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    return (coord - centre) * rm + centre;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord = rot(fragCoord, iMouse.xy, distance(fragCoord, iMouse.xy) / 100. + iTime / 100. - cos(iTime * 2. + distance(fragCoord, iMouse.xy) / 200.));

	vec2 uv = fragCoord / iResolution.xy;
    uv.y *= 6.;
    uv.y -= 3.;

    uv = rot(uv, vec2(0.5, 0.5), -distance(uv, iMouse.xy / iResolution.xy) / 10.);

    vec2 sinmv = vec2(sin(iTime * 10.) * 10., cos(iTime * 10.) * 10.);
    vec2 wavy = vec2(sin(fragCoord.x / 3. + iTime * 20.) * (10. + sin(iTime)), cos(fragCoord.y / 4. + iTime * 20.) * (10. - sin(iTime)));

    if (distance(iMouse.xy, fragCoord + sinmv - wavy) < 100.) uv /= 2.;
    else if (distance(iMouse.xy, fragCoord - sinmv + wavy) < 150.) uv *= 2.;

    float sx = uv.x * 6.28318530718 + iTime * 5.;

    float target =  (sin(sx * 1. + rad(90.)) * 1.);
    float target2 = (cos(sx * 1. - rad(90.)) * 1.);

    vec4 color = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 col2 = vec4(0.5+0.5*cos(iTime), -uv, 1.0);
    vec4 col3 = vec4(uv.x, 0.5-0.5*sin(iTime), -uv.y, 1.0);

	fragColor = color;
    if (uv.y < target) fragColor = mix(fragColor, col2, 0.5);
    if (uv.y > target2) fragColor = mix(fragColor, col3, 0.5);

    if (distance(iMouse.xy, fragCoord + wavy) < 50.) fragColor *= 2.;
    if (mod(distance(iMouse.xy + vec2(sin(fragCoord.x / 10.) * 20., cos(fragCoord.x / 10. * 20.)), fragCoord - wavy) + iTime * 50., 100.) > 20. * cos(iTime * 5.) + 40.) fragColor = 1. - fragColor;

    fragColor.r *= 3. * sin(iTime / 3.);
    fragColor.g *= 3. * cos(iTime / 2.);
    fragColor.b *= 3. * tan(iTime / 5.);
}

