#define float2 vec2
#define float3 vec4
#define float4 vec4
#define frac fract

#define speed 0.618
#define lineNumber 7.0
#define lineWidth 0.1

#define circlePatternColor float4(1.0,0.0,0.0,1.0)
#define squarePatternColor float4(0.2,1.0,0.6,0.0)
//#define backgroundColor float4(0.3,0.8,0.8,1.0)
#define backgroundColor float4(0.0,0.0,0.0,1.0)

float lerp(float a,float b,float v)
{
	//clamp to 0 and 1
	if(v>1.0) v=1.0;
	else if(v<0.0) v=0.0;
	return a*(1.0-v)+b*v;
}

vec4 lerp(vec4 a,vec4 b,float v)
{
	float red = lerp(a.r,b.r,v);
	float green = lerp(a.g,b.g,v);
	float blue = lerp(a.b,b.b,v);
	float alpha = lerp(a.a,b.a,v);
	return vec4(red,green,blue,alpha);
}

bool IsInRange(float value,float pos,float width)
{
	return (value>= pos) && (value<= pos+width);
}

float SquarePattern(float2 uv,float pos,float width)
{
    uv = uv*2.0 - 1.0;
	uv = abs(uv);
	//horizontal
	bool h = IsInRange(uv.x,pos,width) && (uv.y<pos+width);
	//vertical
	bool v= IsInRange(uv.y,pos,width)&&(uv.x<pos+width);

	if(h||v) return 1.0;
    return 0.0;
}

float CirclePattern(float2 uv,float pos,float width)
{
	uv = uv*2.0 - 1.0;
	float dis = length(uv);
	bool b= IsInRange(dis,pos,width);
	if(b) return 1.0;
	return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    uv.x*=iResolution.x/iResolution.y; 
    uv.x -= 0.35;

	float hz = iTime*0.5*speed;


	float circlePatternValue =0.0;
	float squarePatternValue =0.0;		   

	for(float index=0.0;index<lineNumber;index+=1.0)
	{
		squarePatternValue += SquarePattern(uv,frac(hz + index/lineNumber)*2.0,lineWidth);
	}

	for(float index=0.0;index<lineNumber;index+=1.0)
	{
		circlePatternValue += CirclePattern(uv,frac(hz + index/lineNumber)*2.0,lineWidth);
	}

	float4 finalSquareColor = squarePatternColor * squarePatternValue;
	float4 finalCircleColor = circlePatternColor * circlePatternValue;


	float4 finalPatternColor = lerp(finalSquareColor,finalCircleColor,0.618)*1.5; 

    float plus = circlePatternValue+squarePatternValue;

    float mul = circlePatternValue*squarePatternValue;

    float sub = circlePatternValue-squarePatternValue;

    float div = circlePatternValue/squarePatternValue;

    //try different operators
    fragColor = lerp(backgroundColor,finalPatternColor,plus);
    //fragColor = lerp(backgroundColor,finalPatternColor,mul);
    //fragColor = lerp(backgroundColor,finalPatternColor,sub);
    //fragColor = lerp(backgroundColor,finalPatternColor,div);
}

//Global Variables

//Spawning

int numOfParticles = 100;

float gSeed = 213.456;

//Velocity 

vec2 vMin = vec2(0.2,-0.25);
vec2 vMax = vec2(2.0,0.25);

float vScaleMin = 0.1;
float vScaleMax = 1.0;

//Life

float lifeMin = 0.8;
float lifeMax = 1.5;

//StartSpread

float stSpread = 0.1;


//SimTime
//Creates a looping segment of time to syncronize particle behaviours

float sTime()
{
//SDur = Sim Duration
	float sDur = lifeMax;
	return sDur*fract(iTime/sDur);
}


//generates a random value from a seed 

float rand(float seed)
{    
    return fract((seed*7.14376197)+fract(seed*2.5447876)+gSeed);
}


//Linear interpolators for float, vec2 and vec3

vec3 lerpV3(vec3 a, vec3 b, float alpha)
{
	return (a + alpha*(b-a));
}

vec2 lerpV2(vec2 a, vec2 b, float alpha)
{
	return (a + alpha*(b-a));
}

float lerpF(float a, float b, float alpha)
{
	return (a + alpha*(b-a));
}


// non-uniform interpolation
// allows x and y values to be altered independantly
vec2 nui(vec2 a, vec2 b, float alphaA, float alphaB)
{
    float x = (a.x + alphaA*(b.x-a.x));
    float y = (a.y + alphaB*(b.y-a.y));
    return vec2(x,y);
}


//PARTICLE
// calculate particle location and render as a radial gradient
// velocity x time/life plus start location = location

float particle(vec2 uv, float seed)
{

	// Random values for velocity
    float vRandA = fract(rand(seed)*32.456);
    float vRandB = fract(vRandA+fract(rand(seed))*12.678);
    float vRandC = fract(vRandB+fract(rand(seed))*24.395);

    //Rand Value for start time spread
    float stRand = fract(rand(seed)*121.124);

	//Random Value for life  
    float lRand = fract(rand(seed)*91.124);  

    //time
    float t = lifeMax*fract((sTime()/lifeMax)+lerpF(stSpread,0.0,stRand));
    //float t = sTime;

    float life= lerpF(lifeMin,lifeMax,lRand);

	//set particle location
    vec2 loc = vec2(0.0,0.0);     
    vec2 sLoc = vec2(0.2,0.5);


	//Set velocity to an intial random value
	//Randomly scales down the velocity to create bunching closer to the emitter
    vec2 v = nui(vMin,vMax,vRandA,vRandB);
    v = v*lerpF(vScaleMin,vScaleMax,vRandC); 
    v = v*life;

	//Makes y veclocity proportional to x velocity with some variance   
    v.y= v.y*((v.x/2.0)+0.5);

	//Generates location based on velcocity and current time    
    loc = v*fract(clamp(t/life,0.0,1.0))+sLoc; 

	//Generates radial gradient to represenet particle                           
    float p = length(uv-loc)/0.01;    
    p = 1.0-p;
    p = clamp(p,0.0,1.0);

	//Fades out particle over it's life                    
    float fade = fract(t/life);    
    fade = 1.0-fade;    
    p = p*fade;

	//inactivity mask = im
	//mask that hides particles that have completed their life 
    float im = step(t/life,1.0); 
    p = p*im;

    return p;
}




float emitter(vec2 uv)
{

	float p = 0.0;

	float lp = 0.0;

	float vi = 1.0;

	vec2 v = vec2(6.0,2.0);

    float seed = 1.0;

    // for each loop render another particle with a unique seed
	// lp = last particle
	// p = current particle
    for (int i = 0; i < 100; ++i) 
    {         
        vi = vi-0.1;
        seed = vi*1277.1423557;
//        v.y = 1.17;
        lp = particle(uv,seed); 
        p = p+lp;
    }

    p = clamp(p,0.0,1.0);
	return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    vec3 pixel = vec3(1.0,1.0,1.0);

    vec2 v = vec2(2.1,0.1);

    float p = 1.0;

    p = emitter(uv);

   	pixel.x = p;

   	pixel.y = p;     

    pixel.z = p;

	fragColor = vec4(pixel,1.0);
}

float myAngle(vec2 a, vec2 b){
  return atan(b.x - a.x, b.y - a.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	float mx = max(iResolution.x, iResolution.y);
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 center = vec2(0.5,0.5);
	float t = iTime * 10.0;
    float angle = myAngle(uv, center)*2.0;
    float dist = distance(uv, center)*300.0;
    float spiral = sin(t - angle - dist);  
    if(spiral > 0.0){
      uv.y += cos(iTime*1.0)/2.0;
      uv.x += sin(iTime*1.0)/2.0;
    	fragColor = vec4(spiral, uv*spiral, 1.0);
  }
}

