    //col = mix(col, vec3(.5, .7, 1)*2., smoothstep(0., .03, eDist2 - .5 + .02)*.7);

        // Subtle vignette.
    u = fragCoord/iResolution.xy;
    col *= pow(16.*u.x*u.y*(1. - u.x)*(1. - u.y) , .125)*.65 + .35;
