float sphere(vec3 p, float r) {
    return length(p) - r;
}

float room(vec3 p, vec3 size) {
    return min(min(min(p.x, size.x - p.x), min(p.y, size.y - p.y)), min(p.z, size.z - p.z));
}

vec2 sdf(vec3 p) {
    float s = min(sphere(p-vec3(.275,.45,0), 0.25), min(sphere(p-vec3(0,0,0), 0.25), sphere(p-vec3(.55,0,0), 0.25)));
    float r = room(p - vec3(-5, -2.5, -5), vec3(10, 5, 10));
    if (s < r) {
        return vec2(s, 0.0);
    } else {
        return vec2(r, 1.0);
    }
}

float ao(vec3 p, vec3 n) {
    float occ = 0.0;
    float epsilon = 0.1;

    vec3 u;
    if (abs(n.x) < abs(n.y) && abs(n.x) < abs(n.z)) {
        u = cross(n, vec3(1,0,0));
    } else if (abs(n.y) < abs(n.z)) {
        u = cross(n, vec3(0,1,0));
    } else {
        u = cross(n, vec3(0,0,1));
    }
    vec3 v = cross(n, u);

    occ += max(sdf(p + epsilon*n).x,0.0);
    occ += max(sdf(p + epsilon*(u)).x,0.0);
    occ += max(sdf(p + epsilon*(-u)).x,0.0);
    occ += max(sdf(p + epsilon*(v)).x,0.0);
    occ += max(sdf(p + epsilon*(-v)).x,0.0);
    occ += max(sdf(p + 4.0*epsilon*(n+u)).x/2.0,0.0);
    occ += max(sdf(p + 4.0*epsilon*(n-u)).x/2.0,0.0);
    occ += max(sdf(p + 4.0*epsilon*(n+v)).x/2.0,0.0);
    occ += max(sdf(p + 4.0*epsilon*(n-v)).x/2.0,0.0);
    occ += max(sdf(p + 2.0*epsilon*n).x/sqrt(2.0),0.0);
    occ += max(sdf(p + 3.0*epsilon*n).x/sqrt(3.0),0.0);
    occ += max(sdf(p + 4.0*epsilon*n).x/sqrt(4.0),0.0);

    return clamp(1.0 - 1.0/(1.0+2.0*pow(occ,1.0)), 0.0, 1.0);
}

vec3 material(float m) {
    if (m == 0.0) {
        return vec3(.8, .4, .4);
    } else {
        return vec3(.2, .2, .4);
    }
}

vec3 normal(vec3 p) {
    vec3 epsilon = vec3(0.001, 0, 0);
    return normalize(vec3(
        sdf(p + epsilon.xyy).x - sdf(p - epsilon.xyy).x,
        sdf(p + epsilon.yxy).x - sdf(p - epsilon.yxy).x,
        sdf(p + epsilon.yyx).x - sdf(p - epsilon.yyx).x ));
}

vec3 ray(vec3 eye, vec3 dir, out int iters) {
    vec3 p = eye + sdf(eye).x * dir;
    iters = 0;
    vec2 d;
    for (int i = 0; i < 50; i++) {
        d = sdf(p);

        p += d.x * dir;
        if (d.x < .001) {
            break;
        }

        iters += 1;
    }

    vec3 light = normalize(vec3(2.0*cos(2.0*iTime), sin(iTime), 2.0*sin(2.0*iTime)));

    vec3 n = normal(p);
    vec3 light_dir = normalize(light - p);
    vec3 mirrored = -normalize(2.0*dot(n,dir)*n - dir);
    vec3 half_vector = normalize((-dir + light_dir) / 2.0);
    vec3 brightness = ao(p,n) * (material(d.y) * (0.2 + 0.6 * clamp(.3 + .7 * dot(n, light_dir), 0.0, 1.0)) + vec3(1.0, 1.0, 1.0) * 0.15 * pow(clamp(dot(half_vector, n), 0.0, 1.0), 20.0));
    return brightness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    int iters;

    vec3 eye = vec3(3.5*cos(iTime), sin(iTime + 1.0), 3.5*sin(iTime));
    vec3 up = vec3(0,1,0);
    vec3 forward = normalize(vec3(0,0,.5*sin(iTime)) - eye);
    vec3 right = cross(forward, up);
    vec2 pixel = fragCoord.xy / iResolution.x - vec2(.5,.5*iResolution.y/iResolution.x);
    vec3 dir = forward + (pixel.x * right - pixel.y * up);
    vec3 color = ray(eye, normalize(dir), iters);

    if (iters > 25) {
        vec3 dx = .125*right/iResolution.x;
        vec3 dy = .125*up/iResolution.y;
        vec3 a = ray(eye, normalize(dir - dx - dy), iters);
        vec3 b = ray(eye, normalize(dir + dx - dy), iters);
        vec3 c = ray(eye, normalize(dir + dx + dy), iters);
        vec3 d = ray(eye, normalize(dir - dx + dy), iters);

        color = mix(mix(a, b, .5), mix(c, d, .5), .5);
    }

    fragColor = vec4(color, 1.0);
}

#define c_points 128

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0,1.0);
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

float rand1( float x ) {
    return fract(sin(x*9945.2543)*7239.543);
}

float rand1( int x ) {
    return rand1( float(x) );
}

float rand2( float x ) {
    return rand1( x + iTime );
}

float rand2( int x ) {
    return rand2( float(x) );
}

float distance2( vec2 a, vec2 b ) {
    float x = a.x-b.x;
    float y = a.y-b.y;
    return x*x + y*y;
}

vec2 translate( vec2 screenSpace ) {
    return screenSpace/iResolution.y;
}

vec2 position( int i ) {
    return vec2( iResolution.x/iResolution.y*rand1(i) + sin((iTime+15.)*rand1(i+99)/5.)/3., rand1(i+999) + cos((iTime+15.)*rand1(i+99)/5.)/3. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int closest=-1;
    float dis=999.;

    for( int i = 0; i < c_points; i++ ) {
        float distanceToPoint = distance2( position(i), translate(fragCoord) );
        if( distanceToPoint < dis ) {
            closest = i;
            dis = distanceToPoint;
        }
    }

    if( dis<.00001 ) {
        fragColor = vec4( .4, .8, .4, 1. );
    } else {
    	fragColor = vec4( hsl2rgb(vec3(rand1(closest+1),1.,.5/(dis*256.+1.))), 1. );
    }
}

