#define EPS 0.002
#define FAR 40.0 
#define PI 3.14159265359
#define T iTime
#define NTILES 12.0

vec3 ro = vec3(0.0);
vec4 sphere = vec4(0.0, 0.0, 0.0, 0.3);

mat2 rot(float x) {return mat2(cos(x), sin(x), -sin(x), cos(x));}
float rand(vec2 p) {return fract(sin(dot(p, vec2(12.9898,78.233))) * 43758.5453);}

vec3 path(float t) {
    float a = sin(t * PI / 16.0 + 1.5707963 * 1.0);
    float b = cos(t * PI / 16.0);
    return vec3(a * 2.0, b * a, t);    
}

float glyphcell(float uvz, float uva, vec2 cellid) {
    vec2 cuv = vec2(uvz, uva) * 10.0 - 5.0;
    vec2 cmx = mod(cuv, 1.0) - 0.5;
    float lc = length(cmx);
    float r1 = rand(floor(vec2(cuv.x + cellid.y, cuv.y + cellid.x + floor(T)))) > 0.7 ? 1.0 : 0.0;
    float pc = smoothstep(0.4, 0.3, lc) * r1; 
    pc *= step(cuv.x, 2.0) * step(-2.0, cuv.x);
    pc *= step(cuv.y, 0.0) * step(-4.0, cuv.y);
    return pc;
}

//IQ - Sphere functions
//http://www.iquilezles.org/www/articles/spherefunctions/spherefunctions.htm
float sphIntersect(vec3 ro, vec3 rd, vec4 sph) {
    vec3 oc = ro - sph.xyz;
    float b = dot(oc, rd);
    float c = dot(oc, oc) - sph.w * sph.w;
    float h = b * b - c;
    if (h < 0.0) return -1.0;
    h = sqrt(h);
    return -b - h;
}

float sphDensity(vec3 ro, vec3 rd, vec4 sph, float dbuffer) {
    float ndbuffer = dbuffer / sph.w;
    vec3  rc = (ro - sph.xyz) / sph.w;

    float b = dot(rd, rc);
    float c = dot(rc, rc) - 1.0;
    float h = b*b - c;
    if (h < 0.0) return 0.0;
    h = sqrt(h);
    float t1 = -b - h;
    float t2 = -b + h;

    if (t2 < 0.0 || t1 > ndbuffer) return 0.0;
    t1 = max(t1, 0.0);
    t2 = min(t2, ndbuffer);

    float i1 = -(c * t1 + b * t1 * t1 + t1 * t1 * t1 / 3.0);
    float i2 = -(c * t2 + b * t2 * t2 + t2 * t2 * t2 / 3.0);
    return (i2 - i1) * (3.0 / 4.0);
}

vec2 nearest(vec2 a, vec2 b){ 
    float s = step(a.x, b.x);
    return s * a + (1. - s) * b;
}

vec4 map(vec3 rp) {

    rp.xy -= path(rp.z).xy;      

    rp.xy *= rot(T * 0.5);
    rp.z += T;

    vec3 q = rp;

    float a = atan(q.y, q.x) / 6.2831853;
    float ia = floor(a * NTILES) / NTILES * 6.2831853;

    vec2 cell = vec2(rp.z, a * NTILES);

    //panels
    q.xy *= rot(ia);
    q.z = abs(mod(q.z, 1.0) - 0.5);
    vec2 panels = vec2(max(length(q.xy) - 1.7, 1.65 - length(q.xy)), 1.0);
    panels.x = max(panels.x, q.z - 0.25);    
    panels.x = max(panels.x, q.y - 0.5);

    //rings
    q = rp;
    q.z = abs(mod(q.z + sin(T * 0.4) * 30.0, 80.0) - 40.0);
    vec2 rings1 = vec2(max(length(q.xy) - 1.5, 1.45 - length(q.xy)), 2.0);
    rings1.x = max(rings1.x, max(q.z - 0.3, 0.25 - q.z));

    q = rp;
    q.z = abs(mod(q.z + cos(T * 0.5) * 20.0, 60.0) - 30.0);
    vec2 rings2 = vec2(max(length(q.xy) - 0.8, 0.75 - length(q.xy)), 2.0);
    rings2.x = max(rings2.x, max(q.z - 0.3, 0.25 - q.z));

    q = rp;
    q.z = abs(mod(q.z + sin(T * 0.6) * 15.0, 40.0) - 20.0);
    vec2 rings3 = vec2(max(length(q.xy) - 0.4, 0.35 - length(q.xy)), 2.0);
    rings3.x = max(rings3.x, max(q.z - 0.3, 0.25 - q.z));

    return vec4(nearest(panels, nearest(rings1, nearest(rings2, rings3))), cell);
}

struct Scene {
    float t;
    float id;
    float li;
    vec2 cell;
};

Scene march(vec3 ro, vec3 rd) {

    float t = 0.0;
    float li = 0.0;
    float id = 0.0;
    vec2 cell = vec2(0.0);

    for (int i = 0; i < 96; i++) {
        vec3 rp = ro + rd * t;
        vec4 scene = map(rp);
        if (scene.x < EPS || scene.x > FAR) {
            id = scene.y;
            cell = scene.zw;
            break;
        }

        li += 0.005 / (1.0 + scene.x * scene.x * 20.5);

        t += scene.x;
    }

    return Scene(t, id, li, cell);
}

void setupCamera(vec2 fragCoord, inout vec3 rd) {

    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

    float ct = T * 6.0;

    vec3 lookAt = vec3(0.0, 0.0, ct);
    ro = lookAt + vec3(0.0, 0.0, -5.0);
    sphere.xyz = ro + vec3(0.0, 0.0, sin((T + 80.0) * 0.16) * 80.0);

    lookAt.xy += path(lookAt.z).xy;
    ro.xy += path(ro.z).xy;
    sphere.xy += path(sphere.z).xy;

    float FOV = PI / 3.0;
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(vec3(forward.z, 0.0, -forward.x)); 
    vec3 up = cross(forward, right);

    rd = normalize(forward + FOV * uv.x * right + FOV * uv.y * up);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec3 pc = vec3(0.0);
    float mint = FAR;

    vec3 rd;
    setupCamera(fragCoord, rd);

    vec3 c1 = vec3(0.0, 1.0, 0.0);
    vec3 c2 = vec3(0.5, 1.0, 0.0);

    if (sphere.z < ro.z) {
        c1 = vec3(1.0, 0.0, 0.0);
        c2 = vec3(1.0, 0.5, 0.0);
    }

    Scene scene = march(ro, rd);
    if (scene.t > 0.0 && scene.t < FAR) { 

        if (scene.id == 1.0) {
            vec2 cellid = floor(scene.cell);
            vec2 celluv = fract(scene.cell);

            float r = rand(cellid);

            if (r > 0.4 && r < 0.8) {
                pc = c2 * glyphcell(celluv.x, celluv.y, cellid);
                pc /= scene.t * 0.25;
            }
        } else if (scene.id == 2.0 || scene.id == 3.0) {
            pc = c2;
        }

        mint = scene.t;
    }

    pc += c1 * scene.li;

    float st = sphIntersect(ro, rd, sphere);
    if (st > 0.0 && st < mint) {

        float h = sphDensity(ro, rd, sphere, mint);
        if (h > 0.0) {
            pc = mix(pc, vec3(1.0, 0.0, 0.0), h);
            pc = mix(pc, 0.85 * vec3(1.0, 0.7, 0.0), h * h * h);   
        }
    }

    fragColor = vec4(sqrt(clamp(pc, 0.0, 1.0)), 1.0);
}

float hash(vec2 n) {
	return fract(dot(vec2(sin(n.x*2343.34), cos(n.y*30934.0)), vec2(sin(n.y*309392.34), cos(n.x*3991.0))));
}

// Minkowski operators, can be seen at http://citeseerx.ist.psu.edu/viewdoc/download?doi=10.1.1.90.803&rep=rep1&type=pdf
// It's a paper on generalized distance functions, but it intros on minkowski operators (generalizing them).
float len(vec3 p, float l) {
	p = pow(abs(p), vec3(l));
	return pow(p.x + p.y + p.z, 1.0/l);
}

float de(vec3 p) {
	vec3 q = p;
	vec2 c = floor((p.xz + 3.0)/6.0);

	q.xz = mod(q.xz + 3.0, 6.0) - 3.0;
	q.y -= 0.5;
    // use random value to produce different shape.
	return min(len(q, 1.5 + 9.0*hash(c)) - 1.5, p.y + 1.0);
}

// basic trace, with some LOD
float trace(vec3 o, vec3 d, float m) {
	float t = 0.0;
	for(int i = 0; i < 200; i++) {
		float d = de(o + d*t);
		if(d < (0.001 + 0.0001*t) || t >= m) break;
		t += d*0.67;
	}
	return t;
}

// basic normal.
vec3 normal(vec3 p) {
	vec2 h = vec2(0.001, 0.0);
	vec3 n = vec3(
		de(p + h.xyy) - de(p - h.xyy),
		de(p + h.yxy) - de(p - h.yxy),
		de(p + h.yyx) - de(p - h.yyx)
	);
	return normalize(n);
}

// slowish ao
float ao(vec3 p, vec3 n) {
	float o = 0.0, s = 0.005, w = 1.0;

	for(int i = 0; i < 15; i++) {
		float d = de(p + n*s);
		o += (s - d)*w;
		w *= 0.98;
		s += s/float(i + 1);
	}

	return 1.0 - clamp(o, 0.0, 1.0);
}

//euclidean cameras.
mat3 camera(vec3 o, vec3 l) {
	vec3 w = normalize(l - o);
	vec3 u = normalize(cross(vec3(0, 1, 0), w));
	vec3 v = normalize(cross(w, u));

	return mat3(u, v, w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

	vec3 ro = vec3(2.7 + cos(iTime*0.3), 0, iTime);
	vec3 rd = camera(ro, vec3(2.5 + 0.9*cos(iTime*0.3 + 0.3), 0, iTime + 0.2))*normalize(vec3(p, 1.97));

	vec3 col = vec3(0.45, 0.8, 1.0);
	vec3 lig = normalize(vec3(0.8, 0.7, -0.6));

	for(int i = 0; i < 3; i++) {
		float t = trace(ro, rd, 50.0);
		if(t < 50.0) {
			vec3 rcol = vec3(0);

            // geometry, hit position, normal, reflect
			vec3 pos = ro + rd*t;
			vec3 nor = normal(pos);
			vec3 ref = reflect(rd, nor);

            // occlusion and shadows
			float occ = ao(pos, nor);
			float sha = step(5.0, trace(pos + nor*0.001, lig, 5.0));

            // lighting ambient + diffuse + fresnel + specular
			rcol += 0.2*occ;
			rcol += clamp(dot(lig, nor), 0.0, 1.0)*occ*sha;
			rcol += pow(clamp(1.0 + dot(rd, nor), 0.0, 1.0), 2.0)*occ;
			rcol += 2.0*pow(clamp(dot(ref, lig), 0.0, 1.0), 30.0)*occ;

            // simple material.
			if(pos.y > -0.99)
				rcol *= vec3(2.2, 0.7, 0.7);
			else
				rcol *= 0.3 + 0.7*mod(floor(pos.x) + floor(pos.z), 2.0);

            // set up the ray orgin and direction for reflection.
			ro = pos + nor*0.001;
			rd = ref;

            // sky fog.
			rcol = mix(rcol, vec3(0.45, 0.8, 1.0), 1.0 - exp(-0.1*t));

            // lighten intensity on each successive reflect.
			if(i == 0) col = rcol;            
			else col *= mix(rcol, vec3(1), 1.0 - exp(-0.8*float(i)));
		}
	}

    // tone mapping and gamma correction.
	col = 1.0 - exp(-0.5*col);
	col = pow(abs(col), vec3(1.0/2.2));

	fragColor = vec4(col, 1);
}

const int ITERATIONS = 50;
const int MAX_MARCHING_STEPS = 100;
const float MIN_DIST = 1.5;
const float MAX_DIST = 7.5;
const float EPSILON = 0.002;
const float ORBIT_TIME = 2.0;
const float PI = 3.141592653;
const float MIN_POWER = 5.0f;
const float MAX_POWER = 8.0f;
const float POWER_TIME = 70.0f;
const vec3 xDir = vec3(1,0,0);
const vec3 yDir = vec3(0,1,0);
const vec3 zDir = vec3(0,0,1);
const int USE_SPECULAR = 0;
const float SHADOW_VISIBILITY = 0.15f;
const float ROTATION_TIME = 29.0;
const vec3 BACKGROUND_COLOR = vec3(1.0,1.0,1.0);


vec3 bulbPower(vec3 z, float power)
{
    float r = length(z);
    // convert to polar coordinates
	float theta = acos(z.z/r);
	float phi = atan(z.y,z.x);

	// scale and rotate the point
	float zr = pow(r, power);
	theta = theta * power;
	phi = phi * power;

	// convert back to cartesian coordinates
	z = zr * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta));
    return z;
}

float DE(vec3 pos) 
{
	vec3 z = pos;
	float dr = 1.0;
	float r = 0.0;
    float power = MIN_POWER + (MAX_POWER-MIN_POWER) * (sin(iTime * 2.0 * PI / POWER_TIME) + 1.0) ;
	for (int i = 0; i < ITERATIONS ; i++) {
		r = length(z);
		if (r > 2.0) break;
		dr =  pow( r, power-1.0)*power*dr + 1.0;

		z = bulbPower(z, power) + pos;
	}
	return 0.5*log(r)*r/dr;
}

vec2 solveQuadratic(float a, float b, float c)
{
    float x0,x1;
    float discr = b * b - 4.0 * a * c;
    if (discr < 0.0) return vec2(-1.0,-1.0); //dayum this is bad way to do this, but works for this purpose ;)
    else if (discr == 0.0) x0 = x1 = - 0.5 * b / a;
    else {
      	float discrRoot = sqrt(discr);
        float x0 = (-b + discrRoot) / 2.0 / a;
        float x1 = (-b - discrRoot) / 2.0 / a;
        if (x0 > x1) 
           return vec2(x1,x0);
        else
           return vec2(x0,x1);
    }
}


// returns -1 if there is no intersection
float eyeRayToSphereDistance(vec3 rayOrigin, vec3 rayDirection, vec3 sphereOrigin, float sphereRadius)
{
    vec3 L = rayOrigin - sphereOrigin;
	float a = dot(rayDirection, rayDirection);
	float b = 2.0 * dot(rayDirection, L);
	float c = dot(L, L) - sphereRadius * sphereRadius;
	vec2 results = solveQuadratic(a, b, c);
    return results.x;

}


