/**
 * returns the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * end is returned
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the eye to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = DE(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * returns the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}


/**
 * using the gradient of the DE, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        DE(vec3(p.x + EPSILON, p.y, p.z)) - DE(vec3(p.x - EPSILON, p.y, p.z)),
        DE(vec3(p.x, p.y + EPSILON, p.z)) - DE(vec3(p.x, p.y - EPSILON, p.z)),
        DE(vec3(p.x, p.y, p.z  + EPSILON)) - DE(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * calculates ighting contribution for a single point light source via Phong illumination.
 * 
 * the vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) 
{
	vec3 color;

    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    float dotLN = dot(L, N);
    if (dotLN < 0.0) {
        // light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 
    else
    {
        if (USE_SPECULAR == 1)
        {
            vec3 V = normalize(eye - p);
            vec3 R = normalize(reflect(-L, N));
            float dotRV = dot(R, V);
            //vec3 H = normalize(L * V);
            //float dotNH = dot(N, H);
            if (dotRV < 0.0) 
            {
                // light reflection in opposite direction as viewer, apply only diffuse
                // component
                color = lightIntensity * (k_d * dotLN);
            }
            else
                color = lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
        }
        else
        {
            color = lightIntensity * (k_d * dotLN);
        }

        vec3 lightToPos = p - lightPos;
        float maxDist = length(lightToPos);
        float dist = shortestDistanceToSurface(lightPos, normalize(lightToPos), 0.0f, maxDist);
        if (dist <= maxDist - EPSILON) 
        {
            // hit something between light source and point -> shadow
        	return color * SHADOW_VISIBILITY; //
    	}
    }
    return color;
}

/**
 * lighting via Phong illumination.
 * 
 * the vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color of the surface
 * k_d: Diffuse color of the surface
 * k_s: Specular color of the surface
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(2.5*sin(iTime * 2.0 * PI / ROTATION_TIME), 2.0, 3.0*cos(iTime * 2.0 * PI / ROTATION_TIME));
    vec3 light1Intensity = vec3(1, 1, 1);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);


    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

vec4 getBackgroundColor(vec2 fragCoord)
{
	float strength = length((fragCoord - iResolution.xy / 2.0) / iResolution.xy);
	return vec4(BACKGROUND_COLOR - BACKGROUND_COLOR * 2.0 / 3.0 * strength,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(4.5*sin(iTime * 2.0 * PI / ROTATION_TIME), (sin(iTime * 2.0 * PI / ROTATION_TIME) + 1.0), 6.0*cos(iTime * 2.0 * PI / ROTATION_TIME));
    mat4 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float distanceToBoundingSphere = eyeRayToSphereDistance(eye, worldDir, vec3(0,0,0), 1.2);
    if(distanceToBoundingSphere  == -1.0)
    {
		fragColor = getBackgroundColor(fragCoord);	
        return;
    }

    float dist = shortestDistanceToSurface(eye, worldDir, distanceToBoundingSphere, distanceToBoundingSphere + 2.4);

    if (dist > distanceToBoundingSphere + 2.4 - EPSILON) 
       {
        // Didn't hit anything
        fragColor = getBackgroundColor(fragCoord);
        return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;
    float distance = length(p);
    float scale = (distance - 0.5) * 1.2;
    vec3 K_a = vec3(0.7 - 0.4 * scale, 0.24 - 0.14 * scale, 0.0) / 2.0;
    vec3 K_d = K_a * 2.8;
    vec3 K_s = K_a * 2.0;
    float shininess = 50.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

// um, some kind of mystical hole...?

float glint(vec2 p)
{
    vec2 uv = p;
    float speed = 0.68;
    float linewidth = 1.38;
    float grad = 3.0;
    vec2 linepos = uv;
    linepos.x = linepos.x - mod(iTime*speed,4.0)+2.0;
    float y = linepos.x*grad;
	float s = smoothstep( y-linewidth, y, linepos.y) - smoothstep( y, y+linewidth, linepos.y); 
    return s;
}

float _glint(vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    float speed = iTime*0.2;
 	float d = 1.0+p.x*0.25;
    vec2 cst = vec2( cos(d+speed), sin(d+speed) );
    float zoom = 1.0+(0.5*sin(iTime*0.675));
    mat2 rot = zoom * mat2(cst.x,-cst.y,cst.y,cst.x);
    float s = glint(p*rot*0.44);
    return s;
}

void mainImage(out vec4 k, vec2 p)
{
    const vec4 col1 = vec4(0.0,.1,.1,1.0);
    const vec4 col2 = vec4(0.5,0.9,0.3,1.0);
    vec2 uv = p.xy / iResolution.xy;
	float gl = _glint(p)*0.5;

    float s = sin(iTime*0.1);
    float s2 = 0.5+sin(iTime*1.8);
    vec2 d = uv*(4.0+s*.3);						// mod 4.0 for irregularity...

    d.x += iTime*0.25+sin(d.x+d.y + iTime*0.3)*0.5;
    d.y += iTime*0.25+sin(d.x + iTime*0.3)*0.5;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.95;				// 0.9 =more gooey bits, 1.2 = less gooey bits

    d = 0.15+(uv*0.7);			// zoom
    float v2=length(0.5-fract(d.xy));
    v1 *= 1.0-v2*v1;
    v1 = v1*v1*v1;
    v1 *= 1.9+s2*0.2;
    k = mix(col2,col1,v1)*(3.4+(s2*0.2));
    k *= 1.0-(v2);


    if (k.g<=0.4)
    {
        float m = 1.0-clamp(k.g,0.0,0.5);
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        float col = 0.3 + 0.5*f*1.0;
        k.x = k.y = k.z = col*m;
    }
    else
    {
		float f = mod( floor(12.0*uv.x) + floor(12.0*uv.y), 2.0);
        vec4 _col = 0.2*f*vec4(0.175);
        k+=_col;
    }
    // glint
    k *=vec4(1.0+gl);

}

