float PrCylDf (vec3 p, float r, float h);
float PrCapsDf (vec3 p, float r, float h);
float PrEllipsDf (vec3 p, vec3 r);
float PrEllips2Df (vec3 p, vec2 r);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltDir, qHit;
vec2 opCs;
float tCur, dstFar, opPhs, fishAngI;
int idObj;
const float pi = 3.14159;

float FishDf (vec3 p)
{
  vec3 q;
  float dMin, dBodyF, dBodyB, dMouth, dFinT, dFinP, dFinD, dEye, d, szFac;
  szFac = 0.6;
  p /= szFac;
  p.x = abs (p.x);
  p.z -= 2.;
  p.yz = Rot2D (p.yz, 0.2 * fishAngI);
  q = p;
  q.z -= -0.6;
  dBodyF = PrEllipsDf (q, vec3 (0.7, 0.8, 2.4));
  q = p;
  q.z -= -1.2;
  q.yz = Rot2D (q.yz, fishAngI);
  q.z -= -1.6;
  dBodyB = PrEllipsDf (q, vec3 (0.35, 0.5, 2.5));
  q.z -= -2.2;
  q.yz = Rot2D (q.yz, 2. * fishAngI);
  q.xz -= vec2 (0.5, -0.5);
  q.xz = Rot2D (q.xz, 0.4);
  dFinT = PrEllipsDf (q, vec3 (0.8, 0.07, 0.4));
  q = p;
  q.yz -= vec2 (-0.3, 1.7);
  q.yz = Rot2D (q.yz, 0.1);
  q.y = abs (q.y) - 0.04;
  dMouth = PrEllipsDf (q, vec3 (0.25, 0.12, 0.6));
  q = p;
  q.yz -= vec2 (0.7, -1.);
  q.yz = Rot2D (q.yz, 0.6);
  dFinD = PrEllipsDf (q, vec3 (0.05, 1., 0.35));
  q = p;
  q.xy = Rot2D (q.xy, 0.8);
  q.xz -= vec2 (0.7, -0.1);
  q.xz = Rot2D (q.xz, 0.6);
  dFinP = PrEllipsDf (q, vec3 (0.9, 0.04, 0.3));
  q = p;
  q -= vec3 (0.4, -0.1, 1.1);
  dEye = PrEllipsDf (q, vec3 (0.11, 0.15, 0.15));
  idObj = 1;
  qHit = q;
  dMin = SmoothMin (dBodyF, dBodyB, 0.3);
  dMin = SmoothMin (dMin, dFinT, 0.1);
  dMin = SmoothMin (dMin, dMouth, 0.15);
  dMin = SmoothMin (dMin, dFinD, 0.02);
  dMin = SmoothMin (dMin, dFinP, 0.02);
  if (dEye < dMin) { idObj = 2;  qHit = q; }
  dMin = SmoothMin (dMin, dEye, 0.01);
  return dMin * szFac;
}

float ObjDf (vec3 p)
{
  vec3 q, qHitA;
  float dMin, d, r, radH, radV, w;
  int idObjA;
  dMin = dstFar;
  radH = 4.;
  radV = 3.;
  w = 0.1;
  q = p;  q.z += radH;
  d = PrCapsDf (q.yzx, 0.25, 0.95);
  if (d < dMin) { dMin = d;  idObj = 4;  qHit = q; };
  q.yz = Rot2Cs (q.yz, opCs);
  q.y = abs (q.y);
  q.yz = Rot2Cs (q.yz, opCs);
  q.z -= radH;
  r = radH + 0.1 * (1. - q.y / length (q)) * cos (32. * atan (q.x, q.z));
  d = max (PrEllips2Df (q.xzy, vec2 (r, radV) + w), - PrEllips2Df (q.xzy, vec2 (r, radV) - w));
  d = max (d, - q.y);
  if (d < dMin) { dMin = d;  idObj = 3;  qHit = q; };
  idObjA = idObj;
  qHitA = qHit;
  q = p;  q.y -= -0.5 + 2. * opPhs;
  q.xz = Rot2D (q.xz, 0.1 * pi * tCur);
  d = FishDf (q);
  if (d < dMin) { dMin = d;  idObjA = idObj;  qHitA = qHit; }
  idObj = idObjA;
  qHit = qHitA;
  q = p;  q.y -= -radV + 1.5 + 2. * opPhs;
  d = PrCylDf (q.xzy, 2.3, 0.1);
  if (d < dMin) { dMin = d;  idObj = 5;  qHit = q; };
  q = p;  q.y -= -0.5 * (radV - 2. * opPhs);
  d = PrCylDf (q.xzy, 0.05, 0.5 * (radV - 0.5 + 2. * opPhs));
  q = p;  q.y -= - radV + 0.1;
  d = min (d, PrCylDf (q.xzy, 0.5, 0.05));
  if (d < dMin) { dMin = d;  idObj = 6; };
  return 0.9 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 160; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, s;
  bool isLit, isRefl;
  opPhs = 0.9 * SmoothBump (0.2, 0.8, 0.15, mod (0.2 * tCur, 1.));
  opCs = vec2 (cos (0.25 * pi * opPhs), sin (0.25 * pi * opPhs));
  fishAngI = 0.15 * sin (pi * tCur);
  isRefl = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar && idObj == 5 && qHit.y > 0. && length (qHit.xz) < 2.25) {
    ro += dstObj * rd;
    rd = reflect (rd, vec3 (0., 1., 0.));
    ro += 0.01 * rd;
    dstObj = ObjRay (ro, rd);
    isRefl = true;
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    isLit = false;
    if (idObj == 1) {
      col = 1.2 * vec3 (0.8, 0.75, 0.2) * (1. - 0.2 * smoothstep (-0.5, -0.4, vn.y));
    } else if (idObj == 2) {
      if (length (qHit.yz - vec2 (-0.02, 0.05)) < 0.05) {
        col = vec3 (1., 0.1, 0.1);
        isLit = true;
      } else col = vec3 (0.4, 0.7, 0.2);
    } else if (idObj == 3) {
      s = length (qHit / vec3 (4., 3., 4.));
      col = (s > 1. || qHit.y < 0.05) ? vec3 (0.8, 0.75, 0.2) :
         mix (vec3 (0.7, 0.7, 0.9), vec3 (0.2, 0.2, 0.8), smoothstep (0.5, 1.5, 3. - qHit.y) *
         smoothstep (0.96, 0.98, s));
      if (s > 1.) vn = VaryNf (64. * qHit, vn, 0.5);
    } else if (idObj == 4) {
      col = vec3 (0.8, 0.75, 0.2) * (1. - 0.5 * SmoothBump (0.05, 0.1, 0.02, mod (4. * qHit.x, 1.)));
    } else if (idObj == 5 || idObj == 6) {
      col = vec3 (0.8, 0.75, 0.2);
    }
    if (! isLit) col = col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    else col *= (0.5 - 0.5 * dot (rd, vn));
    if (! isRefl && (idObj == 1 || idObj == 3 && s > 1.)) col += 256. * pow (clamp (0.5 +
       0.5 * dot (ltDir, reflect (rd, vn)), 0., 1.), 16.) *
       pow (1. - 0.6 * abs (dot (normalize (ltDir - rd), VaryNf (1024. * qHit, vn, 2.))), 8.);
  } else {
    col = vec3 (0.6, 0.6, 1.) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.6 * pi * (2. * mod (floor (0.04 * tCur), 2.) - 1.) *
       SmoothBump (0.25, 0.65, 0.1, mod (0.04 * tCur, 1.));
    el -= 0.05 * pi * sin (0.05 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 2.8, -30.);
  rd = vuMat * normalize (vec3 (uv, 4.5));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., h * clamp (p.z / h, -1., 1.))) - r;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllips2Df (vec3 p, vec2 r)
{
  return (length (p / r.xxy) - 1.) * min (r.x, r.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.1415972

float d(vec2 uv)
{
    float theta = atan(uv.y, uv.x) + iTime * 0.1;
    float rho = length(uv);

    //float period = 2.*PI/5.;
    float period = 2.*PI/floor(iMouse.y/iResolution.y*8.+2.);
    if(iMouse.y < 0.5)
        period = 2.*PI/5.;

    theta = mod(theta, period);
    theta = min(theta, period-theta);

    vec2 p = vec2(cos(theta), sin(theta)) * rho;


    //float alpha = PI*0.3;
    float alpha = iMouse.x/iResolution.x * ((PI-period*0.5)-asin(.5)) + asin(.5);
    if(iMouse.x < 0.5)
        alpha = 0.5*(1.0+sin(iTime)) * ((PI-period*0.5)-asin(.5)) + asin(.5);

    float x = 2.*sin(alpha)/(sin(period*.5)+2.*sin(alpha+period*.5));
    float rad = sin(period*.5)/(sin(period*.5)+2.*sin(alpha+period*.5));

    //float h0 = 1.-rad-rad*cos(alpha);
    //float h1 = rad*sin(alpha);
    //float xl = sqrt(h0*h0+h1*h1);

    float dist = rad-length(p-vec2(1.0-rad, 0.0));

    if(cross(vec3(p,0.)-vec3(1.-rad, 0., 0.), vec3(x*cos(.5*period), x*sin(.5*period),0.)-vec3(1.-rad, 0., 0.)).z < 0.)
       dist = max(dist, length(p-x*vec2(cos(.5*period), sin(.5*period)))-rad);

    return dist;
}

vec3 n(vec2 uv)
{
    vec2 l = vec2(0.02, 0.0);
    //vec2 l = vec2(0.2, 0.0);
    return normalize(vec3(d(uv+l.xy)-d(uv-l.xy), d(uv+l.yx)-d(uv-l.yx), l.x*5.));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*(fragCoord.xy / min(iResolution.x,iResolution.y) - 0.5*iResolution.xy/ min(iResolution.x,iResolution.y));

    float dist = d(uv);
    vec3 col =  vec3(dist>0.0?0.0:1.0, 0., dist<0.0?0.0:1.0);
    float stripes = 0.5*(1.0-cos(dist*iResolution.x/3.));
    stripes = (stripes+2.0)/3.;
    float contour = 0.5*(1.0-cos(clamp(dist*iResolution.x/3., -PI, PI)));

    fragColor = vec4(col * stripes *contour, 1.0);
    //fragColor = vec4(col*contour, 1.0);
    //fragColor = vec4(contour);



    //vec3 n = normalize(vec3(dFdx(dist), dFdy(dist), .01));
    vec3 n = n(uv);
    vec3 l = normalize(vec3(cos(iTime*.1), sin(iTime*.1), 1.1));
    float phong = max(dot(n,l), 0.0);

    float spec = 0.5*pow(max(dot(reflect(vec3(0.0, 0.0, -1.0),n),l), 0.0), 256.);
    fragColor = vec4((col * phong+ vec3(spec)) * stripes *contour , 1.0);

}

#define pi  3.14159

// from iq
float expStep( float x, float k, float n ){
    return exp( -k*pow(x,n) );
}

mat2 rot(float rads)
{
    return mat2(cos(rads), sin(rads), -sin(rads), cos(rads));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
    p = rot(iTime * 1.25) * p;
    p = vec2(p.x, -p.y) + .15;

    float r = length(p);
    float a = atan(p.y, p.x);
    a += 2. * sin(a);
    float coord = fract(a / pi + expStep(r, 1., .5) * 8. + 1.6 * iTime);
    vec3 col = mix(vec3(.17, 0., .25), vec3(.3, 0., .5), step(.6, coord));

    col *= pow(r, .65) * 1.75;
    fragColor.rgb = col;
}

#define PI 3.14159265358979323846

mat2 rotateMatrix2D(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    mat2 rot = rotateMatrix2D(_angle);
    _st =  rot * _st;
    _st += 0.5;
    return _st;
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

vec3 daisy(vec2 st, vec2 orig, float resize, float smoothness, float nPale){
    vec2 toCenter = vec2(orig)-st;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*resize;

    float f = abs(cos(angle*nPale));
    return vec3( 1.-smoothstep(f,f+smoothness,radius) );;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 colorA = vec3(0.149,0.141,0.912);
    vec3 colorB = vec3(1.000,0.833,0.224);

    vec2 st = fragCoord/iResolution.y;
    vec2 originalSt = st;

    // Divide the space
    st = tile(st,3.);
    st = rotate2D(st,(sin(iTime)*PI/16.0) );
    vec2 rotDaisyA = rotate2D(st,(sin(iTime)*PI/16.0) );

    // divide the space again
    st = tile(st,2.);
    vec2 rotDaisyB = rotate2D(st,(fract(iTime)) );

    // Draw a square
    //color = vec3(box(st,vec2(0.7),0.01));
    colorA *= daisy(rotDaisyA, vec2(0.5), -abs(sin(iTime)*PI/2.0)+2.3, 1.9, 8.0);
    colorB *= daisy(rotDaisyB, vec2(0.5), (abs(sin(iTime)) + 2.0)*7.0, 2.9, 3.0);
    fragColor = vec4((colorA+colorB),1.0);    

}

