vec3 bSize, qHit, ltPos, cId;
float tCur, dstFar, stepCnt;
int idObj;
bool colImg, marchImg;
const float pi = 3.14159;
const int idFlr = 1, idCol = 2, idRail = 3, idStr = 4, idShlf = 5, idBk = 6, idLt = 7;

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }
#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q, qr;
  float dMin, d, stAng, r, a, s;
  dMin = dstFar;
  r = length (p.xz);
  stAng = 0.5 * pi * floor (4. * Hashfv2 (cId.xz));
  q = p;  q.y = 0.5 * bSize.y - 0.3 - abs (p.y);
  d = max (q.y, 9.8 - r);
  DMINQ (idFlr);
  q.xz = abs (q.xz) - 15.;
  q.y -= 0.1;
  d = min (length (q.xz) - 0.6, PrCylDf (q.xzy, 1., 0.1));
  DMIN (idCol);
  qr = p;
  qr.xz = Rot2D (qr.xz, stAng);
  q = qr;
  d = length (vec2 (r - 10.2, abs (q.y + 6.) - 1.3)) - 0.13;
  d = max (d, 2. - abs (qr.z));
  d = min (d, PrCylDf (vec3 (abs (q.x) - 10., q.y + 7.1, abs (q.z) - 2.).xzy, 0.25, 2.8));
  q.xz = Rot2D (q.xz, 2. * pi / 32.);
  a = (r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (16. * a + 0.5) / 16.));
  d = min (d, PrCylDf ((q + vec3 (10.2, + 7.2, 0.)).xzy, 0.13, 2.5));
  q = qr;
  q.xy = Rot2Cs (q.xy, vec2 (sqrt (0.5)));
  d = min (d, max (length (vec2 (abs (q.y - 2.9) - 0.9, abs (q.z) - 2.)) - 0.13, abs (qr.x) - 10. ));
  q = qr;  q.x += 20.;
  q.xy = Rot2D (q.xy, 0.25 * pi);
  d = min (d, max (length (vec2 (abs (q.y - 2.9) - 0.9, abs (q.z) - 2.)) - 0.13, abs (qr.x) - 10.));
  DMIN (idRail);
  q = qr;
  q.xy = Rot2Cs (q.xy - vec2 (10., -10.), vec2 (sqrt (0.5)));
  s = mod (q.x, 1. * sqrt (2.));
  d = 0.5 * max (max (q.y - min (s, 1. * sqrt (2.) - s), abs (q.z) - 2.), -0.2 - q.y);
  DMINQ (idStr);
  q = p;  q.y -= -6.7;
  s = min (abs (q.x), abs (q.z));
  d = max (max (max (abs (r - 17.) - 1., abs (q.y) - 3.), 5. - s),
     - max (max (abs (r - 16.) - 1.5, abs (abs (q.y) - 1.4) - 1.2), 5.5 - s));
  DMINQ (idShlf);
  d = max (max (abs (r - 17.) - 0.5, abs (q.y) - 2.8), 5.2 - s);
  DMINQ (idBk);
  q = p;  q.y -= 9.5;
  d = length (vec2 (r - 11., q.y)) - 0.2;
  q.xz = abs (q.xz) - 0.5 * bSize.xz;
  d = min (d, PrCylDf (q.xzy, 2., 0.2));
  DMIN (idLt);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p, rdi, s;
  float dHit, d, eps;
  eps = 0.0005;
  dHit = eps;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  ro /= bSize;
  rd /= bSize;
  rdi = 1. / rd;
  stepCnt = 0.;
  for (int j = 0; j < 330; j ++) {
    p = ro + dHit * rd;
    cId = floor (p);
    s = (cId + step (0., rd) - p) * rdi;
    d = min (ObjDf (bSize * (p - (cId + 0.5))), abs (min (min (s.x, s.z), s.y)) + eps);
    dHit += d;
    ++ stepCnt;
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  cId = floor (p / bSize);
  p -= bSize * (cId + 0.5);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 60; j ++) {
    p = ro + d * rd;
    cId = floor (p / bSize);
    h = ObjDf (p - bSize * (cId + 0.5));
    sh = min (sh, smoothstep (0., 0.08 * d, h));
    d += clamp (h, 0.15, 0.3);
    if (sh < 0.05 || d > ltDist) break;
  }
  return sh;
}

float ObjAO (vec3 ro, vec3 rd)
{
  vec3 p;
  float ao, d;
  ao = 0.;
  for (int j = 0; j < 8; j ++) {
    d = 0.1 + float (j) / 16.;
    p = ro + d * rd;
    cId = floor (p / bSize);
    ao += max (0., d - 3. * ObjDf (p - bSize * (cId + 0.5)));
  }
  return 0.5 + 0.5 * clamp (1. - 0.2 * ao, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 objCol;
  vec3 col, bgCol, ltVec, roo, vn;
  vec2 vf, bt;
  float dstObj, ltDist, sh, ao, atten, r, s, a;
  bgCol = mix (0.9 * vec3 (0.2, 0.4, 0.5), vec3 (0.4) * (1. - 0.05 * Noisefv2 (512. * abs (rd.xy))),
     smoothstep (-0.01, 0.01, rd.y)) * 1.2 / (1. + 0.002 * pow (dstFar, 1.5));
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    vf = vec2 (0.);
    if (idObj == idFlr || idObj == idShlf || idObj == idBk) {
      r = length (qHit.xz);
      a = (r > 0.) ? atan (qHit.z, - qHit.x) / pi + 0.5 : 0.;
    }
    if (idObj == idFlr) {
      if (vn.y > 0.99) {
        objCol = vec4 (0.2, 0.4, 0.5, 0.1) * (1. - 0.3 * Fbm2 (2. * ro.xz)) *
           (0.5 + 0.5 * SmoothBump (0.03, 0.97, 0.01, mod (r, 1.)));
        objCol.rgb *= ((1. - 0.3 * smoothstep (1., 5., length (abs (qHit.xz) - 20.))) +
           (1. - 0.3 * smoothstep (1., 5., r - 11.)));
        vf = vec2 (32., 1.);
      } else if (vn.y < -0.99) {
        objCol = vec4 (vec3 (0.8) * (1. - 0.4 * smoothstep (3., 7., length (abs (qHit.xz) - 20.))) *
           (1. - 0.4 * smoothstep (0., 4., r - 11.)), -1.);
      } else {
        objCol = vec4 (0.3, 0.3, 0.7, 0.1);
        vf = vec2 (32., 1.);
      }
    } else if (idObj == idCol) {
      objCol = vec4 (0.8, 0.8, 0.85, 0.05);
      vf = vec2 (32., 1.);
    } else if (idObj == idRail) {
      objCol = vec4 (0.8, 0.8, 0.85, 0.2);
      vf = vec2 (32., 0.2);
    } else if (idObj == idStr) {
      if (abs (qHit.z) > 1.9) {
        objCol = vec4 (0.5, 0.55, 0.5, 0.1);
        vf = vec2 (32., 0.2);
      } else if (vn.y > -0.01) {
        objCol = vec4 (0.2, 0.4, 0.5, 0.1) * (1. - 0.2 * Fbm2 (2. * ro.xz));
        vf = vec2 (32., 0.2);
      } else {
        objCol = vec4 (0.6, 0.6, 0.6, -1.);
      }
    } else if (idObj == idShlf) {
      objCol = vec4 (vec3 (0.7, 0.4, 0.2) * (0.5 + 0.5 * Fbm2 ((r > 17.99) ? vec2 (128. * a, 0.5 * qHit.y) :
         ((abs (vn.y) < 0.01) ? vec2 (4. * r, 0.5 * qHit.y) : vec2 (32. * a, 4. * r)))), 0.1);
      vf = vec2 (32., 0.1);
      a = mod (4. * a + 0.5, 1.) - 0.5;
      if (r > 17.99 && abs (a) < 0.07 && abs (qHit.y - 2.2) < 0.4) {
        cId = floor (ro / bSize);
        if (ShowInt (vec2 (- 10. * a - 0.5, 0.8 * (qHit.y - 2.)), vec2 (1., 0.25), 4.,
           dot (mod (vec2 (66., 60.) + cId.xz, 100.), vec2 (100., 1.))) != 0.) 
           objCol = vec4 (0.2, 1., 0.2, -1.);
      }
    } else if (idObj == idBk) {
      cId = floor (ro / bSize);
      a *= 128.;
      bt = floor (vec2 (16. * a, 16. * qHit.y));
      s = Hashfv2 (vec2 (floor (a), 1. + floor (qHit.y / 2.8)));
      qHit.y = mod (qHit.y / 2.8, 1.) / (0.95 - 0.3 * s);
      if (qHit.y < 1.) {
        a = mod (a, 1.);
        objCol = vec4 (HsvToRgb (vec3 (mod (Hashfv3 (cId) + 0.2 * s, 1.), 1.,
           0.7 * (0.5 + 0.5 * SmoothBump (0.05, 0.95, 0.02, a)))), 0.1);
        objCol.rgb = mix (mix (objCol.rgb, vec3 (0.9, 0.9, 0.3), SmoothBump (0.2, 0.25, 0.01, qHit.y)),
           vec3 (1., 1., 0.2), step (abs (qHit.y - 0.5), 0.15) *
           step (abs (a - 0.5), 0.25) * step (0.5, Noisefv2 (cId.xz * vec2 (19., 31.) + bt)));
        vn.xz = Rot2D (vn.xz, 0.5 * pi * (a - 0.5));
      } else {
        objCol = vec4 (0.02, 0.01, 0., -1.);
      }
    } else if (idObj == idLt) {
      objCol = vec4 (vec3 (1., 1., 0.8) * (0.8 - 0.3 * vn.y), -1.);
    }
    ltVec = roo + ltPos - ro;
    ltDist = length (ltVec);
    atten = 1.2 / (1. + 0.002 * pow (ltDist, 1.5));
    ltVec /= ltDist;
    sh = 1.;
    ao = 1.;
    if (idObj != idBk) {
      sh = 1. - 0.8 * (1. - ObjSShadow (ro, ltVec, ltDist)) * min (0.3 * atten, 1.);
      ao = ObjAO (ro, vn);
    }
    if (objCol.a >= 0.) {
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      col = 0.2 * objCol.rgb;
      if (idObj == idBk) col += 0.3 * objCol.rgb * (0.6 - 0.4 * dot (normalize (vn.xz), normalize (qHit.xz)));
      col += sh * atten * (objCol.rgb * 0.8 * max (dot (vn, ltVec), 0.) +
         objCol.a * pow (max (dot (normalize (ltVec - rd), vn), 0.), 32.));
    } else col = objCol.rgb * (0.5 + 0.5 * atten);
    col *= ao;
    col = clamp (mix (bgCol, col, exp (32. * min (0., 0.7 - dstObj / dstFar))), 0., 1.);    
  } else col = bgCol;
  col = pow (col, vec3 (0.8));
  if (! colImg) col = vec3 (0.9) * RgbToVal (col);
  else if (marchImg) col = HsvToRgb (vec3 (0.7 * (1. - stepCnt / 300.), 1., 1.));
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az, asp;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  bSize = vec3 (40., 20., 40.);
  az = 0.;
  el = 0.;
  colImg = true;
  marchImg = false;
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y < -0.4) colImg = false;
  if (mPtr.z > 0. && mPtr.x > 0.4 && mPtr.y > 0.4) marchImg = true;
  if (mPtr.z > 0. && colImg && ! marchImg) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  } else {
    el = -0.03 * pi;
    az = 0.5 * pi * (2. * mod (floor (0.07 * tCur), 2.) - 1.) *
       SmoothBump (0.2, 0.8, 0.1, mod (0.07 * tCur, 1.));
  }
  el = clamp (el, -0.45 * pi, 0.45 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -1.) + vec3 (0., 9., 4. * tCur);
  asp = canvas.x / canvas.y;
  uv.xy /= 1.8;
  rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uv.x / asp)) * asp, uv.y, 1.));
  ltPos = vuMat * vec3 (0., 1., -1.);
  dstFar = 440.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

float RgbToVal (vec3 c)
{
  return max (c.r, max (c.g, c.b));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 3; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float DigSeg (vec2 q)
{
  return step (abs (q.x), 0.12) * step (abs (q.y), 0.6);
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  float d;
  int k, kk;
  const vec2 vp = vec2 (0.5, 0.5), vm = vec2 (-0.5, 0.5), vo = vec2 (1., 0.);
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.5, 2.2);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (min (q.x, q.y) >= 0. && max (q.x, q.y) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log (val) / log (10.), 0.)) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (sgn < 0.) {
      if (idChar == mxChar - nDig - 1.) s = ShowDig (q, -1);
      else ++ v;
    }
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

