float stime, ctime;
 void ry(inout vec3 p, float a){  
 	float c,s;vec3 q=p;  
  	c = cos(a); s = sin(a);  
  	p.x = c * q.x + s * q.z;  
  	p.z = -s * q.x + c * q.z; 
 }  
 void rx(inout vec3 p, float a){  
 	float c,s;vec3 q=p;  
  	c = cos(a); s = sin(a);  
  	p.y = c * q.y - s * q.z;  
  	p.z = s * q.y + c * q.z; 
 }  

 void rz(inout vec3 p, float a){  
 	float c,s;vec3 q=p;  
  	c = cos(a); s = sin(a);  
  	p.x = c * q.x - s * q.y;  
  	p.y = s * q.x + c * q.y; 
 }  
float plane(vec3 p, float y) {
    return length(vec3(p.x, y, p.z) - p);
}

vec3 mat=vec3(0.0);
bool bcolor = false;

float menger_spone(in vec3 z0){

    z0=z0.yzx;
	vec4 z=vec4(z0,1.0);
    vec3 offset =0.83*normalize(vec3(3.4,2., .2));
    float scale = 2.;
	for (int n = 0; n < 8; n++) {
		z = abs(z);
        ry(z.xyz, 1.5);
		if (z.x<z.y)z.xy = z.yx;
		if (z.x<z.z)z.xz = z.zx;
		if (z.y<z.z)z.yz = z.zy;
        ry(z.xyz, -1.21);

		z = z*scale;
		z.xyz -= offset*(scale-1.0);
       	if(bcolor && n==2)mat+=vec3(0.4)+sin(z.zxy*z.yxz)*vec3(0.3, .8, 0.7);
	}
	return (length(max(abs(z.xyz)-vec3(1.0),0.0))-0.01)/z.w;
}

 vec3 f(vec3 p){ 
