     float d1 = plane(p, -0.5);
     float d2 = menger_spone(p+vec3(0.,-0.1,0.));
     //return vec3(d2, 1., 0.);
     float d = d1;
     vec3 res = vec3(d1, 0., 0.);
     if(d > d2) 
     {
         d = d2;
         res = vec3(d2, 1., 0.0);
     }

	return res;
 } 

 float ao(vec3 p, vec3 n){ 
 	float ao=0.0,sca=1.0; 
 	for(float i=0.0;i<20.0;++i){ 
 		float hr=0.05+0.015*i*i; 
 		ao+=(hr-f(n*hr+p).x)*sca; 
 		sca*=0.75; 
 	} 
 	return 1.0-clamp(ao,0.0,1.0); 
 } 


float rand(vec2 t){
	return fract(sin(dot(t*0.123,vec2(12.9898,78.233))) * 43758.5453);
}
float softshadow(vec3 ro, vec3 rd, float k ){ 
     float akuma=1.0,h=0.0; 
     float t = 0.01;
     for(int i=0; i < 40; ++i){ 
         h=f(ro+rd*t).x; 
         if(h<0.001){akuma=0.0;break;} 
         akuma=min(akuma, k*h/t); 
         t+=abs(h);
     } 
     return clamp(akuma*0.8+0.2, 0.0, 1.0); 
} 
 vec3 nor(vec3 p){ 
 	vec3 e=vec3(0.0001,0.0,0.0); 
 	return normalize(vec3(f(p+e.xyy).x-f(p-e.xyy).x, 
 						  f(p+e.yxy).x-f(p-e.yxy).x, 
 						  f(p+e.yyx).x-f(p-e.yyx).x)); 
 } 

vec3 intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
    vec3 res = vec3(-1.0);
	vec3 h = vec3(1.0);
    for( int i=0; i<64; i++ )
    {
		if( h.x<0.003 || t>30.0 ){
		}else {
        	h = f(ro + rd*t);
        	res = vec3(t,h.yz);
        	t += abs(h.x);
		}
    }
	if( t>30.0 ) res=vec3(-1.0);
    return res;
}

 void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
 { 
    vec2 q=fragCoord.xy/iResolution.xy; 
 	vec2 uv = -1.0 + 2.0*q; 
 	uv.x*=iResolution.x/iResolution.y; 
	// camera
 	stime=sin(iTime*0.2); 
 	ctime=cos(iTime*0.2); 

 	vec3 ta=vec3(.0,0.0,0.0); 
	vec3 ro = vec3(3.5*ctime, 2.2+0.5*ctime, 5.8+1.0*stime) * 0.31;

 	vec3 cf = normalize(ta-ro); 
    vec3 cs = normalize(cross(cf,vec3(0.0,1.0,0.0))); 
    vec3 cu = normalize(cross(cs,cf)); 
 	vec3 rd = normalize(uv.x*cs + uv.y*cu + 2.8*cf);  // transform from view to world

    vec3 sundir = normalize(vec3(-8.5, 7.0,18.8)); 
    vec3 sun = vec3(1.64, 1.27, 0.99); 
    vec3 skycolor = vec3(0.6, 1.5, 1.); 

	vec3 bg = exp(uv.y-1.0)*vec3(.6, 0.9, 1.4)*1.3;

    float sc=clamp(dot(sundir, rd), 0.0, 1.0); 
    vec3 col=bg; 


    float t=0.0;
    vec3 p=ro; 

	vec3 res = intersect(ro, rd);
	 if(res.x > 0.0){
		   p = ro + res.x * rd;
           bcolor = true;
           mat=vec3(0.0);
           vec3 n=nor(p);
           mat/=6.0;
           bcolor = false;
           float occ = ao(p, n); 
           float shadow = softshadow(p, sundir, 50.0);

           float dif = max(0.0, dot(n, sundir)); 
           float sky = 0.6 + 0.4 * max(0.0, dot(n, vec3(0.0, 1.0, 0.0))); 
 		   float bac = max(0.3 + 0.7 * dot(vec3(-sundir.x, -1.0, -sundir.z), n), 0.0); 
           float spe = max(0.0, pow(clamp(dot(sundir, reflect(rd, n)), 0.0, 1.0), 10.0)); 

           vec3 lin = 4.5 * sun * dif * occ * shadow; 
           lin += 0.8 * bac * sun * occ; 
           lin += 0.6 * sky * skycolor * occ; 
           lin += 2.0 * spe * occ; 

           col = lin *(vec3(1.5)*(1.0-res.y)+mat*res.y) *  0.2; 
 		   col=mix(col,bg, 1.0-exp(-0.001*res.x*res.x)); 
    } 

    // post

    col=pow(clamp(col,0.0,1.0),vec3(0.45)); 
    col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
    col=mix(col, vec3(dot(col, vec3(0.33))), -0.5);  // satuation
    col*=0.5+0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting

 	fragColor.xyz = col; 
 }

// https://en.wikipedia.org/wiki/Distance_from_a_point_to_a_line
float lineDistance(in vec2 M, vec2 P1, vec2 P2) {
    float x1 = P1.x, y1 = P1.y;
    float x2 = P2.x, y2 = P2.y;
    float x0 = M.x, y0 = M.y;
    float numer = abs((y2-y1)*x0 - (x2-x1)*y0 + x2*y1 - y2*x1);
    float denom = length(P2 - P1);
    return numer / denom;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(0);

    const vec2 cen = vec2(0.5);
    const float radius = 0.4;
    float sinT = sin(iTime);
    float cosT = cos(iTime);
    vec2 P1 = vec2(cen.x + radius * cosT, cen.y + radius * sinT);
    vec2 P2 = vec2(cen.x, cen.y + radius * cosT);

    const float dotsize = 0.02;
    if (length(uv-P1) < dotsize) {
         fragColor = vec4(1.0, 0., 0., 0.);
 	}
    else if (length(uv-P2) < dotsize) {
         fragColor = vec4(1.0, 0., 0., 0.);
    }
    else {
        float d = lineDistance(uv, P1, P2);
        d = smoothstep(0., 1., d*100.0);
        fragColor = vec4(d);
    }

}

