float PrBoxDf (vec3 p, vec3 b);
float PrEllipsDf (vec3 p, vec3 r);
vec2 Rot2D (vec2 q, float a);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);

vec3 sunDir, pSize, fishPos, qHit;
float tCur, dstFar, wWid, fishAngI;
int idObj;
const float pi = 3.14159;

float WaveHt (vec2 p)
{
  mat2 qRot = mat2 (0.8, -0.6, 0.6, 0.8);
  vec4 t4, v4;
  vec2 q, t, tw;
  float wFreq, wAmp, h;
  h = -0.15;
  q = p;
  wFreq = 1.;
  wAmp = 0.03;
  tw = 0.5 * tCur * vec2 (1., -1.);
  for (int j = 0; j < 4; j ++) {
    q *= qRot;
    t4 = q.xyxy * wFreq + tw.xxyy;
    t = vec2 (Noisefv2 (t4.xy), Noisefv2 (t4.zw));
    t4 += 2. * t.xxyy - 1.;
    v4 = (1. - abs (sin (t4))) * (abs (sin (t4)) + abs (cos (t4)));
    t = 1. - sqrt (v4.xz * v4.yw);
    h += wAmp * length (t); //dot (t, t);
    wFreq *= 2.;
    wAmp *= 0.5;
  }
  return h;
}

float WaveRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 100; j ++) {
    p = ro + s * rd;
    h = p.y - WaveHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.05, h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - WaveHt (p.xz));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  p.xz = abs (p.xz) - 0.5 * pSize.xz;
  return (max (p.x, p.z) > 0.) ? dstFar : dHit;
}

vec3 WaveNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (WaveHt (p.xz) - vec2 (WaveHt (p.xz + e.xy), WaveHt (p.xz + e.yx)), e.x).xzy);
}

float FishDf (vec3 p)
{
  vec3 q;
  float dMin, dBodyF, dBodyB, dMouth, dFinT, dFinP, dFinD, dEye, d, szFac;
  szFac = 4.;
  p *= szFac;
  p.x = abs (p.x);
  p.z -= 2.;
  p.yz = Rot2D (p.yz, 0.2 * fishAngI);
  q = p;
  q.z -= -0.6;
  dBodyF = PrEllipsDf (q, vec3 (0.7, 0.8, 2.4));
  q = p;
  q.z -= -1.2;
  q.yz = Rot2D (q.yz, fishAngI);
  q.z -= -1.6;
  dBodyB = PrEllipsDf (q, vec3 (0.35, 0.5, 2.5));
  q.z -= -2.2;
  q.yz = Rot2D (q.yz, 2. * fishAngI);
  q.xz -= vec2 (0.5, -0.5);
  q.xz = Rot2D (q.xz, 0.4);
  dFinT = PrEllipsDf (q, vec3 (0.8, 0.07, 0.4));
  q = p;
  q.yz -= vec2 (-0.3, 1.7);
  q.yz = Rot2D (q.yz, 0.1);
  q.y = abs (q.y) - 0.04;
  dMouth = PrEllipsDf (q, vec3 (0.25, 0.12, 0.6));
  q = p;
  q.yz -= vec2 (0.7, -1.);
  q.yz = Rot2D (q.yz, 0.6);
  dFinD = PrEllipsDf (q, vec3 (0.05, 1., 0.35));
  q = p;
  q.xy = Rot2D (q.xy, 0.8);
  q.xz -= vec2 (0.7, -0.1);
  q.xz = Rot2D (q.xz, 0.6);
  dFinP = PrEllipsDf (q, vec3 (0.9, 0.04, 0.3));
  q = p;
  q -= vec3 (0.4, -0.1, 1.1);
  dEye = PrEllipsDf (q, vec3 (0.11, 0.15, 0.15));
  idObj = 2;
  dMin = SmoothMin (dBodyF, dBodyB, 0.3);
  dMin = SmoothMin (dMin, dFinT, 0.1);
  dMin = SmoothMin (dMin, dMouth, 0.15);
  dMin = SmoothMin (dMin, dFinD, 0.02);
  dMin = SmoothMin (dMin, dFinP, 0.02);
  if (dEye < dMin) idObj = 3;
  dMin = SmoothMin (dMin, dEye, 0.01);
  qHit = q;
  return dMin / szFac;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  int idObjA;
  dMin = dstFar;
  q = p;
  d = PrBoxDf (q - vec3 (0., -0.5 * pSize.y, 0.), 0.5 * pSize + vec3 (wWid, 0., wWid));
  d = max (d, - PrBoxDf (q - vec3 (0., -0.5 * pSize.y + 0.05, 0.), 0.5 * pSize));
  d = max (d, - PrBoxDf (q  - vec3 (0., fishPos.y, 0.), vec3 (0.5 * pSize.x + wWid - 0.1, 0.5, 0.5)));
  if (d < dMin) { dMin = d;  idObj = 1; }
  idObjA = idObj;
  q = p - fishPos;
  q.xz = Rot2D (q.xz, 0.5 * pi);
  d = FishDf (q);
  if (d < dMin) { dMin = d;  idObjA = idObj; }
  idObj = idObjA;
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = 0; j < 50; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += 0.04;
    if (sh < 0.05) break;
  }
  return sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col;
  if (rd.y >= 0.) {
    ro.xz += 2. * tCur;
    col = vec3 (0.1, 0.2, 0.4) + 0.2 * (1. - max (rd.y, 0.)) +
       0.1 * pow (max (dot (rd, sunDir), 0.), 16.);
    col = mix (col, vec3 (0.8), clamp (0.2 + Fbm2 (0.1 *
       (ro.xz + rd.xz * (100. - ro.y) / max (rd.y, 0.001))) * rd.y, 0., 1.));
  } else {
    ro -= (ro.y / rd.y) * rd;
    col = 0.7 * mix (vec3 (0.3, 0.4, 0.1), vec3 (0.4, 0.5, 0.2), Noisefv2 (256. * ro.xz)) *
         (1. - 0.1 * Fbm2 (4. * ro.xz));
  }
  return col;
}

vec3 ShGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colW, colC, vn, q, rg;
  vec2 cg;
  float dstWat, dstObj, aRefl, s, sh;
  bool isBg, isBase, uWat;
  aRefl = 0.;
  colW = vec3 (0.);
  colC = vec3 (0.);
  col = BgCol (ro, rd);
  isBg = true;
  isBase = false;
  uWat = false;
  if (ro.y > 0.) {
    dstObj = ObjRay (ro, rd);
    dstWat = WaveRay (ro, rd);
    if (dstWat < min (dstObj, dstFar)) {
      ro += rd * dstWat;
      vn = WaveNf (ro);
      aRefl = 0.5 * smoothstep (0.9, 1., 1. - pow (dot (- rd, vn), 5.));
      colW = BgCol (ro, reflect (rd, vn));
      rd = refract (rd, vn, 1. / 1.3);
      dstObj = ObjRay (ro, rd);
      ro += rd * dstObj;
      isBg = false;
      uWat = true;
      if (dot (vn, sunDir) > 0.) {
        q = ro - (ro.y / sunDir.y) * sunDir;
        q.xz = abs (q.xz) - 0.5 * pSize.xz;
        if (max (q.x, q.z) < 0.) colC = 0.3 * vec3 (0.9, 0.9, 1.) *
           pow (max (0., dot (WaveNf (q), sunDir)), 128.);
      }
    } else if (dstObj < dstFar) {
      ro += rd * dstObj;
      q.xz = abs (ro.xz) - 0.5 * pSize.xz;
      s = max (q.x, q.z);
      if (ro.y < 0. && s > wWid - 0.01 || ro.y >= 0. && s > 0.5) col = BgCol (ro, rd);
      else isBg = false;
    }
  } else {
    ro -= (ro.y / rd.y) * rd;
    q.xz = abs (ro.xz) - 0.5 * pSize.xz - 0.5;
    if (abs (max (q.x, q.z) + 0.2) < 0.2) {
      col = vec3 (0.6, 0.4, 0.1);
      vn = vec3 (0., -1., 0.);
      isBg = false;
      isBase = true;
    }
  }
  if (! isBg) {
    sh = 1.;
    if (! isBase) {
      vn = ObjNf (ro);
      if (idObj == 1) {
        cg = (abs (vn.y) < 0.01) ? ((abs (vn.x) > 0.99) ? ro.zy : ro.xy) : ro.xz;
        rg = ShGrid (4. * cg);
        if (abs (vn.y) < 0.01) {
          if (abs (vn.x) > 0.99) {
            rg.xz *= sign (vn.x);
            if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
            else vn.xz = Rot2D (vn.xz, rg.x);
          } else {
            rg.xz *= sign (vn.z);
            if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
            else vn.zx = Rot2D (vn.zx, rg.x);
          }
        } else {
          if (vn.y > 0.99) {
            if (rg.x == 0.) vn.yz = Rot2D (vn.yz, rg.z);
            else vn.yx = Rot2D (vn.yx, rg.x);
          }
        }
        col = vec3 (0.6, 0.6, rg.y) * (1. - 0.2 * Fbm2 (64. * cg));
      } else if (idObj == 2) {
        col = vec3 (0.6, 1., 0.9) * (1. - 0.4 * smoothstep (-0.5, -0.4, vn.y));
      } else if (idObj == 3) {
        col = vec3 (1., 0.5, 0.2) * step (0.05, length (qHit.yz - vec2 (-0.02, 0.05)));
      }
      if (uWat) col *= vec3 (0.9, 1., 1.);
      sh = 0.5 + 0.5 * ObjSShadow (ro, sunDir);
    }
    col = col * (0.5 + 0.5 * sh * max (dot (vn, sunDir), 0.)) +    
       0.2 * sh * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    col+= colC;
  }
  return pow (clamp (mix (col, colW, aRefl), 0., 1.), vec3 (0.9));
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  pSize = vec3 (9., 3., 6.);
  wWid = 2.5;
  fishPos = vec3 ((0.5 * pSize.x + 2.) * (2. * mod (0.1 * tCur, 1.) - 1.), -1.5, 0.);
  fishAngI = 0.15 * sin (pi * tCur);
  dstFar = 50.;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = pi * mPtr.y;
  } else {
    el = -0.35 * pi + 0.4 * pi * SmoothBump (0.39, 0.61, 0.1, mod (0.06 * tCur, 1.));
    az = pi * (2. * mod (floor (0.07 * tCur), 2.) - 1.) *
       SmoothBump (0.25, 0.75, 0.1, mod (0.07 * tCur, 1.));
  }
  el = clamp (el, -0.4 * pi, 0.2 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 0., -15.);
  rd = vuMat * normalize (vec3 (uv, 4.2));
  sunDir = normalize (vec3 (1., 5., -1.));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

// === Drawing transformed-space isolines using screen-space metrics ================
// Inspired by "Mobius Weave" by Shane. https://shadertoy.com/view/XtsBRS
// Cleaning and application here: https://www.shadertoy.com/view/llsfRj
// Conformal version here: https://www.shadertoy.com/view/MllBzj

#define UNIQ // if on, inverse gives one unique solution. Also, dist more robust !
#define S(v) smoothstep(2./iResolution.y, 0., v )

// --- direct transforms
vec2 Mobius(vec2 p, vec2 z1, vec2 z2)
{
	z1 = p - z1; p -= z2;
	return mat2(z1,z1.y,-z1.x) * p / dot(p, p);
}

vec2 spiralZoom(vec2 p, vec2 offs, float n, float spiral, float zoom, vec2 phase)
{
	p -= offs;
	float a = atan(p.y, p.x)/6.283 + iTime/32.;
	float d = length(p);
	return mat2(n,1, spiral,-zoom) * vec2(a, log(d)) + phase;
}

// --- inverse transforms
float k; // for tests
vec4 iMobius(vec2 p, vec2 z1, vec2 z2, float s)      // s = -1 or 1, 2 solution for each
{   float sb = 1.;
#ifdef UNIQ                                          // ... or if activating signs,
    s *= sign(p.x); sb = sign(p.y);                  // unique sol: s=1,x1,y1 otherwise 4
#endif
    float l = length(p), t = p.y/p.x,                // solve  u = v*l ; tan(u,v) = p.y/p.x
          c = s / sqrt(1.+t*t),                      // c = cos(atan( ) )
        v = length(z1-z2) / sqrt( 1.+ l*l -2.*l*c ), // c = (u+v-|z1z2| ) / 2uv
        u = v*l;                                   
    vec2  a = 2.*(z1-z2);                            // solve |P-z1| = u ; |P-z2| = v
    float b = u*u-v*v + dot(z2,z2)-dot(z1,z1);       // ||-||: a.P + b = 0
    // y = -( b + a.x* x ) / a.y                     // normalize by a.y
    b /= a.y; a /= a.y;                              // ( in Shane example a.y was = 0 ! )
    float A = 1. +  a.x*a.x,                         //  |P-z1| = u , P = (x,y)
          B =     2.* b*a.x -2.*dot(vec2(1,-a.x),z1),
          C =           b*b +2.*b*z1.y + dot(z1,z1) - u*u,
          D = max(0., B*B - 4.*A*C),
         x1 = (-B+sb*sqrt(D))/(2.*A), y1 = -( b + a.x*x1 ),
         x2 = (-B-sb*sqrt(D))/(2.*A), y2 = -( b + a.x*x2 );
    k += 1./ (.1*abs(t)); // for tests
 // if (abs(A)<1e-5) { x1 = x2 = -C/B; y1 = y2 = -( b + a.x*x1 ) / a.y; } // degenerate case
	return vec4(x1,y1, x2,y2);                       // 2 solutions per s
}
// horizontal red/blue glitches at t ~ 0 ( or D ~ 0 ) -> solved by #UNIQ
// vertical red/blue glitches at t ~ inf ( or c ~ 0 )

vec2 ispiralZoom(vec2 p, vec2 offs, float n, float spiral, float zoom, vec2 phase)
{
    p = inverse(mat2(n,1, spiral,-zoom)) * (p-phase) ;
    p.x = (p.x - iTime/32.) * 6.283;             // ( p.x, p.y )  = ( a, log(d) )
    return exp(p.y) * vec2(cos(p.x),sin(p.x)) + offs;
}

// --- demo configuration 
vec2 Z1 = vec2(-.875, -.525),  // in Shane example Z1.y=Z2.y was causing a degenerescence
     Z2 = vec2(.375, -.125);

vec2 MobiusSpiral(vec2 q)      // total direct transform
{
    q = Mobius(q, Z1, Z2);
    return spiralZoom(q, vec2(-.5), 5., 3.14159*.2, .5, vec2(-1, 1)*iTime*.125);
}
float DrawInvMobiusSpiral(vec2 q, vec2 uv0, float r) // total inverse transform + draw iso-X
{
    vec4 v; float s = 0.;
    q = ispiralZoom(q, vec2(-.5), 5., 3.14159*.2, .5, vec2(-1, 1)*iTime*.125);
    v = iMobius(q, Z1, Z2, 1.);  
#ifdef UNIQ
    return S(length(uv0-v.xy) - r );
#else
    s += S(length(uv0-v.xy) - r ); // note that only the quadrant of uv0 contributes
    s += S(length(uv0-v.zw) - r ); // (but costly to 1st determine it: transfo + inverse + tests )
    v = iMobius(q, Z1, Z2, -1.);   
    s += S(length(uv0-v.xy) - r ); 
    s += S(length(uv0-v.zw) - r ); 
	return s;
#endif
}

void mainImage(out vec4 O, vec2 U)
{
	vec2 R = iResolution.xy,
        uv0 = (U -.5*R) / R.y,
    uv = MobiusSpiral(uv0);

    O -= O;
    vec2 p = uv*4., q;
  //O += .5*DrawInvMobiusSpiral(uv, uv0, .01 ); return; // check inverse ok ( = grey + white overlaps if !UNIQ )


    if ( mod(iTime,2.) > 1. ) {
        p = abs(fract(p+.5)-.5);                       // --- isolines in Moebius space
        O += .5*float(min(p.x,p.y)<.1);
        return;
    }

    // --- to get iso-X in screenspace, 
    //     take it in final space ( floor(...) ),  inverse it, and draw distance to the result.
    const int N = 2, dN = N/2-1;
    for (int i=0; i<N; i++) {
        //q = floor(p)/4.;
        q = vec2( floor(p.x+float(i-dN)), p.y ) / 4.;  // --- H isolines in screen space
        O.r += DrawInvMobiusSpiral(q, uv0, .005 );

        q = vec2( p.x, floor(p.y+float(i-dN)) ) / 4.;  // --- V isolines in screen space
        O.b += DrawInvMobiusSpiral(q, uv0, .005 );

        for (int j=0; j<N; j++) {
            q = floor(p+vec2(i-dN,j-dN) ) / 4.;        // --- dots in screen space
            O.g += DrawInvMobiusSpiral(q, uv0, .02 );
        }
    }
    //O += k/1e4; // for tests
}

