//David Hoskins hash: https://www.shadertoy.com/view/4djSRW 
float hash11(float p)
{
	vec3 p3  = fract(vec3(p) * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z)*2.0 - 1.0;
}

float gnoise(float p) 
{
    float i = floor(p);
	float f = fract(p);

    float a = dot(hash11(i), f);
    float b = dot(hash11(i + 1.0), f - 1.0);

    float u = smoothstep(0.0, 1.0, f);
    return mix(a, b, u);
}

float segment(vec2 uv, float e, vec2 a, vec2 b)  {         
    b -= a;
    uv -= a;
    return smoothstep(0.0003, 0.0003 + 1.5*e, 
                      length( uv - b * clamp(dot(b, uv) / dot(b, b), 0.0, 1.0)));
}

float point(vec2 uv, float e, vec2 p) 
{
	return smoothstep(0.002, 0.002 + 1.5*e, length(uv - p)); 
}

vec2 transform(vec3 p, vec4 q) 
{
    p += 2.0 * cross(q.xyz, cross(q.xyz, p) + q.w * p);
    return p.xy / (p.z + 10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float e = 1.0 / iResolution.x;
    vec2 uv = (fragCoord - iResolution.xy*0.5) * e;

    float t = iTime*0.3;

    vec3 a = vec3(gnoise(t + 2.0) * 3.1, 
                  gnoise(t + 6.0) * 3.5, 
                  gnoise(t + 4.0) * 3.2);
    float s = clamp((gnoise(t + 10.0) + 0.5), -1.0, 1.0);
    vec4 q = normalize(vec4(a * s, sqrt(1.0 - s*s))); 

    vec2 b1 = transform(vec3(-1.0, -1.0, -1.0), q);
    vec2 b2 = transform(vec3(-1.0, -1.0,  1.0), q);
    vec2 b3 = transform(vec3( 1.0, -1.0,  1.0), q);
    vec2 b4 = transform(vec3( 1.0, -1.0, -1.0), q);

    vec2 t1 = transform(vec3(-1.0, 1.0, -1.0), q);
    vec2 t2 = transform(vec3(-1.0, 1.0,  1.0), q);
    vec2 t3 = transform(vec3( 1.0, 1.0,  1.0), q);
    vec2 t4 = transform(vec3( 1.0, 1.0, -1.0), q);

    vec2 o = transform(vec3(0.0), q);

    float v = 1.0;
    v *= segment(uv, e, b1, b2);
    v *= segment(uv, e, b2, b3);
    v *= segment(uv, e, b3, b4);
    v *= segment(uv, e, b4, b1);

    v *= segment(uv, e, t1, t2);
    v *= segment(uv, e, t2, t3);
    v *= segment(uv, e, t3, t4);
    v *= segment(uv, e, t4, t1);

    v *= segment(uv, e, b1, t1);
    v *= segment(uv, e, b2, t2);
    v *= segment(uv, e, b3, t3);
    v *= segment(uv, e, b4, t4);

    v *= point(uv, e, b1);
    v *= point(uv, e, b2);
    v *= point(uv, e, b3);
    v *= point(uv, e, b4);

    v *= point(uv, e, t1);
    v *= point(uv, e, t2);
    v *= point(uv, e, t3);
    v *= point(uv, e, t4);
    v *= point(uv, e, o);

    fragColor = vec4(vec3(sqrt(v)), 1.0); 
}

