// Glowing festive pentagram: ray trace 5 rotations of basic line, use
// distance of ray to line to determine color.
////////////////////////////////////////////////////////////////////////////////

const int N = 5; // Number of lines
const float scale = 4.0;
const float A = 0.6; // Light amplitude
const float K = 0.4; // Concentration
const float R = 1.0; // Radius
const float PI = 3.14159;
const float theta = 2.0*PI/float(N);
const mat3 m = mat3(cos(theta),sin(theta),0,
                    -sin(theta),cos(theta),0,
                    0,0,1);

float det(vec2 c0, vec2 c1) {
  return determinant(mat2(c0,c1));
}

vec2 closest0(vec3 p,vec3 q,vec3 r,vec3 s) {
  // Use Cramer's rule to solve linear system
  // Matrices are column major!
  // Assume q and s are unit vectors
  // No cross products, 4 dot products, 3 2x2 determinants
  vec2 c0 = vec2(1.0,dot(q,s));
  vec2 c1 = vec2(-dot(q,s),-1.0);
  vec2 a = vec2(dot(r-p,q),dot(r-p,s));
  return vec2(det(a,c1),det(c0,a))/det(c0,c1);
}

// return vec2(k,j) such that p + kq and
// r + js are closest points on lines
// Assumes q and s are normalized
vec2 closest1(vec3 p,vec3 q,vec3 r,vec3 s) {
  // Matrices are column major!
  // Assume q and s are unit vectors
  mat2 m = mat2(1.0,dot(q,s),-dot(q,s),-1.0);
  return inverse(m)*vec2(dot(r-p,q),dot(r-p,s));
}

// Nice "geometric" solution from Wikipedia.
// Probably not as fast as Cramer's rule,
// 3 cross products, 4 dot products & a normalize
vec2 closest2(vec3 p,vec3 q,vec3 r,vec3 s) {
   vec3 n = normalize(cross(q,s));
   vec3 n1 = cross(q,n); 
   vec3 n2 = cross(s,n);
   return vec2(dot(r-p,n2)/dot(q,n2),
               dot(p-r,n1)/dot(s,n1));
}

vec2 mmul(mat2 m, vec2 p) {
  return vec2(m[0][0]*p[0]+m[1][0]*p[1],
              m[0][1]*p[0]+m[1][1]*p[1]);
}

vec2 closest3(vec3 p,vec3 q,vec3 r,vec3 s) {
  // Matrices are column major!
  // Assume q and s are unit vectors
  float k = dot(q,s);
  mat2 m = mat2(-1.0,-k,k,1.0);
  //return m*vec2(dot(r-p,q),dot(r-p,s))/(k*k-1.0);
  return mmul(m,vec2(dot(r-p,q),dot(r-p,s)))/(k*k-1.0);
}

vec2 closest(vec3 p,vec3 q,vec3 r,vec3 s) {
  return closest3(p,q,r,s);
}

// Distance is weird because we see lines from behind
// the viewing plane.
float dist(vec3 p,vec3 q,vec3 r,vec3 s) {
   vec3 n = normalize(cross(q,s));
   return abs(dot(n,p-r));
}

// Distance is weird because we see lines from behind
// the viewing plane.
float dist1(vec3 p,vec3 q,vec3 r,vec3 s) {
  vec2 k = closest(p,q,r,s);
  return distance(p+k.x*q,r+k.y*s);
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb_smooth( in vec3 c ) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

// Quaternion to rotation matrix
// Assumes normalized
mat3 qrot(vec4 q) {
  float x = q.x, y = q.y, z = q.z, w = q.w;
  float x2 = x*x, y2 = y*y, z2 = z*z;
  float xy = x*y, xz = x*z, xw = x*w;
  float yz = y*z, yw = y*w, zw = z*w;
  return 2.0*mat3(0.5-y2-z2, xy+zw, xz-yw,
                  xy-zw, 0.5-x2-z2, yz+xw,
                  xz+yw, yz-xw, 0.5-x2-y2);
}

vec2 rotate(vec2 p, float t) {
  return p * cos(t) + vec2(p.y, -p.x) * sin(t);
}

vec3 transform(in vec3 p) {
  if (iMouse.x > 0.0) {
    float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
    float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  return p;
}

void mainImage(out vec4 outColor, vec2 fragCoord) {
  vec2 xy = scale*(2.0*fragCoord - iResolution.xy)/iResolution.y;
  // p+kq is viewing ray
  vec3 p = vec3(0,0,-6);
  vec3 q = vec3(xy,0)-p;
  p = transform(p);
  q = transform(q);
  q = normalize(q);

  // r+js is polygon line, to be rotated in loop
  vec3 r = vec3(0,1,0);
  vec3 s = vec3(1,0,0);
  // Rotation axis
  vec3 axis = normalize(vec3(1,1,cos(0.1*iTime)));
  float phi = iTime*0.15;
  mat3 n = qrot(vec4(sin(phi)*axis,cos(phi)));
  p = n*p; q = n*q;
  float mindist = 1e10;
  vec3 color = vec3(0); // Accumulate color here
  for (int i = 0; i < N; i++) {
    vec2 k = closest(p,q,r,s);
    vec3 p1 = p+k.x*q;
    vec3 r1 = r+k.y*s;
    float d = distance(p1,r1);
    float h = mod(0.3*(-iTime+log(1.0+abs(k.y))),1.0);
    vec3 basecolor = hsv2rgb_smooth(vec3(h,1.0,1.0));
    color += A*float(k.x > 0.0)*(1.0-pow(smoothstep(0.0,R,d),K))*basecolor;
    s = m*s; r = m*r; // Rotate to next line
  }
  outColor = vec4(sqrt(color),1.0);
}

// crank this up until your GPU chokes
const int nSpheres = 10; 


const float sphereRadius = 3.0 / log2(float(nSpheres));

const float tau = 6.28318;
const float inf = 999.0;

struct Hit { 
    float t; 
    vec3 p; 
    vec3 n;
    int id;
};

Hit noHit;

Hit sphereHit(vec3 center, float radius, vec3 rayOrigin, vec3 rayDirection, int id) {
    float a = dot(rayDirection, rayDirection);
    vec3 L = rayOrigin - center;
    float b = 2.0 * dot(rayDirection, L);
    float c = dot(L, L) - radius*radius;
    float discrim = b*b - 4.0*a*c;
    if (discrim < 0.0) return noHit;
    float t = (-b - sqrt(discrim)) / (2.0 * a);
    if (t < 0.0) return noHit;
    vec3 p = rayOrigin + t*rayDirection;
    return Hit(t, p, normalize(p-center), id);
}

Hit hitSomething(vec3 rayOrigin, vec3 rayDirection, float time) {
    const float fns = float(nSpheres);

    Hit minHit = noHit;

    for (int i = 0; i < nSpheres; i++) {
        float fi = float(i);
        float fni = fi / fns;
        float d = 3.0 * sin(time * fni + fi);
        vec3 center = vec3(d*cos(tau*fni+time*10.0*fni)+sin(time*10.0), 
                       d*sin(tau*fni+time*10.0*fni), 
                       d*sin(0.5*tau*fni+time*fni*1.4142)+cos(time*10.0));

        Hit hit = sphereHit(center, sphereRadius + sphereRadius*2.0*pow(float(i%10)/10.0, 5.0), rayOrigin, rayDirection, 0);
        if (hit.t <= 0.0 || hit.t > minHit.t) continue;
        minHit = hit;
    }

    Hit hit = sphereHit(vec3(0.0, -41.5, 2.0), 40.0, rayOrigin, rayDirection, 1);
    if (hit.t > 0.0 && hit.t < minHit.t) minHit = hit;

    return minHit;
}

vec3 colorSomething(Hit hit, float time, vec3 rayOrigin, vec3 rayDirection, mat3x3 cam) {
    if (hit.t > inf - 0.5) return vec3(0.0, 0.75+0.25*sin(time), 1.0);

    vec3 color;

    // diffuse
    if (hit.id == 0) {
        float dup    = 1.0 * dot(hit.n, cam[1]) + 0.0;
        float dright = 1.0 * dot(hit.n, cam[0]) + 0.0;
        float dfwd   = 1.0 * dot(hit.n, -cam[2] ) + 0.0;
        color = vec3(dright, dup, dfwd);
    } else if (hit.id == 1) {
        float check = 0.4 + 0.6 * float(int(mod(hit.p.x, 2.0)) ^ int(mod(hit.p.z, 2.0)));
        color = vec3(check, check, 0.1);
    }

    // specular
    vec3 refl = reflect(rayDirection, hit.n);
    float spec = pow(dot(refl, cam[1]), 20.0);
    if (spec > 0.0) color += vec3(spec, spec, spec);

    return color;
}

const int maxDepth = 4;
vec3 trace( mat3x3 cam, vec3 rayOrigin, vec3 rayDirection, float time )
{
    vec3 color = vec3(0.0, 0.0, 0.0);
    float ref = 1.0;
    for (int i = 0; i < maxDepth; i++) {
        Hit minHit = hitSomething(rayOrigin, rayDirection, time);
        color += ref * colorSomething(minHit, time, rayOrigin, rayDirection, cam);
        if (minHit.id < 0) break;
        ref *= 0.6;
        rayOrigin = minHit.p;
        rayDirection = reflect(rayDirection, minHit.n);
    }
    color = color * 0.8;
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec3 zero = vec3(0.0, 0.0, 0.0);
    noHit = Hit(inf, zero, zero, -1);

    // make time optionally srubbable
    float time = (iMouse.z > 0.0) ? iMouse.x / iResolution.x :  iTime / 20.0;

    float camDist = 8.0;
    vec3 rayOrigin = vec3(1.1*camDist*sin(tau * time * 0.5), 0.0, 0.9*camDist*cos(tau * time * 0.5));
    vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - vec2(1.0, 1.0);
    uv.x *= iResolution.x / iResolution.y;
    vec3 up = vec3(0.0, 1.0, 0.0);
    vec3 lookat = vec3(0.0, 0.0, 0.0);
    vec3 fwd   = normalize(lookat - rayOrigin);
    vec3 right = normalize(cross(fwd, up));
    vec3 camup = normalize(cross(right, fwd));
    vec3 rayDirection = normalize(fwd + 0.5 * (uv.x*right + uv.y*up));

    fragColor = vec4(trace(mat3x3(right, camup, fwd), rayOrigin, rayDirection, time), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = abs(fragCoord - 0.5 * iResolution.xy) / iResolution.x;

    float speed = 24.0;
    float frequency = 128.0;

    float dist = uv.x + uv.y;

    float color = 0.5 + 0.5*sin(frequency*dist + speed*iTime);

	fragColor = vec4(vec3(color) ,1.0);
}

