// Color and image manipulation

float GetRecursionFade(int r, float timePercent)
{
    if (r > recursionCount)
        return timePercent;

    // fade in and out recusion
    float rt = max(float(r) - timePercent - recursionFadeDepth, 0.0);
    float rc = float(recursionCount) - recursionFadeDepth;
    return rt / rc;
}

float GetRecursionFade2(int r, float timePercent)
{
    if (r > recursionCount)
        return timePercent;

    // fade in and out recusion
    float rt = max(float(r) - timePercent, 0.0);
    float rc = float(recursionCount);
    return rt / rc;
}

vec3 InitPixelColor() 
{ 
    vec3 color = vec3(0,0.4,0.1);
    color.x -= (0.033*iTime);
    return color; 
}

vec3 CombinePixelColor(vec3 color, float timePercent, int i, int r, vec2 pos, ivec2 glyphPos, ivec2 glyphPosLast)
{
    float f = GetRecursionFade(r, timePercent);
    //if (f == 0.0)
    //    return color;

    vec3 myColor = vec3
    (
    	mix( 0.5, 0.7, RandFloat(i + r + 419*glyphPosLast.x + 773*glyphPosLast.y)),
    	mix(-0.7, 0.7, RandFloat(i + r + 929*glyphPosLast.x + 499*glyphPosLast.y)),
        1.0
    );
    //
    //float f2 = smoothstep(3.0, 4.0, float(r) + timePercent);
    //if (r == 5|| r == 6)
    //    myColor.x = mix(-0.5, 0.5, RandFloat(i + r + 419*glyphPosLast.x + 773*glyphPosLast.y)),

    // combine with my color
    myColor.x = pow(myColor.x, 3.0);
    myColor.y = pow(myColor.y, 3.0);
    float f2 = GetRecursionFade2(r, timePercent);
    color.x += f2*myColor.x;
    color.y = max(min( color.y + myColor.y*f2, 1.0), 0.0);
    color.z = max(color.z, myColor.z*f);
    return color;
}

vec3 FinishPixel(vec3 color, vec2 uv)
{
    // rotate over time
    float theta = 0.01177*iTime;
	float c = cos(theta);
	float s = sin(theta);
    uv *= mat2(-s, c, c, s);

    // color wander
    color.x += (0.1*uv.y + 0.1*uv.x + 0.01*iTime);

    // convert to rgb
    color = HsvToRgb(color);
    return color;
}

vec2 InitUV(vec2 uv)
{
	// wave
	uv.x += 0.005*sin(10.0*uv.y + 0.51*iTime);
	uv.y += 0.005*sin(10.0*uv.x + 0.53*iTime);
	uv.x += 0.05*sin(2.0*uv.y + 0.57*iTime);
	uv.y += 0.05*sin(2.0*uv.x + 0.55*iTime);

    // rotate over time
    float theta = 0.01*iTime;
	float c = cos(theta);
	float s = sin(theta);
	uv = vec2((uv.x*c - uv.y*s), (uv.x*s + uv.y*c));

    return uv;
}

float TimeWarp(vec2 uv)
{
    // time warp to add add some 3d curve
    return timeWarp*(uv.x*uv.x + uv.y*uv.y);
}

