void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// use square aspect ratio
	vec2 uv = fragCoord;
	uv = fragCoord / iResolution.y;
	uv -= vec2(0.5*iResolution.x / iResolution.y, 0.5);
    uv = InitUV(uv);

	// get time 
    float timePercent = (3.0 + iTime + 180.+TimeWarp(uv))*zoomSpeed;
	int iterations = int(floor(timePercent));
	timePercent -= float(iterations);;

	// update zoom, apply pow to make rate constant
	float zoom = pow(e, -glyphSizeLog*timePercent);
	zoom *= zoomScale;

    // cache focus positions
    for(int i = 0; i  < powTableCount + 2; ++i)
      focusList[i] = CalculateFocusPos(iterations+i-2);

	// get offset
	vec2 offset = vec2(0);
	for (int i = 0; i < powTableCount; ++i)
		offset += ((vec2(GetFocusPos(i)) + vec2(glyphMargin)) * gsfi) * powTable[i];

	// apply zoom & offset
    vec2 uvFractal = uv * zoom + offset;

	// check pixel recursion depth
	vec3 pixelFractalColor = GetPixelFractal(uvFractal, iterations, timePercent);
    pixelFractalColor = FinishPixel(pixelFractalColor, uv);

	// apply final color
	fragColor = vec4(pixelFractalColor, 1.0);
}

vec2 Location = vec2(.45, .35);
vec2 Dot1 = vec2(.6, .28);
vec2 Dot2 = vec2(.75, .28);
vec2 Dot3 = vec2(.9, .28);
float Radius1;
float Radius2 = .015;
float Radius3 = .02;

vec3 BgColor = vec3(1.0,1.0,1.0);
vec3 Black = vec3(0.0,0.0,0.0);
vec3 Yellow = vec3(1.0,1.0,0.0);
vec3 Dot = vec3(0.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragCoord = (fragCoord + fragCoord - iResolution.xy) / iResolution.y;
    Radius1 = length(fragCoord);

    uv.y *= iResolution.y / iResolution.x;

    vec4 Layer1 = vec4(BgColor, 1.0);
    vec4 Layer2 = vec4(Dot, step(length(Dot1-uv), Radius3));
    vec4 Layer3 = vec4(Dot, step(length(Dot2-uv), Radius3));
    vec4 Layer4 = vec4(Dot, step(length(Dot3-uv), Radius3));
    vec4 Layer5 = vec4(Yellow, 1.0) * step(Radius1, .5)
        * step(fragCoord.x / Radius1, .9 - .1 * sin(iTime * 5.0));
    vec4 Layer6 = vec4(Black, step(length(Location-uv), Radius2));

    vec4 Composite = mix(Layer1, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);
    Composite = mix(Composite, Layer5, Layer5.a);
    Composite = mix(Composite, Layer6, Layer6.a);
    fragColor = Composite;   
}

precision highp float;


// Convention:
// vec2s ending with 'c'
// are in circular coords
// ex: pc = vec2(angle of p, length of p)



#define time (iTime / 2.0)

#define PI2 6.28
#define PI 3.1416


// Point to polar coord
vec2 p_to_pc(vec2 p){
    return vec2(atan(p.y, p.x), length(p));
}

// Polar coord to point
vec2 pc_to_p(vec2 pc){
    return vec2(pc.y * cos(pc.x), pc.y * sin(pc.x));
}

// I use these fields to create and tweak the snowflakes
vec2 fieldA(vec2 pc){
    // Modify angle and distance
    pc.y += 0.02 * floor(cos(pc.x * 6.0 ) * 5.0);
    pc.y += 0.01 * floor(10.0 * cos(pc.x * 30.0));
    pc.y += 0.5 * cos(pc.y * 10.0);

    // Take back to position
    return pc;
}


// Different values of f happen to give quite different
// snowflake shapes. Interesting...
vec2 fieldB(vec2 pc, float f){
    // Modify angle and distance
    pc.y += 0.1 * cos(pc.y * 100.0 + 10.0);
    pc.y += 0.1 * cos(pc.y * 20.0 + f);
    pc.y += 0.04 * cos(pc.y * 10.0 + 10.0);

    return pc;
} 

vec4 snow_flake(vec2 p, float f){

    vec4 col = vec4(0.0);

	vec2 pc = p_to_pc(p * 10.0);


    pc = fieldA(fieldB(pc, f));

    p = pc_to_p(pc);

    float d = length(p);

    if(d < 0.3){
        col.rgba += vec4(1.0);
    }

    return col;
}

vec4 snow(vec2 p){

    vec4 col = vec4(0.0);

	p.y += 2.0 * time;

    p = fract(p + 0.5) - 0.5;

    p *= 1.0;


    p.x += 0.01 * cos(p.y * 3.0 + p.x * 3.0 + time * PI2);


    // Just me pseudo randomly adding snowflakes
    // Could be optimized by not calling the function 8 times
    // (using some field distortion )
    col += snow_flake(p, 1.0);
    col += snow_flake(p + vec2(0.2, -0.1), 4.0);
    col += snow_flake(p * 2.0 + vec2(-0.4, -0.5), 5.0);
    col += snow_flake(p * 1.0 + vec2(-0.2, 0.4), 9.0);
    col += 2.0 * snow_flake(p * 1.0 + vec2(0.4, -0.4), 5.0);
    col += snow_flake(p * 1.0 + vec2(-1.2, 1.2), 9.0);
    col += snow_flake(p * 1.0 + vec2(2.4, -1.2), 5.0);
    col += snow_flake(p * 1.0 + vec2(-1.2, 1.1), 9.0);


    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){

	vec2 uv = fragCoord.xy / iResolution.x;
    float x = uv.x;
    float y = uv.y;

    vec2 p = vec2(x, y) - vec2(0.5);

    vec4 col = vec4(0.2, 0.4, 0.7, 1.0);

    col += 0.3 * snow(p * 2.0);
    col += 0.2 * snow(p * 4.0 + vec2(time, 0.0));
    col += 0.1 * snow(p * 8.0);

    col.a = 1.0;

    fragColor = col;
}

// http://www.randomghost.com/post/45955195639

#define red vec3(.933,.255,.106)
#define glow 2.0

float circle(in vec2 uv, in vec2 p, in float r, in float width){
    float dist = length(uv - p);

	return 
        (1. - smoothstep(r - glow, r + glow, dist)) -
        (1. - smoothstep((r - width) - glow, (r - width) + glow, dist));
}

float sector(in vec2 uv, in vec2 p, in float r, in float width, in float start, in float end){
    float dist = length(uv - p);
    float angle = degrees(atan(uv.y - p.y, uv.x - p.x)) + 180.;

	return 
        ((1. - smoothstep(r - glow, r + glow, dist)) -
        (1. - smoothstep((r - width) - glow, (r - width) + glow, dist))) * 
        smoothstep(start - glow, start + glow, angle) * 
        (1. - smoothstep(end - glow, end + glow, angle));
}

float rect(in vec2 uv, in vec4 p){
    float t = p.y; // top
    float r = p.x + p.z; // right
    float b = p.y + p.w; // bottom
    float l = p.x; // left

    return
        smoothstep(l - glow, l, uv.x) *
        (1. - smoothstep(r, r + glow, uv.x)) *
        smoothstep(t - glow, t, uv.y) *
        (1. - smoothstep(b, b + glow, uv.y));
}

// http://thebookofshaders.com/edit.php#06/easing.frag
float expoOut(float t) {
  return t == 1.0 ? t : 1.0 - pow(2.0, -10.0 * t);
}

float expoIn(float t) {
  return t == 0.0 ? t : pow(2.0, 10.0 * (t - 1.0));
}

float expoInOut(float t) {
  return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(2.0, 10.0 - (t * 20.0)) + 1.0;
} 

void rotate(inout vec2 uv, in vec2 center, inout float rotation, in float angle){
    float a = radians(angle - rotation);

    rotation = angle;

    uv -= center;
    uv = mat2(cos(a), -sin(a), sin(a), cos(a)) * uv;
    uv += center;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    vec2 center = iResolution.xy / 2.;
    center.y -= 10.;
    vec3 color = vec3(0.);
    float rotation = 0.;
    float t = fract(iTime * .3);

    color += circle(uv, center, 65., 3.) * red;

    float inRotation = 0.;
    inRotation -= 120. * expoInOut(smoothstep(.6, 1., t)) * step(.6, t) * (1. - step(1., t));

    rotate(uv, center, rotation, 50. + inRotation);

    float s1 = sector(uv, center, 64., 6., 0., 80.);
    color = mix(color, red, s1);

    float s2 = sector(uv, center, 64., 6., 120., 200.);
    color = mix(color, red, s2);

    float s3 = sector(uv, center, 64., 6., 240., 320.);
    color = mix(color, red, s3);

    float outRotation = 0.; 
    outRotation -= (120. * 2.) * expoInOut(smoothstep(.7, .9, t)) * step(.7, t) * (1. - step(.9, t));

    rotate(uv, center, rotation, sin(iTime * 3.) * 60. + outRotation);

    float outRadius = 80.;
    outRadius -= 35. * expoInOut(smoothstep(.3, .5, t)) * step(.3, t) * (1. - step(.5, t));
    outRadius -= 35. * step(.5, t);
    outRadius += 35. * expoInOut(smoothstep(.7, 1., t)) * step(.7, t) * (1. - step(1., t));

    float s7 = sector(uv, center, outRadius, 5., 0., 60.);
    color = mix(color, red * .4, s7);

    float s8 = sector(uv, center, outRadius, 5., 120., 180.);
    color = mix(color, red * .4, s8);

    float s9 = sector(uv, center, outRadius, 5., 240., 300.);
    color = mix(color, red * .4, s9);

    rotate(uv, center, rotation, 45. + outRotation);

    float s4 = sector(uv, center, outRadius, 5., 0., 90.);
    color = mix(color, red, s4);

    float s5 = sector(uv, center, outRadius, 5., 120., 210.);
    color = mix(color, red, s5);

    float s6 = sector(uv, center, outRadius, 5., 240., 330.);
    color = mix(color, red, s6);rotate(uv, center, rotation, 0.);

    float rectY = center.y + 75.;
    rectY += 180. * (1. - expoOut(min(t, .3) / .3));
    rectY -= 65. * expoInOut(smoothstep(.4, .6, t)) * step(.4, t) * (1. - step(.6, t));
    rectY -= 65. * step(.6, t); 
    rectY += (180. + 65.) * expoIn(smoothstep(.7, .9, t) * step(.7, t * (1. - step(.9, t))));
    rectY += (180. + 65.) * step(.9, t);

    float rectColorAlpha = 1.;
    rectColorAlpha -= (1. - step(.03, t));
    rectColorAlpha -= step(.9, t);
    rectColorAlpha -= (1. - step(center.y + 15., rectY)) * ceil(sin(iTime * 100.));
    rectColorAlpha = clamp(rectColorAlpha, 0., 1.);

    vec3 rectColor = mix(vec3(0.), red, rectColorAlpha);

    float r1 = rect(uv, vec4(center.x, rectY, 1., 25.));
    color = mix(color, rectColor, r1);

    rotate(uv, center, rotation, 120.);

    float r2 = rect(uv, vec4(center.x, rectY, 1., 25.));
    color = mix(color, rectColor, r2);

    rotate(uv, center, rotation, -120.);

    float r3 = rect(uv, vec4(center.x, rectY, 1., 25.));
    color = mix(color, rectColor, r3);

	fragColor = vec4(color,1.);
}

