// THE PIECES

// Rotate the space around (0, 0) by angle.
vec2 rotate(vec2 uv, float angle)
{
	return vec2(
		uv.x*cos(angle) - uv.y*sin(angle),
		uv.x*sin(angle) + uv.y*cos(angle)
	);
}

// Return 1 if even, 0 if odd.
float is_even(float n)
{
    return 1.0 - step(1.0, mod(n, 2.0));
}

// Tile space, repeats 0->1 for number of times (per square unit space).
vec4 tile_space(vec2 uv, float times)
{
    vec2 scaledUV = uv * times;
    return vec4(fract(scaledUV), floor(scaledUV));
}

// Values from Wikipedia page on YUV colorspace.
vec3 yuv_to_rgb(vec3 yuvCol)
{
    return vec3(
        yuvCol.x +                    1.13983*yuvCol.z,
		yuvCol.x - 0.39465*yuvCol.y - 0.58060*yuvCol.z,
		yuvCol.x + 2.03211*yuvCol.y
    );
}

// From pixel spirit deck.
float rectSDF(vec2 uv, vec2 size)
{
	uv = uv * 2. - 1.;
	return max(
		abs(uv.x/size.x),
		abs(uv.y/size.y)
	);
}

// Adapted from pixel spirit deck.
float crossSDF(vec2 uv, float s, float thick)
{
	return min(
		rectSDF(uv, vec2(s, thick)),
		rectSDF(uv, vec2(thick, s))
	);
}

// From pixel spirit deck.
float fill(float x, float size)
{
	return 1.0 - step(size, x);
}


// THE CONSTRUCTION

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize UVs.
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Background color parameter.
    vec4 bgCol = mix(vec4(.05, .05, .05, 1), vec4(.25, .25, .25, 1.), distance(uv, vec2(.5))*1.4);

    // Correct UV aspect ratio.
    uv.x *= iResolution.x / iResolution.y;

    // More parameters.
    vec4 strokeCol = vec4(yuv_to_rgb(vec3(.5, uv.x, uv.y-.1)), 1.);
    const float tileAmt = 4.0;
    float crossSize = iMouse.x / iResolution.x * 6.;
    float crossThickness = iMouse.y / iResolution.y * 6.;

    // Build on these.
    float sdf = 0.;
    vec4 col;

    // Tile the space.
    vec4 tiledSpace = tile_space(uv, tileAmt);
    uv = tiledSpace.xy;

    // Make checkerboard mask to flip rotations for the effect.
    float xor = is_even(tiledSpace.w) + is_even(tiledSpace.z);
    //xor = (1.0 - step(1.5, xor)) * xor;
    xor = 1. - is_even(xor);

    // Rotate the space (works per tile).
    uv -= vec2(.5);
    uv = rotate(uv, mix(1., -1., xor)*iTime*0.5);
    uv += vec2(.5);

    uv.x = fract(length(uv * 5.));

    // Shape the SDF with a cross.
    sdf = crossSDF(uv, crossSize, crossThickness);

    // Mix the colors for the final result.
    col = mix(bgCol, strokeCol, fill(sdf, 0.5));


    fragColor = vec4(uv.x, .0, .0, 1.);
    //fragColor = bgCol;
}

 float cyl_length = 2.0;
 float cyl_rad = 0.8;
 float curve_rad = 0.2;
const vec3 cyl_center = vec3(0.1, 0.0, -1.0);

float cyl_sdf(in highp vec3 point) {
    vec3 cyl_dir =
        vec3(sin(0.1 * iTime) * cos(0.33 * iTime),
             sin(0.33*iTime),
             cos(0.1 * iTime) * cos(0.33 * iTime));
	vec3 rel = point - cyl_center;
    float along = dot(rel, cyl_dir);
    vec3 across = rel - cyl_dir * along;
    return length(max(vec2(length(across) - cyl_rad + curve_rad,
                  	       abs(along) - 0.5 * cyl_length + curve_rad),
                     0.0)) - curve_rad;
}

vec3 cyl_norm(in highp vec3 point) {
	float v = cyl_sdf(point);
    return normalize(vec3(v + cyl_sdf(point + vec3(0.01, 0.00, 0.00)),
                          v + cyl_sdf(point + vec3(0.00, 0.01, 0.00)),
                          v + cyl_sdf(point + vec3(0.00, 0.00, 0.01))));
}

float cyl_isect(in highp vec3 ray_orig, in highp vec3 ray_dir) {
	float result = 0.0;
    vec3 p = ray_orig;
    float last_d = 20.0;
    for (int i = 0; i < 64; ++i) {
    	float d = cyl_sdf(p);
        result += d;
        p += d * ray_dir;
        last_d = d;
    }
    if (last_d > 1.0e-3) {
        return -1.0; // miss
    }
    return result;
}

float dist_out_of_cyl(in highp vec3 ray_orig, in highp vec3 ray_dir) {

    float result = 0.1;
    vec3 p = ray_orig + 0.1 * ray_dir;
    for (int i = 0; i < 64; ++i) {
        float d = -0.8 * cyl_sdf(p);
        result += d; // d may be negative
        p += d * ray_dir;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float texture_y = 0.0;
    float val1 = 
        texture(iChannel0, vec2(0.2, texture_y)).r + 
        texture(iChannel0, vec2(0.4, texture_y)).r;
    float val2 =
        texture(iChannel0, vec2(0.6, texture_y)).r +
        texture(iChannel0, vec2(0.8, texture_y)).r;
    cyl_length = 0.5 * (1.0 + 3.0 * val1);
    cyl_rad = sqrt(4.0 / cyl_length);
 curve_rad = 0.5 * (1.0 + val2) * min(0.9 * cyl_rad, 0.4 * cyl_length);
    // cyl_rad -= curve_rad;
    //cyl_length -= 2.0 * curve_rad;
    const vec3 scatter_color = vec3(1.0, 0.4, 0.1);
    const vec3 rough_color = vec3(1.0, 0.8, 0.1);
    vec3 light_loc =
        //5.0 * vec3(sin(iTime), 1.0, cos(iTime) + 0.5);
        vec3(5.0, 2.0, -1.0);
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 ray_orig = vec3(0.0, 0.0, 2.0);
    vec3 ray_dir = normalize(vec3(uv, -1.0));

    float d = cyl_isect(ray_orig, ray_dir);
    float eta = 0.16;
    if (d > 0.0) {
    	vec3 p = ray_orig + d * ray_dir;

        vec3 light_dir = normalize(light_loc - ray_orig);

        vec3 n = cyl_norm(p);
        vec3 b = reflect(ray_dir, n);
        float diffuse = smoothstep(0.9, 1.0, dot(b, light_dir));

        vec3 refr_ray = normalize(refract(ray_dir, n, eta));
        float refr_d = dist_out_of_cyl(p, refr_ray);
        vec3 refr_p = p + refr_d * refr_ray;
        vec3 refr_n = -cyl_norm(refr_p);
        refr_ray = normalize(refract(refr_ray, refr_n, 1.0 / eta));
        vec3 refr_light_dir = normalize(light_loc - refr_p);
        float refracted = smoothstep(0.9, 1.0, dot(refr_ray,
                                                   refr_light_dir));

        float d = dist_out_of_cyl(p, light_dir);
        float subsurface = exp(-1.0 * d);
		fragColor = vec4(subsurface * scatter_color +
                         diffuse * rough_color +
                         refracted * vec3(0.2),1.0);

    } else {
        vec3 light_dir = normalize(light_loc - ray_orig);
		fragColor = vec4(0.5 * vec3(smoothstep(0.9, 1.0, dot(ray_dir,
                                                       light_dir))),1.0);
    }
        // fragColor = texture(iChannel0, ray_dir);
}

vec4 Slime(vec2 uv)
{
    const vec4 col1 = vec4(0.0,.1,.1,1.0);
    const vec4 col2 = vec4(0.5,0.9,0.3,1.0);

    vec4 k = vec4(0.0);

    float s = sin(iTime*0.1);
    float s2 = 0.5+sin(iTime*1.8);
    vec2 d = uv*(3.0+s*.3);						// mod 4.0 for irregularity...

    d.x += iTime*0.3+sin(d.x+d.y + iTime*0.3)*0.5;
    d.y += iTime*0.15+sin(d.x + iTime*0.3)*0.5;	//-(s*0.5);
    float v1=length(0.5-fract(d.xy))+0.55;				// 0.9 =more gooey bits, 1.2 = less gooey bits

    d = (uv);			// zoom
    float v2=length(0.5-fract(d.xx))-0.175;		// border
    v1 *= 1.0-v2*v1;
    v1 = v1*v1*v1;
    v1 *= 2.9+s2*0.2;
    k = mix(col2,col1,v1)*(3.2+(s2*0.2));
    k *= 1.0-(v2);

    if (k.g<=0.4)
    {
        float m = 1.0-clamp(k.g,0.0,0.5);
		float f = mod( floor(6.0*uv.x) + floor(6.0*uv.y), 2.0);
        float col = 0.3 + 0.2*f*1.0;
        k.x = k.y = k.z = col*m;
		k.rb *= 1.2;
    }
    else
    {
		float f = mod( floor(6.0*uv.x) + floor(6.0*uv.y), 2.0);
        vec4 _col = 0.2*f*vec4(0.175);
        k+=_col;
    }
    return k;
}

// mirror mod
float pModMirror1(inout float p, float size)
{
	float halfsize = size*0.5;
	float c = floor((p + halfsize)/size);
	p = mod(p + halfsize,size) - halfsize;
	p *= mod(c, 2.0)*2. - 1.;
	return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy)/ iResolution.y;

    float speed = iTime*0.15;
