float Blur = 8.0;
float DotSize = 0.3;
float OutlineThickness = 0.03;

float YinYang( float co, vec2 p, float scale, float blur, float outline, float dotSize )
{
   	float b = blur*scale/min(iResolution.y,iResolution.x);
    float d = dotSize;
    float o = outline;
    float c;

    float r = length(p);
    float r1 = length(2.0*p + vec2(0, 1));
    float r2 = length(2.0*p - vec2(0, 1));

    // curves
    if (p.x < 0.0)
		c = mix(1.0, 0.0, smoothstep(1.0-b, 1.0+b, r1));
    else
		c = mix(0.0, 1.0, smoothstep(1.0-b, 1.0+b, r2));

    // dots
    c = mix(0.0, c, smoothstep(d-b, d+b, r1));
    c = mix(1.0, c, smoothstep(d-b, d+b, r2));

    // outline
    c = mix(c, 0.0, smoothstep(1.0-b, 1.0, r));
    c = mix(c, co, smoothstep(1.0+o, 1.0+o+b, r));

    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // zoom
    float z =  mix(1.1, 5.0, pow(smoothstep(-1.0, 1.0, sin(20.0 + 0.5*iTime)), 2.0));
	vec2 p = z * (2.0*fragCoord-iResolution.xy)/min(iResolution.y, iResolution.x);

    // rotate
    float theta = -0.2*iTime;
	float ct = cos(theta);
	float st = sin(theta);
    p *= mat2(-st, ct, ct, st);

    // use mouse to pick center line
    float centerLine = (iMouse.x > 0.0)? iMouse.x : iResolution.x/2.0;

    // yin yang (no blur on right side)
   	float c = 0.5;
    if (fragCoord.x < centerLine)
	    c = YinYang(c, p, z, Blur, OutlineThickness, DotSize);
    else
	    c = YinYang(c, p, z, 0.0, OutlineThickness + .005*pow(z, 0.9), DotSize);

    // center line
    if (fragCoord.x > centerLine - 1.0 && fragCoord.x < centerLine + 1.0)
        c = mix(c, 0.0, 0.5);

    fragColor = vec4(c);
}

float map(vec3 p) {
	float t = length(mod(p, 2.0) - 1.0) - 0.1;
	t = min(t, length(mod(p.xz, 2.0) - 1.0) - 0.03);
	t = min(t, length(mod(p.yz, 2.0) - 1.0) - 0.03);
	return min(t, length(mod(p.xy, 2.0) - 1.0) - 0.03);
}


vec2 rot(vec2 p, float a) {
	return vec2(
		p.x * cos(a) - p.y * sin(a),
		p.x * sin(a) + p.y * cos(a));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = ( gl_FragCoord.xy / iResolution.xy ) * 2.0 - 1.0;

	vec3 dir = normalize(vec3(uv, 1.0));
	dir.xz= rot(dir.xz, iTime * 0.1);
    dir.yx= rot(dir.yx, iTime * 0.1);

	vec3 pos = vec3(0, 0, iTime);
	float t = 0.0;
	for(int i = 0;  i < 100; i++) {
		float k = map(dir * t + pos);
		if(k < 0.1) break;
		t += 1.0;
	}
	vec3 ip = dir * t + pos;

	fragColor = vec4(t * 0.01) + map(ip + 0.1) + dir.xyzz * 0.7;
}

#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / min(iResolution.x,iResolution.y);

	uv /= .7+0.35*sin(t);                           // zoom
	uv /= .7 + abs(.7-length(uv));                  // ball
	uv += 0.3*tan(t/5.)*cos(t/19.9*vec2(.77,2.22)); // pattern slide
    uv *= 0.54+.5*sin(t/3.2);                       // pattern zoom

	vec3 c = cos(uv.x*20.)*cos(uv.y*20.) + sin(t*vec3(1.,1.1,1.11));
    c = c*c/3.;                                     // colour fold and spiff

    fragColor=vec4( c, .0 );
}

// hash function by iq

float hash(vec2 p)  // replace this by something better
{
    p  = 50.0*fract( p*0.3183099 + vec2(0.71,0.113));
    return -1.0+2.0*fract( p.x*p.y*(p.x+p.y) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}


vec3 map( vec3 p )
{

	vec3 dl = vec3( length( p ) - 1.5 );
    vec3 dlOne = vec3( p.y + 2.0 );
    if( dl.y < dlOne.y ) dlOne = dl;
    return dlOne;

}

vec3 rayIntersect( vec3 ro, vec3 rd )
{

    vec3 t = vec3( 0.0, 0.0, 0.0);
    int maxiter = 64;

    for(int i = 0; i < maxiter; ++i )
    {

    	vec3 hit = ro + rd * t;
        vec3 d = map(hit);
        t += d * vec3( 0.5 );

    }

    return t;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 

    vec2 mouser = iMouse.xy / iResolution.y; 

    vec3 ro = vec3( 0.0, 0.0, 2.5 );
    vec3 rd = normalize( vec3( uv, -1.0 ) );

    float f = 0.0;

    float wav = texture(iChannel0, vec2( 0.0, 0.25 )).x;
    wav *= 0.3;
    float fft = texture(iChannel0, vec2( 0.0, 0.75 )).x;
    fft *= 0.3;

    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    uv += iTime * 0.1; 
	f  = 0.5000*noise( uv ); uv = m*uv;
	f += 0.2500*noise( uv ); uv = m*uv;
	f += 0.1250*noise( uv ); uv = m*uv;
	f += wav * 0.1 + 0.0625*noise( uv ); uv = m*uv;
    fft += f * 0.4738*noise( uv ); uv = m*uv;

    vec3 hitter = vec3( rayIntersect( ro, rd ) );
    hitter = 1.0 / (2.0 * hitter * hitter * 0.5 );
    //hitter = smoothstep( hitter, 0.1, 1.2 );
    vec3 hit = vec3( hitter * f ) * vec3( sin( fft * iMouse.x), f, cos( fft * iMouse.y * fft )  );
    hit *= vec3(5.0);


	fragColor = vec4( hit, 1.0 );
}

#define ITER 28

mat2 rot(float a){ float s=sin(a), c=cos(a); return mat2(c,s,-s,c); }
float box(vec3 p, float r){ return length(max(abs(p)-r,0.)); }

float map(vec3 p)
{   
    float s = sin( iTime + .6*p.z )/4.;
    p.xy *= rot( s );
    p.xy += .5 + vec2( -s, s );

    vec3 q = abs( p-round(p) );
    float d = min(min(length(q.xy), length(q.xz)), length(q.yz)) - .01;
    d = min(d, box(q, .05));
    q = vec3(.0, ( p.xy - vec2(2.5, 0.5) ) * rot( p.z+iTime ) );
    d = min(d, box(q, .25));

    return d;
}

void mainImage( out vec4 O, vec2 U )
{
	vec3  R = iResolution.xyy,
         rd = normalize(vec3(U-.5*R.xy, R.y)),
	     ro = vec2(.0, iTime).xxy;

    //uint xor = ( (uint(U.x) ^ uint(U.y)) - uint(iFrame) ) % uint(R.x);
    //if(xor != 0u)discard;

    rd.xz *= rot(.6);

    float t=.0, m;
    for( int i=0; ++i < ITER; ) {
    	t += map(ro+rd*t)/2.;
    }

    O = vec4( cos(t*rd.z + iTime - .5),
              exp(-t/6.),
              1.-exp(-t/4.), 
              1 );
}

float df(vec3 p)
{
	float a = p.z * cos(p.z * 2.0) * 0.001;
	p.xy *= mat2(cos(a),sin(a),-sin(a), cos(a));
	p.y += sin(p.x * .5 + p.z * 0.5);
	float sp = 10. - abs(p.y * 0.5);
	float cy = max(1.0-0.2*sin(p.z*0.5)-abs(p.y*0.5),length(mod(p.xz, 1.)-0.5) - 0.2);
    return min(sp,cy);
}

vec3 nor( vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy) - df(pos-eps.xyy),
	    df(pos+eps.yxy) - df(pos-eps.yxy),
	    df(pos+eps.yyx) - df(pos-eps.yyx) );
	return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 g = fragCoord;
	vec2 si = iResolution.xy;
	vec2 uv = (g+g-si)/si.y/0.8;
	vec3 ro = vec3(0,0, (iTime + 30.) * 2.); 
    vec3 cv = ro + vec3(0,0,1); 
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y = cross(z,x);
    float fov = .9;
  	vec3 rd = normalize(fov * (uv.x * x + uv.y * y) + z);

	float s = 1., d = 0.;
	for (int i=0; i<150; i++) 
	{
		if (log(d*d/s/1e6)>0.) break;
		s=df(ro+rd*d);
		d += s * .15;
	}

	vec3 p = ro + rd * d;
	vec3 lid = normalize(ro-p);
	vec3 n = nor(p, 0.1);
	vec3 refl = reflect(rd,n);
	float diff = clamp( dot( n, lid ), 0.0, 1.0 );
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. );
	float spe = pow(clamp( dot( refl, lid ), 0.0, 1.0 ),16.);
	vec3 col = vec3(.8,.5,.2);

    float sss = df(p - n*0.001)/0.05;

	fragColor.rgb = mix(
	(diff * vec3(0.8,0.2,0.5) + fre + sss * vec3(0.8,0.5,0.5)) * 0.25,
	vec3(spe * 0.5), 
	exp(-0.01 * d*d));

	vec2 q = g/si;
    fragColor.rgb *= 0.5 + 0.5*pow( 16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y), 0.55 );
}

