void calcRayForPixel( in vec2 pix, out vec3 resRo, out vec3 resRd )
{
	vec2 p = (2.0*pix-iResolution.xy) / iResolution.y;

     // camera movement	
	vec3 ro, ta;
	calcCamera( ro, ta );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

	resRo = ro;
	resRd = rd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord) / iResolution.y;

    float th = (iMouse.z>0.001) ? (2.0*iMouse.x-iResolution.x)/iResolution.y : 0.0;

	vec3 ro, rd, ddx_ro, ddx_rd, ddy_ro, ddy_rd;
	calcRayForPixel( fragCoord + vec2(0.0,0.0), ro, rd );
	calcRayForPixel( fragCoord + vec2(1.0,0.0), ddx_ro, ddx_rd );
	calcRayForPixel( fragCoord + vec2(0.0,1.0), ddy_ro, ddy_rd );

    // trace
	vec3 pos, nor;
	float occ;
    int mid;
    float t = intersect( ro, rd, pos, nor, occ, mid );

	vec3 col = vec3(0.9);
	if( mid!=-1 )
	{
#if 1
		// -----------------------------------------------------------------------
        // compute ray differentials by intersecting the tangent plane to the  
        // surface.		
		// -----------------------------------------------------------------------

		// computer ray differentials
		vec3 ddx_pos = ddx_ro - ddx_rd*dot(ddx_ro-pos,nor)/dot(ddx_rd,nor);
		vec3 ddy_pos = ddy_ro - ddy_rd*dot(ddy_ro-pos,nor)/dot(ddy_rd,nor);

		// calc texture sampling footprint		
		vec2     uv = texCoords(     pos, mid );
		vec2 ddx_uv = texCoords( ddx_pos, mid ) - uv;
		vec2 ddy_uv = texCoords( ddy_pos, mid ) - uv;
#else
		// -----------------------------------------------------------------------
        // Because we are in the GPU, we do have access to differentials directly
        // This wouldn't be the case in a regular raytracer.
		// It wouldn't work as well in shaders doing interleaved calculations in
		// pixels (such as some of the 3D/stereo shaders here in Shadertoy)
		// -----------------------------------------------------------------------
		vec2 uvw = texCoords( pos, mid );

		// calc texture sampling footprint		
		vec2 ddx_uvw = dFdx( uvw ); 
        vec2 ddy_uvw = dFdy( uvw ); 
#endif

		// shading		
		vec3 mate = vec3(0.0);

        if( p.x<th ) mate = vec3(1.0)*gridTexture( uv );
        else         mate = vec3(1.0)*gridTextureGradBox( uv, ddx_uv, ddy_uv );

        // lighting	
		vec3 lin = doLighting( pos, nor, occ, rd );

        // combine lighting with material		
		col = mate * lin;

        // fog		
        col = mix( col, vec3(0.9), 1.0-exp( -0.00001*t*t ) );
	}

    // gamma correction	
	col = pow( col, vec3(0.4545) );

	col *= smoothstep( 1.0, 2.0, abs(p.x-th)/(2.0/iResolution.y) );

	fragColor = vec4( col, 1.0 );
}

vec4 skyColor = vec4(1.0, 0.3, 0.0, 1.0);
vec4 sunColor = vec4(1.0, 1.0, 0.0, 1.0);
vec4 sunraysColor = vec4(1.0, 0.8, 0.0, 1.0);
vec4 groundColor = vec4(0.0, 0.3, 0.0, 1.0);

vec4 BlendColors(vec4 _c1, vec4 _c2) {

    //Blend equation. c1 is over c2
    //c1*a1 + c2*a2(1 - a1)
    //---------------------
    //   a1 + a2(1 - a1)

    return (_c1*_c1.a + _c2*_c2.a*(1.0 - _c1.a)) / (_c1.a + _c2.a*(1.0 - _c1.a));
}

vec4 DrawTheSun(vec2 _coords) {

    //Sun's radius is between 90% and 110% of the half vertical resolution
    float sunRadius = (sin(iTime)/10.0 + 1.0) * iResolution.y / 2.0;

    //Current pixel's distance from screen's center
    float currentLength = length(_coords);

    //Depending on the distance from the center, color is white at center and yellow on the edge
    sunColor.b = 1.0 - smoothstep(0.0, sunRadius, currentLength);

    //Alpha is 1.0 at 60% of the radius inwards and drops to 0.0 on the edge
    sunColor.a = 1.0 - smoothstep(sunRadius * 0.6, sunRadius, currentLength);

    return BlendColors(sunColor, skyColor);
}

vec4 DrawTheSunrays(vec2 _coords) {

    //Angle with X axis. Range is [-, ]
    float angle = atan(_coords.y, _coords.x);

    //Scale the angle to get more rays
    angle *= 16.0;

    //cos() range is [-1.0, 1.0]. Scale and translate to make it [0.0, 0.5]
    sunraysColor.a = cos(angle) / 4.0 + 0.25;

    return sunraysColor;
}

vec4 DrawTheSky(vec2 _coords) {

    skyColor = BlendColors(DrawTheSunrays(_coords), skyColor);

    return BlendColors(DrawTheSun(_coords), skyColor);
}

vec4 DrawTheRoad(vec2 _coords) {

    float roadWidth = iResolution.x / 4.0;
    float xLength = abs(_coords.x);
    vec4 roadColor = vec4(0.0, 0.0, 0.0, 1.0);

    roadWidth *= abs(_coords.y) / iResolution.y * 1.5 + 0.25;

    if(xLength > roadWidth) {
        return vec4(0.0, 0.0, 0.0, 0.0);
    }

    if(xLength < roadWidth*0.85 && xLength > roadWidth*0.8){
        return vec4(1.0, 1.0, 1.0, 1.0);
    }

    if(xLength < roadWidth*0.05 && cos((_coords.y + iTime*200.0 ) / 20.0) > 0.0){
        return vec4(1.0, 1.0, 1.0, 1.0);
    }

    return vec4(0.0, 0.0, 0.0, 1.0);
}

vec4 DrawTheGround(vec2 _coords) {

    vec4 sunBlend = vec4(1.0, 0.4, 0.0, 0.5);
    sunBlend.a *= 1.0 - smoothstep(0.0, iResolution.y/2.0, abs(_coords.y));
    groundColor = BlendColors(DrawTheRoad(_coords), groundColor);

    return BlendColors(sunBlend, groundColor);
}

vec4 GetColor(vec2 _coords) {

    if(_coords.y >= 0.0){
        return DrawTheSky(_coords);
    }else{
        return DrawTheGround(_coords);
    }
    //return DrawSky(_coords);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 newCoords = fragCoord.xy - iResolution.xy / 2.0;
	fragColor = GetColor(newCoords);
}

