    vec3	col = vec3(0.0);
    vec2	dist = vec2(0.0);
    z.xy = of;
    float ii = -1.;
    z.xy = modA(z.xy, 5.);
    z.x -= .753;
    for (int i = -1; i < I_MAX; ++i)
    {
        ++ii;
        z.xy = cadd((cmult( abs(z.xy)-1.0*sin(iTime*.25)*.13 , abs(z.xy)-1.0*sin(iTime*.25)*.13 )), vec2(-.3, -2.0));
        vec2 fz = fract(z.xy*1.1)-.5;
        z.xy += 1./max(fz*fz, .9750);
        z.xy = cadd(z.xy, .051*vec2(sin(-3.55), cos(-3.55)) );
        z.z = 2.0 * (z.x*z.z - z.y*z.w);
        z.w = 2.0 * (z.y*z.z - z.x*z.w);
        dist.x = dot(z.xy,z.xy);
		dist.y = dot(z.zw,z.zw);
        if (dist.x > 100000000000.0 || dist.y > 100000000000.0)
            break;
    }
    // iq
    // http://iquilezles.org/www/articles/mset_smooth/mset_smooth.htm
    vec3	tmp = vec3(0.);
        if( ii < float(I_MAX)-.5 )
        {
            float sit = ii - log2(log2(dot(z.xy,z.xy))/(log2(2.)))/log2(2.); 
            tmp = 
                +
                vec3(
                sin(+sit*.25+0.00)*.25 + .5
                ,
                sin(+sit*.25+1.04)*.25 + .3
                ,
                sin(+sit*.25+2.08)*.25 + .2
                )
                ;
        }
    col = tmp;
    fragColor = vec4(col, 1.0);
}

