  return mat3(
     c,0,-s,
     0,1,0,
     s,0,c
  );
}

mat3 rotX(float a){
  float s =sin(a);
  float c=cos(a);

  return mat3(
     1,0,0,
     0,c,-s,
     0,s,c
  );
}


float obj1(vec3 pr,vec3 c){
    vec3 p=pr-c;
    p.xz=mod(p.xz,4.);
    p.xz-=2.;

    return length(p)-0.4;
}

float obj2(vec3 pr,vec3 c){
    vec3 p=pr-c;

    float arc = atan(p.y,p.x);
    vec3 polor =vec3(cos(arc),sin(arc),5);
    return length(p-polor)-0.5;
}   

float obj3(vec3 pr,vec3 c){
    vec3 p=pr-c;
    p=abs(p);
    return (p.y+p.x+p.z-2.)/sqrt(3.0);
}   


float plane(vec3 p,vec3 c){
    p-=c;

    vec3 polor =vec3(p.x,-3.,p.z);

    return length(p-polor);
}

