float map(vec3 p){
   float ref=1.;

   float temp =obj1(p,vec3(0,-2.1,-9));

   if(ref>temp){
       ref=temp;
   }

   temp =obj2(p,vec3(1,sin(iTime),-8));

   if(ref>temp){
       ref=temp;
   }

   temp =obj3(p,vec3(-2,sin(iTime),1-1));

   if(ref>temp){
       ref=temp;
   }


   temp =plane(p,vec3(0,0,0));

   if(ref>temp){
        ref=temp;    
   }

   return ref;
